// File: contracts/MintYourself.sol


pragma solidity ^0.8.14;






contract MintYourself is ERC721A, ERC721AQueryable, AccessControl, Pausable {
    using Counters for Counters.Counter;

    event FeeUpdated(uint256 feeAmt);
    event PermissionConfigured(uint256 permId, uint256 cost);
    event UserPermissionAdded(address user, uint256 permId);

    struct UserStruct {
        uint256[] permissions;
        Counters.Counter permCount;
    }

    bytes32 public constant PAUSER_ROLE = keccak256("PAUSER_ROLE");
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    Counters.Counter private _tokenIdCounter;
    string public updatableBaseURI = "https://mintyourself.xyz/api/metadata/";
    uint256 public feeAmt = 0;
    mapping(address => UserStruct) users;
    Counters.Counter private _permCounter;
    uint256[] public permissionCost;

    constructor() ERC721A("Mint Yourself", "MYS") {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(PAUSER_ROLE, msg.sender);
        _grantRole(MINTER_ROLE, msg.sender);
        _tokenIdCounter.increment(); // start @ 1
    }

    function pause() public onlyRole(PAUSER_ROLE) {
        _pause();
    }

    function unpause() public onlyRole(PAUSER_ROLE) {
        _unpause();
    }

    function _baseURI() internal view override returns (string memory) {
        return updatableBaseURI;
    }

    function setBaseURI(string memory newBaseURI) public onlyRole(DEFAULT_ADMIN_ROLE) {
        updatableBaseURI = newBaseURI;
    }

    function supportsInterface(bytes4 interfaceId) public view virtual override(ERC721A, AccessControl, IERC721A) returns (bool) {
        // The interface IDs are constants representing the first 4 bytes of the XOR of
        // all function selectors in the interface. See: https://eips.ethereum.org/EIPS/eip-165
        // e.g. `bytes4(i.functionA.selector ^ i.functionB.selector ^ ...)`
        return
            interfaceId == 0x01ffc9a7 || // ERC165 interface ID for ERC165.
            interfaceId == 0x80ac58cd || // ERC165 interface ID for ERC721.
            interfaceId == 0x5b5e139f; // ERC165 interface ID for ERC721Metadata.
    }

    function mintYourself(address to) public payable whenNotPaused {
        if (!hasRole(MINTER_ROLE, _msgSender())) {
            require(msg.value >= feeAmt, "Insufficiant funds");
        }
        _mint(to, 1);
    }

    function setFee(uint256 newFee) public onlyRole(DEFAULT_ADMIN_ROLE) {
        feeAmt = newFee;
        emit FeeUpdated(newFee);
    }

    function configurePermissionCost(uint256 permId, uint256 cost) public onlyRole(DEFAULT_ADMIN_ROLE) {
        if(permId > _permCounter.current()){
            permissionCost[permId] = cost;
        }else{
            permissionCost.push(cost);
            _permCounter.increment();
        }
        emit PermissionConfigured(permId, cost);
    }

    function hasPerm(address user, uint256 permId) public view virtual returns (bool) {
        require(permId <= _permCounter.current(), "Setting not configured");
        UserStruct storage userStruct = users[user];
        if(userStruct.permCount.current() < 1){
            return false;
        }
        for(uint i = 0; i < userStruct.permCount.current() - 1; i++){
            if(userStruct.permissions[i] == permId){
                return true;
            }
        }
        return false;
    }

    function getUserPerms(address user) public view returns (uint256[] memory) {
        return users[user].permissions;
    }

    function grantSetting(address userAddress, uint256 permId) public payable {
        require(permissionCost[permId] > 0, "Setting not configured");
        require(msg.value >= permissionCost[permId], "Transaction value too low");
        require(!hasPerm(userAddress, permId), "User already has setting");
        users[userAddress].permissions.push(permId);
        emit UserPermissionAdded(userAddress, permId);
    }

    function withdraw() public payable onlyRole(DEFAULT_ADMIN_ROLE) {
        // withdraw total balance on contract
        (bool os, ) = payable(_msgSender()).call{value: address(this).balance}("");
        require(os);
    }
}
