    "contracts/marketplace/libs/MarketplaceLib.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

/// @author: VERTICAL.art

import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/utils/introspection/ERC165Checker.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "@openzeppelin/contracts-upgradeable/utils/AddressUpgradeable.sol";

import "@manifoldxyz/libraries-solidity/contracts/access/IAdminControl.sol";

import "../ILazyDelivery.sol";

import "./TokenLib.sol";

/**
 * Interface for Ownable contracts
 */
interface IOwnable {
    function owner() external view returns (address);
}

/**
 * @dev Marketplace libraries
 */
library MarketplaceLib {
    using AddressUpgradeable for address;
    using EnumerableSet for EnumerableSet.AddressSet;

    // Events
    event CreateListing(
        uint40 indexed listingId,
        uint16 marketplaceBPS,
        uint8 listingType,
        uint24 totalAvailable,
        uint24 editionSize,
        uint48 startTime,
        uint48 endTime,
        uint256 initialAmount,
        uint16 extensionInterval,
        uint16 minIncrementBPS,
        address erc20
    );
    event CreateListingTokenDetails(
        uint40 indexed listingId,
        uint256 id,
        address address_,
        uint8 spec,
        bool lazy
    );
    event CreateListingFees(
        uint40 indexed listingId,
        uint16 deliverBPS,
        uint240 deliverFixed
    );

    event PurchaseEvent(
        uint40 indexed listingId,
        address buyer,
        uint24 count,
        uint256 amount
    );
    event BidEvent(uint40 indexed listingId, address bidder, uint256 amount);
    event OfferEvent(uint40 indexed listingId, address oferrer, uint256 amount);
    event RescindOfferEvent(
        uint40 indexed listingId,
        address oferrer,
        uint256 amount
    );
    event AcceptOfferEvent(
        uint40 indexed listingId,
        address oferrer,
        uint256 amount
    );
    event ModifyListing(
        uint40 indexed listingId,
        uint256 initialAmount,
        uint48 startTime,
        uint48 endTime,
        uint16 extensionInterval
    );
    event CancelListing(
        uint40 indexed listingId,
        address requestor,
        uint16 holdbackBPS
    );
    event FinalizeListing(uint40 indexed listingId);

    // Listing types
    enum ListingType {
        INVALID,
        FIXED_PRICE,
        OFFERS_ONLY,
        INDIVIDUAL_AUCTION,
        RANKED_AUCTION,
        LINEAR_DUTCH_AUCTION,
        EXPO_DUTCH_AUCTION,
        SETTLEMENT_DUTCH_AUCTION
    }

    /**
     * @dev Listing structure
     *
     * @param seller          - the selling party
     * @param flags           - bit flag (hasBid, finalized, tokenCreator).  See FLAG_MASK_*
     * @param totalSold       - total number of items sold.
     * @param marketplaceBPS  - Marketplace fee BPS
     * @param curationBPS     - curation fee BPS
     * @param bidCount        - bid count
     * @param details         - ListingDetails.  Contains listing configuration
     * @param token           - TokenDetails.  Contains the details of token being sold
     * @param receivers       - Array of ListingReceiver structs.  If provided, will distribute sales proceeds to receivers accordingly.
     * @param fees            - DeliveryFees.  Contains the delivery fee configuration for the listing
     * @param markleRoot      - MerkleRoot for whitelisted listing
     */
    struct Listing {
        address payable seller;
        uint8 flags;
        uint24 totalSold;
        uint16 marketplaceBPS;
        uint16 curationBPS;
        uint16 bidCount;
        ListingDetails details;
        TokenDetails token;
        ListingReceiver[] receivers;
        DeliveryFees fees;
        bytes32 merkleRoot;
    }

    uint8 internal constant FLAG_MASK_HAS_BID = 0x1;
    uint8 internal constant FLAG_MASK_FINALIZED = 0x2;
    uint8 internal constant FLAG_MASK_TOKEN_CREATOR = 0x4;
    uint8 internal constant FLAG_MASK_ACCEPT_OFFERS = 0x8;
    uint8 internal constant FLAG_MASK_FINALIZED_LOGICALLY = 0x10;

    /**
     * @dev Listing details structure
     *
     * @param initialAmount     - The initial amount of the listing. For auctions, it represents the reserve price.
     * @param restingAmount     - The resting amount of the listing.
     * @param dutchDecAmount    - The reduction amount of the dutch auction.
     * @param type_             - Listing type
     * @param totalAvailable    - Total number of tokens available.
     * @param editionSize       - Total number of winners.
     * @param extensionInterval - Only valid for *_AUCTION types. Indicates how long an auction will extend if a bid is made within the last <extensionInterval> seconds of the auction.
     * @param minIncrementBPS   - Only valid for *_AUCTION types. Indicates the minimum bid increase required
     * @param erc20             - If not 0x0, it indicates the erc20 token accepted for this sale
     * @param startTime         - The start time of the sale.  If set to 0, startTime will be set to the first bid/purchase.
     * @param endTime           - The end time of the sale.  If startTime is 0, represents the duration of the listing upon first bid/purchase.
     */
    struct ListingDetails {
        uint256 initialAmount;
        uint256 restingAmount;
        uint256 dutchDecAmount;
        ListingType type_;
        uint24 totalAvailable;
        uint16 editionSize;
        uint16 extensionInterval;
        uint16 minIncrementBPS;
        uint16 dutchInterval;
        address erc20;
        uint48 startTime;
        uint48 endTime;
    }

    /**
     * @dev Token detail structure
     *
     * @param address_  - The contract address of the token
     * @param id        - The token id (or for a lazy asset, the asset id)
     * @param spec      - The spec of the token.  If it's a lazy token, it must be blank.
     * @param lazy      - True if token is to be lazy minted, false otherwise.  If lazy, the contract address must support ILazyDelivery
     */
    struct TokenDetails {
        uint256 id;
        address address_;
        TokenLib.Spec spec;
        bool lazy;
    }

    /**
     * @dev Fee configuration for listing
     *
     * @param deliverBPS         - Additional fee needed to deliver the token (BPS)
     * @param deliverFixed       - Additional fee needed to deliver the token (fixed)
     */
    struct DeliveryFees {
        uint16 deliverBPS;
        uint240 deliverFixed;
    }

    /**
     * Listing receiver.  The array of listing receivers must add up to 10000 BPS if provided.
     */
    struct ListingReceiver {
        address payable receiver;
        uint16 receiverBPS;
    }

    /**
     * Represents an active bid
     *
     * @param amount       - The bid amount
     * @param bidder       - The bidder
     * @param delivered    - Whether or not the token has been delivered.
     * @param settled      - Whether or not the seller has been paid
     * @param refunded     - Whether or not the bid has been refunded
     * @param timestamp    - Timestamp of bid
     */
    struct Bid {
        uint256 amount;
        address payable bidder;
        bool delivered;
        bool settled;
        bool refunded;
        uint48 timestamp;
    }

    /**
     * Represents an active offer
     *
     * @param amount        - The offer amount
     * @param timestamp     - Timestamp of offer
     * @param accepted      - Whether or not the offer was accepted (seller was paid)
     * @param erc20         - Currently unused.
     *                        Offers can only be made on the listing currency
     */
    struct Offer {
        uint200 amount;
        uint48 timestamp;
        bool accepted;
        address erc20;
    }

    /**
     * Construct a marketplace listing
     */
    function constructListing(
        address seller,
        uint40 listingId,
        Listing storage listing,
        ListingDetails calldata listingDetails,
        TokenDetails calldata tokenDetails,
        DeliveryFees calldata deliveryFees,
        ListingReceiver[] calldata listingReceivers,
        bool acceptOffers,
        bool intake
    ) public {
        require(
            tokenDetails.address_.isContract(),
            "Token address must be a contract"
        );
        require(
            listingDetails.endTime > listingDetails.startTime,
            "End time must be after start time"
        );
        require(
            listingDetails.startTime == 0 ||
                listingDetails.startTime > block.timestamp,
            "Start and end time cannot occur in the past"
        );
        require(
            listingDetails.totalAvailable % listingDetails.editionSize == 0,
            "Invalid token config"
        );
        require(
            !acceptOffers ||
                listingDetails.type_ == ListingType.INDIVIDUAL_AUCTION ||
                listingDetails.type_ == ListingType.RANKED_AUCTION ||
                listingDetails.type_ == ListingType.LINEAR_DUTCH_AUCTION ||
                listingDetails.type_ == ListingType.EXPO_DUTCH_AUCTION ||
                listingDetails.type_ == ListingType.SETTLEMENT_DUTCH_AUCTION,
            "Type cannot accept offers"
        );

        if (listingReceivers.length > 0) {
            uint256 totalBPS;
            for (uint i; i < listingReceivers.length; ) {
                listing.receivers.push(listingReceivers[i]);
                totalBPS += listingReceivers[i].receiverBPS;
                unchecked {
                    ++i;
                }
            }
            require(totalBPS == 10000, "Invalid receiver config");
        }

        if (listingDetails.type_ == ListingType.INDIVIDUAL_AUCTION) {
            require(listingDetails.editionSize == 1, "Invalid token config");
        } else if (listingDetails.type_ == ListingType.OFFERS_ONLY) {
            require(
                listingDetails.initialAmount == 0 &&
                    listingDetails.startTime > block.timestamp,
                "Invalid listing config"
            );
        }

        // Purchase types
        if (isPurchase(listingDetails.type_) || isOffer(listingDetails.type_)) {
            require(
                deliveryFees.deliverBPS == 0 &&
                    deliveryFees.deliverFixed == 0 &&
                    listingDetails.extensionInterval == 0 &&
                    listingDetails.minIncrementBPS == 0,
                "Invalid listing config"
            );
        }

        if (tokenDetails.lazy) {
            require(
                ERC165Checker.supportsInterface(
                    tokenDetails.address_,
                    type(ILazyDelivery).interfaceId
                ),
                "Lazy delivery requires token address to implement ILazyDelivery"
            );
        } else {
            require(
                listingDetails.type_ == ListingType.INDIVIDUAL_AUCTION ||
                    listingDetails.type_ == ListingType.RANKED_AUCTION ||
                    listingDetails.type_ == ListingType.LINEAR_DUTCH_AUCTION ||
                    listingDetails.type_ == ListingType.EXPO_DUTCH_AUCTION ||
                    listingDetails.type_ ==
                    ListingType.SETTLEMENT_DUTCH_AUCTION ||
                    listingDetails.type_ == ListingType.OFFERS_ONLY ||
                    listingDetails.type_ == ListingType.FIXED_PRICE,
                "Invalid type"
            );
            if (intake) {
                _intakeToken(
                    tokenDetails.spec,
                    tokenDetails.address_,
                    tokenDetails.id,
                    listingDetails.totalAvailable,
                    seller
                );
            }
        }

        // Set Listing Data
        listing.seller = payable(seller);
        listing.details = listingDetails;
        listing.token = tokenDetails;
        listing.fees = deliveryFees;

        // Token ownership check
        if (
            ERC165Checker.supportsInterface(
                tokenDetails.address_,
                type(IAdminControl).interfaceId
            ) && IAdminControl(tokenDetails.address_).isAdmin(seller)
        ) {
            listing.flags |= FLAG_MASK_TOKEN_CREATOR;
        } else {
            try IOwnable(tokenDetails.address_).owner() returns (
                address owner
            ) {
                if (owner == seller) listing.flags |= FLAG_MASK_TOKEN_CREATOR;
            } catch {}
        }

        if (acceptOffers) {
            listing.flags |= FLAG_MASK_ACCEPT_OFFERS;
        }

        _emitCreateListing(listingId, listing);
    }

    function _emitCreateListing(
        uint40 listingId,
        Listing storage listing
    ) private {
        emit CreateListing(
            listingId,
            listing.marketplaceBPS,
            uint8(listing.details.type_),
            listing.details.totalAvailable,
            listing.details.editionSize,
            listing.details.startTime,
            listing.details.endTime,
            listing.details.initialAmount,
            listing.details.extensionInterval,
            listing.details.minIncrementBPS,
            listing.details.erc20
        );
        emit CreateListingTokenDetails(
            listingId,
            listing.token.id,
            listing.token.address_,
            uint8(listing.token.spec),
            listing.token.lazy
        );
        if (listing.fees.deliverBPS > 0 || listing.fees.deliverFixed > 0) {
            emit CreateListingFees(
                listingId,
                listing.fees.deliverBPS,
                listing.fees.deliverFixed
            );
        }
    }

    function _intakeToken(
        TokenLib.Spec tokenSpec,
        address tokenAddress,
        uint256 tokenId,
        uint256 tokensToTransfer,
        address from
    ) private {
        if (tokenSpec == TokenLib.Spec.ERC721) {
            require(
                tokensToTransfer == 1,
                "ERC721 invalid number of tokens to transfer"
            );
            TokenLib._erc721Transfer(
                tokenAddress,
                tokenId,
                from,
                address(this)
            );
        } else if (tokenSpec == TokenLib.Spec.ERC1155) {
            TokenLib._erc1155Transfer(
                tokenAddress,
                tokenId,
                tokensToTransfer,
                from,
                address(this)
            );
        } else {
            revert("Unsupported token spec");
        }
    }

    function isAuction(ListingType type_) internal pure returns (bool) {
        return (type_ == ListingType.INDIVIDUAL_AUCTION ||
            type_ == ListingType.RANKED_AUCTION ||
            type_ == ListingType.SETTLEMENT_DUTCH_AUCTION);
    }

    function isPurchase(ListingType type_) internal pure returns (bool) {
        return (type_ == ListingType.FIXED_PRICE ||
            type_ == ListingType.LINEAR_DUTCH_AUCTION ||
            type_ == ListingType.EXPO_DUTCH_AUCTION);
    }

    function isOffer(ListingType type_) internal pure returns (bool) {
        return (type_ == ListingType.OFFERS_ONLY);
    }

    function canOffer(
        ListingType type_,
        uint8 listingFlags
    ) internal pure returns (bool) {
        // Can only make an offer if:
        // 1. Listing is an OFFERS_ONLY type
        // 2. Listing is an INDIVIDUAL_AUCTION that has offers enabled and no bids
        return (isOffer(type_) ||
            (isAuction(type_) &&
                (listingFlags & FLAG_MASK_ACCEPT_OFFERS) != 0 &&
                !hasBid(listingFlags)));
    }

    function hasBid(uint8 listingFlags) internal pure returns (bool) {
        return listingFlags & FLAG_MASK_HAS_BID != 0;
    }

    function isFinalized(uint8 listingFlags) internal pure returns (bool) {
        return listingFlags & FLAG_MASK_FINALIZED != 0;
    }

    function isFinalizedLogically(uint8 listingFlags) internal pure returns (bool) {
        return listingFlags & FLAG_MASK_FINALIZED_LOGICALLY != 0;
    }

    function sellerIsTokenCreator(
        uint8 listingFlags
    ) internal pure returns (bool) {
        return listingFlags & FLAG_MASK_TOKEN_CREATOR != 0;
    }

    function modifyListing(
        uint40 listingId,
        Listing storage listing,
        uint256 initialAmount,
        uint48 startTime,
        uint48 endTime,
        uint16 extensionInterval
    ) public {
        require(endTime > startTime, "End time must be after start time");
        require(
            startTime == 0 ||
                (startTime == listing.details.startTime &&
                    endTime > block.timestamp) ||
                startTime > block.timestamp,
            "Start and end time cannot occur in the past"
        );
        require(
            !isFinalized(listing.flags) &&
                ((!isAuction(listing.details.type_) &&
                    listing.totalSold == 0) ||
                    (isAuction(listing.details.type_) &&
                        listing.bidCount == 0)),
            "Cannot modify listing that has already started or completed"
        );
        listing.details.initialAmount = initialAmount;
        listing.details.startTime = startTime;
        listing.details.endTime = endTime;
        listing.details.extensionInterval = extensionInterval;

        emit ModifyListing(listingId, initialAmount, startTime, endTime, extensionInterval);
    }
}
"
    },
    "contracts/marketplace/libs/SettlementLib.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

/// @author: VERTICAL.art

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "@manifoldxyz/royalty-registry-solidity/contracts/IRoyaltyEngineV1.sol";

import "../ILazyDelivery.sol";

import "./MarketplaceLib.sol";
import "./TokenLib.sol";

/**
 * @dev Marketplace settlement logic
 */
library SettlementLib {
    using EnumerableSet for EnumerableSet.AddressSet;

    event Escrow(address indexed receiver, address erc20, uint256 amount);

    /**
     * Purchase logic
     */
    function performPurchase(
        address royaltyEngineV1,
        uint40 listingId,
        MarketplaceLib.Listing storage listing,
        uint24 count,
        mapping(address => uint256) storage feesCollected
    ) public {
        require(
            MarketplaceLib.isPurchase(listing.details.type_),
            "Not available to purchase"
        );
        require(
            listing.details.startTime <= block.timestamp,
            "Listing has not started"
        );
        require(
            listing.details.endTime > block.timestamp ||
                listing.details.startTime == 0,
            "Listing is expired"
        );

        listing.totalSold += count;
        require(
            listing.totalSold <= listing.details.totalAvailable,
            "Not enough left"
        );

        // If startTime is 0, start on first purchase
        if (listing.details.startTime == 0) {
            listing.details.startTime = uint48(block.timestamp);
            listing.details.endTime += uint48(block.timestamp);
        }

        uint256 totalPrice = _computeTotalPrice(listing, count);
        if (listing.details.erc20 == address(0)) {
            if (
                listing.details.type_ ==
                MarketplaceLib.ListingType.LINEAR_DUTCH_AUCTION ||
                listing.details.type_ ==
                MarketplaceLib.ListingType.EXPO_DUTCH_AUCTION
            ) {
                // For dutch auctions, price may have changed so allow for a mismatch of funds sent
                receiveTokens(listing, msg.sender, totalPrice, true, false);
            } else {
                receiveTokens(listing, msg.sender, totalPrice, false, true);
            }
        } else {
            require(msg.value == 0, "Invalid amount");
        }

        if (listing.token.lazy) {
            // Lazy delivered
            deliverTokenLazy(
                listingId,
                listing,
                msg.sender,
                count,
                totalPrice,
                0
            );
        } else {
            // Single item
            deliverToken(listing, msg.sender, count, totalPrice, false);
        }

        // Automatically finalize listing if all sold
        if (listing.details.totalAvailable == listing.totalSold) {
            listing.flags |= MarketplaceLib.FLAG_MASK_FINALIZED;
        }

        // Pay seller
        if (listing.details.erc20 == address(0)) {
            _paySeller(
                royaltyEngineV1,
                listing,
                address(this),
                totalPrice,
                feesCollected
            );
        } else {
            _paySeller(
                royaltyEngineV1,
                listing,
                msg.sender,
                totalPrice,
                feesCollected
            );
        }

        emit MarketplaceLib.PurchaseEvent(
            listingId,
            msg.sender,
            count,
            totalPrice
        );
    }

    /**
     * Bid logic
     */
    function _preBidCheck(MarketplaceLib.Listing storage listing) private {
        require(
            MarketplaceLib.isAuction(listing.details.type_),
            "Not available to bid"
        );
        require(
            listing.details.startTime <= block.timestamp,
            "Listing has not started"
        );
        require(
            listing.details.endTime > block.timestamp ||
                listing.details.startTime == 0,
            "Listing is expired"
        );

        // If startTime is 0, start on first purchase
        if (listing.details.startTime == 0) {
            listing.details.startTime = uint48(block.timestamp);
            listing.details.endTime += uint48(block.timestamp);
        }
    }

    function _postBidExtension(MarketplaceLib.Listing storage listing) private {
        if (
            listing.details.extensionInterval > 0 &&
            listing.details.endTime <=
            (block.timestamp + listing.details.extensionInterval)
        ) {
            // Extend auction time if necessary
            listing.details.endTime =
                uint48(block.timestamp) +
                listing.details.extensionInterval;
        }
    }

    function performBid(
        uint40 listingId,
        MarketplaceLib.Listing storage listing,
        uint24 count,
        uint256 bidAmount,
        bool increase,
        mapping(address => mapping(address => uint256)) storage escrow,
        mapping(uint40 => mapping(uint16 => MarketplaceLib.Bid)) storage bids
    ) public {
        // Basic auction
        _preBidCheck(listing);

        address payable bidder = payable(msg.sender);

        if (
            listing.details.type_ ==
            MarketplaceLib.ListingType.INDIVIDUAL_AUCTION
        ) {
            require(count == 1, "Invalid Count");

            performIndividualAuctionBid(
                listingId,
                bidder,
                listing,
                bidAmount,
                increase,
                escrow,
                bids
            );
        } else if (
            listing.details.type_ == MarketplaceLib.ListingType.RANKED_AUCTION
        ) {
            require(count == 1, "Invalid Count");

            performRankedAuctionBid(
                listingId,
                bidder,
                listing,
                bidAmount,
                increase,
                escrow,
                bids
            );
        } else if (
            listing.details.type_ ==
            MarketplaceLib.ListingType.SETTLEMENT_DUTCH_AUCTION
        ) {
            performSettlementDutchAuctionBid(
                listingId,
                bidder,
                listing,
                count,
                bids
            );
        }

        emit MarketplaceLib.BidEvent(listingId, bidder, bidAmount);

        _postBidExtension(listing);
    }

    function performIndividualAuctionBid(
        uint40 listingId,
        address payable bidder,
        MarketplaceLib.Listing storage listing,
        uint256 bidAmount,
        bool increase,
        mapping(address => mapping(address => uint256)) storage escrow,
        mapping(uint40 => mapping(uint16 => MarketplaceLib.Bid)) storage bids
    ) public {
        MarketplaceLib.Bid storage currentBid = bids[listingId][0];
        if (MarketplaceLib.hasBid(listing.flags)) {
            if (currentBid.bidder == bidder) {
                // Bidder is the current high bidder
                require(bidAmount > 0 && increase, "Existing bid");
                receiveTokens(listing, bidder, bidAmount, false, true);
                bidAmount += currentBid.amount;
            } else {
                // Bidder is not the current high bidder
                // Check minimum bid requirements
                require(
                    bidAmount >=
                        computeMinBid(
                            listing.details.initialAmount,
                            currentBid.amount,
                            listing.details.minIncrementBPS
                        ),
                    "Minimum bid not met"
                );
                receiveTokens(listing, bidder, bidAmount, false, true);
                // Refund bid amount
                refundTokens(
                    listing.details.erc20,
                    currentBid.bidder,
                    currentBid.amount,
                    escrow
                );
            }
        } else {
            // Check minimum bid requirements
            require(
                bidAmount >= listing.details.initialAmount,
                "Invalid bid amount"
            );
            // Set has bid flag first to prevent re-entrancy
            listing.flags |= MarketplaceLib.FLAG_MASK_HAS_BID;
            receiveTokens(listing, bidder, bidAmount, false, true);
            listing.bidCount++;
        }
        // Update bidder if necessary
        if (currentBid.bidder != bidder) currentBid.bidder = bidder;
        // Update amount and timestamp
        currentBid.amount = bidAmount;
        currentBid.timestamp = uint48(block.timestamp);
    }

    function performRankedAuctionBid(
        uint40 listingId,
        address payable bidder,
        MarketplaceLib.Listing storage listing,
        uint256 bidAmount,
        bool increase,
        mapping(address => mapping(address => uint256)) storage escrow,
        mapping(uint40 => mapping(uint16 => MarketplaceLib.Bid)) storage bids
    ) public {
        uint16 editionSize = listing.details.editionSize;

        if (increase) {
            require(bidAmount > 0, "none bid amount");

            uint16 pos = 0;
            for (; pos < listing.bidCount; pos++) {
                if (bids[listingId][pos].bidder == bidder) {
                    break;
                }
            }

            require(pos < listing.bidCount, "existing bid");

            receiveTokens(listing, bidder, bidAmount, false, true);

            uint256 newBidAmount = bids[listingId][pos].amount + bidAmount;
            uint16 index = pos;
            for (; index > 0; index--) {
                if (newBidAmount <= bids[listingId][index - 1].amount) {
                    break;
                }
                bids[listingId][index] = bids[listingId][index - 1];
            }
            bids[listingId][index].bidder = bidder;
            bids[listingId][index].amount = newBidAmount;
            bids[listingId][index].timestamp = uint48(block.timestamp);
        } else {
            if (listing.bidCount < editionSize) {
                // Check minimum bid requirements
                require(
                    bidAmount >= listing.details.initialAmount,
                    "Invalid bid amount"
                );

                uint16 index = listing.bidCount;
                for (; index > 0; index--) {
                    if (bidAmount <= bids[listingId][index].amount) {
                        break;
                    }
                    bids[listingId][index] = bids[listingId][index - 1];
                }
                bids[listingId][index].bidder = bidder;
                bids[listingId][index].amount = bidAmount;
                bids[listingId][index].timestamp = uint48(block.timestamp);
                // Set has bid flag first to prevent re-entrancy
                listing.flags |= MarketplaceLib.FLAG_MASK_HAS_BID;
                listing.bidCount++;
                receiveTokens(listing, bidder, bidAmount, false, true);
            } else {
                require(
                    bidAmount >=
                        computeMinBid(
                            listing.details.initialAmount,
                            bids[listingId][editionSize - 1].amount,
                            listing.details.minIncrementBPS
                        ),
                    "Minimum bid not met"
                );

                receiveTokens(listing, bidder, bidAmount, false, true);
                // Refund bid amount
                refundTokens(
                    listing.details.erc20,
                    bids[listingId][editionSize - 1].bidder,
                    bids[listingId][editionSize - 1].amount,
                    escrow
                );

                uint16 index = editionSize - 1;
                for (; index > 0; index--) {
                    if (bidAmount <= bids[listingId][index - 1].amount) {
                        break;
                    }
                    bids[listingId][index] = bids[listingId][index - 1];
                }
                bids[listingId][index].bidder = bidder;
                bids[listingId][index].amount = bidAmount;
                bids[listingId][index].timestamp = uint48(block.timestamp);
            }
        }
    }

    function performSettlementDutchAuctionBid(
        uint40 listingId,
        address payable bidder,
        MarketplaceLib.Listing storage listing,
        uint24 count,
        mapping(uint40 => mapping(uint16 => MarketplaceLib.Bid)) storage bids
    ) public {
        uint16 editionSize = listing.details.editionSize;

        require(
            listing.bidCount < editionSize,
            "No more bid can be performed!"
        );

        listing.totalSold += count;

        require(
            listing.totalSold <= listing.details.totalAvailable,
            "Not enough left"
        );

        uint256 totalPrice = _computeTotalPrice(listing, count);
        bids[listingId][listing.bidCount].bidder = bidder;
        bids[listingId][listing.bidCount].amount = totalPrice;
        bids[listingId][listing.bidCount].timestamp = uint48(block.timestamp);
        // Set has bid flag first to prevent re-entrancy
        listing.flags |= MarketplaceLib.FLAG_MASK_HAS_BID;
        listing.bidCount++;
        receiveTokens(listing, bidder, totalPrice, false, true);

        if (listing.token.lazy) {
            // Lazy delivered
            deliverTokenLazy(
                listingId,
                listing,
                msg.sender,
                count,
                totalPrice,
                0
            );
        } else {
            // Single item
            deliverToken(listing, msg.sender, count, totalPrice, false);
        }

        // Automatically finalize listing if all sold
        if (
            listing.details.totalAvailable == listing.totalSold ||
            listing.bidCount == editionSize
        ) {
            listing.flags |= MarketplaceLib.FLAG_MASK_FINALIZED_LOGICALLY;
        }
    }

    /**
     * Offer logic
     */
    function makeOffer(
        uint40 listingId,
        MarketplaceLib.Listing storage listing,
        uint256 offerAmount,
        mapping(address => MarketplaceLib.Offer) storage offers,
        EnumerableSet.AddressSet storage offerAddresses,
        bool increase
    ) public {
        require(
            MarketplaceLib.canOffer(listing.details.type_, listing.flags),
            "Cannot make offer"
        );
        require(
            offerAmount <= 0xffffffffffffffffffffffffffffffffffffffffffffffffff
        );
        require(
            listing.details.startTime <= block.timestamp,
            "Listing has not started"
        );
        require(
            listing.details.endTime > block.timestamp ||
                listing.details.startTime == 0,
            "Listing is expired"
        );

        receiveTokens(listing, payable(msg.sender), offerAmount, false, true);
        MarketplaceLib.Offer storage currentOffer = offers[msg.sender];
        currentOffer.timestamp = uint48(block.timestamp);
        if (offerAddresses.contains(msg.sender)) {
            // Has existing offer, increase offer
            require(increase, "Existing offer");
            currentOffer.amount += uint200(offerAmount);
        } else {
            offerAddresses.add(msg.sender);
            currentOffer.amount = uint200(offerAmount);
        }
        emit MarketplaceLib.OfferEvent(
            listingId,
            msg.sender,
            currentOffer.amount
        );
    }

    function rescindOffer(
        uint40 listingId,
        MarketplaceLib.Listing storage listing,
        address offerAddress,
        mapping(address => MarketplaceLib.Offer) storage offers,
        EnumerableSet.AddressSet storage offerAddresses
    ) public {
        require(offerAddresses.contains(offerAddress), "No offers found");
        MarketplaceLib.Offer storage currentOffer = offers[offerAddress];
        require(!currentOffer.accepted, "Offer already accepted");
        uint256 offerAmount = currentOffer.amount;

        // Remove offers first to prevent re-entrancy
        offerAddresses.remove(offerAddress);
        delete offers[offerAddress];

        refundTokens(listing.details.erc20, payable(offerAddress), offerAmount);

        emit MarketplaceLib.RescindOfferEvent(
            listingId,
            offerAddress,
            offerAmount
        );
    }

    /**
     * Deliver tokens
     */
    function deliverToken(
        MarketplaceLib.Listing storage listing,
        address to,
        uint24 count,
        uint256 payableAmount,
        bool reverse
    ) public {
        // Check listing deliver fees if applicable
        if (
            payableAmount > 0 &&
            (listing.fees.deliverBPS > 0 || listing.fees.deliverFixed > 0)
        ) {
            uint256 deliveryFee = computeDeliverFee(listing, payableAmount);
            receiveTokens(listing, msg.sender, deliveryFee, false, true);
            // Pay out
            distributeProceeds(listing, address(this), deliveryFee);
        }

        if (listing.token.spec == TokenLib.Spec.ERC721) {
            require(count == 1, "Invalid amount");
            TokenLib._erc721Transfer(
                listing.token.address_,
                listing.token.id,
                address(this),
                to
            );
        } else if (listing.token.spec == TokenLib.Spec.ERC1155) {
            if (!reverse) {
                TokenLib._erc1155Transfer(
                    listing.token.address_,
                    listing.token.id,
                    count,
                    address(this),
                    to
                );
            } else if (listing.details.totalAvailable > listing.totalSold) {
                require(count == 1, "Invalid amount");
                TokenLib._erc1155Transfer(
                    listing.token.address_,
                    listing.token.id,
                    listing.details.totalAvailable - listing.totalSold,
                    address(this),
                    to
                );
            }
        } else {
            revert("Unsupported token spec");
        }
    }

    /**
     * Deliver lazy tokens
     */
    function deliverTokenLazy(
        uint40 listingId,
        MarketplaceLib.Listing storage listing,
        address to,
        uint24 count,
        uint256 payableAmount,
        uint256 index
    ) public {
        // Check listing deliver fees if applicable
        if (
            payableAmount > 0 &&
            (listing.fees.deliverBPS > 0 || listing.fees.deliverFixed > 0)
        ) {
            // Receive tokens for fees
            uint256 deliveryFee = computeDeliverFee(listing, payableAmount);
            receiveTokens(listing, msg.sender, deliveryFee, false, true);
            // Pay out
            distributeProceeds(listing, address(this), deliveryFee);
        }

        // Call deliver (which can mint)
        ILazyDelivery(listing.token.address_).deliver(
            listingId,
            to,
            listing.token.id,
            count,
            payableAmount,
            listing.details.erc20,
            index
        );
    }

    /**
     * Distribute proceeds
     */
    function distributeProceeds(
        MarketplaceLib.Listing storage listing,
        address source,
        uint256 amount
    ) public {
        if (listing.receivers.length > 0) {
            uint256 totalSent;
            uint256 receiverIndex;
            for (
                receiverIndex;
                receiverIndex < listing.receivers.length - 1;

            ) {
                uint256 receiverAmount = (amount *
                    listing.receivers[receiverIndex].receiverBPS) / 10000;
                sendTokens(
                    listing.details.erc20,
                    source,
                    listing.receivers[receiverIndex].receiver,
                    receiverAmount
                );
                totalSent += receiverAmount;
                unchecked {
                    ++receiverIndex;
                }
            }
            require(totalSent < amount, "Settlement error");
            sendTokens(
                listing.details.erc20,
                source,
                listing.receivers[receiverIndex].receiver,
                amount - totalSent
            );
        } else {
            sendTokens(listing.details.erc20, source, listing.seller, amount);
        }
    }

    /**
     * Receive tokens.  Returns amount received.
     */
    function receiveTokens(
        MarketplaceLib.Listing storage listing,
        address source,
        uint256 amount,
        bool refundExcess,
        bool strict
    ) public {
        if (source == address(this)) return;

        if (listing.details.erc20 == address(0)) {
            if (strict) {
                require(
                    msg.value == amount,
                    msg.value < amount ? "Insufficient funds" : "Invalid amount"
                );
            } else {
                if (msg.value < amount) {
                    revert("Insufficient funds");
                } else if (msg.value > amount && refundExcess) {
                    // Refund excess
                    (bool success, ) = payable(source).call{
                        value: msg.value - amount
                    }("");
                    require(success, "Token send failure");
                }
            }
        } else {
            require(msg.value == 0, "Invalid amount");
            require(
                IERC20(listing.details.erc20).transferFrom(
                    source,
                    address(this),
                    amount
                ),
                "Insufficient funds"
            );
        }
    }

    /**
     * Send proceeds to receiver
     */
    function sendTokens(
        address erc20,
        address source,
        address payable to,
        uint256 amount
    ) public {
        require(source != to, "Invalid send request");

        if (erc20 == address(0)) {
            (bool success, ) = to.call{value: amount}("");
            require(success, "Token send failure");
        } else {
            if (source == address(this)) {
                require(
                    IERC20(erc20).transfer(to, amount),
                    "Insufficient funds"
                );
            } else {
                require(
                    IERC20(erc20).transferFrom(source, to, amount),
                    "Insufficient funds"
                );
            }
        }
    }

    /**
     * Refund tokens
     */
    function refundTokens(
        address erc20,
        address payable to,
        uint256 amount,
        mapping(address => mapping(address => uint256)) storage escrow
    ) public {
        if (erc20 == address(0)) {
            // solhint-disable-next-line avoid-low-level-calls
            (bool success, ) = to.call{value: amount, gas: 20000}("");
            if (!success) {
                escrow[to][erc20] += amount;
                emit Escrow(to, erc20, amount);
            }
        } else {
            try IERC20(erc20).transfer(to, amount) {} catch {
                escrow[to][erc20] += amount;
                emit Escrow(to, erc20, amount);
            }
        }
    }

    function refundTokens(
        address erc20,
        address payable to,
        uint256 amount
    ) public {
        if (erc20 == address(0)) {
            // solhint-disable-next-line avoid-low-level-calls
            (bool success, ) = to.call{value: amount}("");
            require(success);
        } else {
            IERC20(erc20).transfer(to, amount);
        }
    }

    /**
     * Compute deliver fee
     */
    function computeDeliverFee(
        MarketplaceLib.Listing memory listing,
        uint256 price
    ) public pure returns (uint256) {
        return
            (price * listing.fees.deliverBPS) /
            10000 +
            listing.fees.deliverFixed;
    }

    /**
     * Compute current listing price
     */
    function computeListingPrice(
        uint40 listingId,
        MarketplaceLib.Listing storage listing,
        mapping(uint40 => mapping(uint16 => MarketplaceLib.Bid)) storage bids
    ) public view returns (uint256 currentPrice) {
        require(
            listing.details.endTime > block.timestamp ||
                listing.details.startTime == 0 ||
                !MarketplaceLib.isFinalized(listing.flags),
            "Listing is expired"
        );
        currentPrice = listing.details.initialAmount;
        if (
            listing.details.type_ ==
            MarketplaceLib.ListingType.LINEAR_DUTCH_AUCTION
        ) {
            uint256 totalDecAmount = (listing.details.dutchDecAmount *
                (block.timestamp - listing.details.startTime)) /
                listing.details.dutchInterval;
            currentPrice = listing.details.restingAmount;
            if (listing.details.initialAmount > totalDecAmount) {
                uint256 reducedPrice = listing.details.initialAmount -
                    totalDecAmount;
                if (currentPrice < reducedPrice) {
                    currentPrice = reducedPrice;
                }
            }
        } else if (
            listing.details.type_ ==
            MarketplaceLib.ListingType.EXPO_DUTCH_AUCTION ||
            listing.details.type_ ==
            MarketplaceLib.ListingType.SETTLEMENT_DUTCH_AUCTION
        ) {
            uint256 halfCount = (block.timestamp - listing.details.startTime) /
                listing.details.dutchInterval;
            currentPrice = listing.details.restingAmount;
            uint256 reducedPrice = listing.details.initialAmount /
                (2 ** halfCount);
            if (currentPrice < reducedPrice) {
                currentPrice = reducedPrice;
            }
        } else {
            if (MarketplaceLib.hasBid(listing.flags)) {
                if (
                    listing.details.type_ ==
                    MarketplaceLib.ListingType.INDIVIDUAL_AUCTION
                ) {
                    currentPrice = computeMinBid(
                        listing.details.initialAmount,
                        bids[listingId][0].amount,
                        listing.details.minIncrementBPS
                    );
                }
            }
        }
        return currentPrice;
    }

    /**
     * Compute total price for a <COUNT> of items to buy
     */
    function computeTotalPrice(
        MarketplaceLib.Listing storage listing,
        uint24 count
    ) public view returns (uint256) {
        require(
            listing.details.endTime > block.timestamp ||
                listing.details.startTime == 0 ||
                !MarketplaceLib.isFinalized(listing.flags),
            "Listing is expired"
        );
        return _computeTotalPrice(listing, count);
    }

    function _computeTotalPrice(
        MarketplaceLib.Listing storage listing,
        uint24 count
    ) private view returns (uint256 price) {
        if (
            listing.details.type_ ==
            MarketplaceLib.ListingType.LINEAR_DUTCH_AUCTION
        ) {
            uint256 totalDecAmount = (listing.details.dutchDecAmount *
                (block.timestamp - listing.details.startTime)) /
                listing.details.dutchInterval;
            uint256 currentAmount = listing.details.restingAmount;
            if (listing.details.initialAmount > totalDecAmount) {
                uint256 reducedAmount = listing.details.initialAmount -
                    totalDecAmount;
                if (currentAmount < reducedAmount) {
                    currentAmount = reducedAmount;
                }
            }
            price = currentAmount * count;
        } else if (
            listing.details.type_ ==
            MarketplaceLib.ListingType.EXPO_DUTCH_AUCTION ||
            listing.details.type_ ==
            MarketplaceLib.ListingType.SETTLEMENT_DUTCH_AUCTION
        ) {
            uint256 halfCount = (block.timestamp - listing.details.startTime) /
                listing.details.dutchInterval;
            uint256 currentAmount = listing.details.restingAmount;
            uint256 reducedAmount = listing.details.initialAmount /
                (2 ** halfCount);
            if (currentAmount < reducedAmount) {
                currentAmount = reducedAmount;
            }
            price = currentAmount * count;
        } else {
            price = listing.details.initialAmount * count;
        }
    }

    /**
     * Get the min bid
     */
    function computeMinBid(
        uint256 baseAmount,
        uint256 currentAmount,
        uint16 minIncrementBPS
    ) public pure returns (uint256) {
        if (currentAmount == 0) {
            return baseAmount;
        }
        if (minIncrementBPS == 0) {
            return currentAmount + 1;
        }
        uint256 incrementAmount = (currentAmount * minIncrementBPS) / 10000;
        if (incrementAmount == 0) incrementAmount = 1;
        return currentAmount + incrementAmount;
    }

    /**
     * Helper to settle bid, which pays seller
     */
    function settleBid(
        address royaltyEngineV1,
        MarketplaceLib.Bid storage bid,
        MarketplaceLib.Listing storage listing,
        mapping(address => uint256) storage feesCollected
    ) public {
        settleBid(royaltyEngineV1, bid, listing, 0, feesCollected);
    }

    function settleBid(
        address royaltyEngineV1,
        MarketplaceLib.Bid storage bid,
        MarketplaceLib.Listing storage listing,
        uint256 refundAmount,
        mapping(address => uint256) storage feesCollected
    ) public {
        require(!bid.refunded, "Bid has been refunded");

        if (!bid.settled) {
            // Set to settled first to prevent re-entrancy
            bid.settled = true;
            _paySeller(
                royaltyEngineV1,
                listing,
                address(this),
                bid.amount - refundAmount,
                feesCollected
            );
        }
    }

    /**
     * Refund bid
     */
    function refundBid(
        MarketplaceLib.Bid storage bid,
        MarketplaceLib.Listing storage listing,
        uint256 holdbackBPS,
        mapping(address => mapping(address => uint256)) storage escrow
    ) public {
        require(
            listing.details.type_ ==
                MarketplaceLib.ListingType.INDIVIDUAL_AUCTION,
            "Cannot refund for this type of auction"
        );
        require(!bid.settled, "Cannot refund, already settled");
        if (!bid.refunded) {
            // Set to refunded first to prevent re-entrancy
            bid.refunded = true;
            _refundBid(bid.bidder, bid.amount, listing, holdbackBPS, escrow);
        }
    }

    function _refundBid(
        address payable bidder,
        uint256 amount,
        MarketplaceLib.Listing storage listing,
        uint256 holdbackBPS,
        mapping(address => mapping(address => uint256)) storage escrow
    ) private {
        uint256 refundAmount = amount;

        // Refund amount (less holdback)
        if (holdbackBPS > 0) {
            uint256 holdbackAmount = (refundAmount * holdbackBPS) / 10000;
            refundAmount -= holdbackAmount;
            // Distribute holdback
            distributeProceeds(listing, address(this), holdbackAmount);
        }
        // Refund bidder
        refundTokens(listing.details.erc20, bidder, refundAmount, escrow);
    }

    /**
     * Helper to settle offer, which pays seller
     */
    function settleOffer(
        address royaltyEngineV1,
        uint40 listingId,
        MarketplaceLib.Listing storage listing,
        MarketplaceLib.Offer storage offer,
        address payable offerAddress,
        mapping(address => uint256) storage feesCollected,
        uint256 maxAmount,
        mapping(address => mapping(address => uint256)) storage escrow
    ) public {
        require(!offer.accepted, "Already settled");

        // Set to accepted first to prevent re-entrancy
        offer.accepted = true;
        uint256 offerAmount = offer.amount;
        if (maxAmount > 0 && maxAmount < offerAmount) {
            // Refund the difference
            refundTokens(
                listing.details.erc20,
                offerAddress,
                offerAmount - maxAmount,
                escrow
            );
            // Set offerAmount to the max amount
            offerAmount = maxAmount;
        }
        _paySeller(
            royaltyEngineV1,
            listing,
            address(this),
            offerAmount,
            feesCollected
        );
        emit MarketplaceLib.AcceptOfferEvent(
            listingId,
            offerAddress,
            offerAmount
        );
    }

    /**
     * Helper to pay seller given amount
     */
    function _paySeller(
        address royaltyEngineV1,
        MarketplaceLib.Listing storage listing,
        address source,
        uint256 amount,
        mapping(address => uint256) storage feesCollected
    ) private {
        uint256 sellerAmount = amount;
        if (listing.marketplaceBPS > 0) {
            uint256 marketplaceAmount = (amount * listing.marketplaceBPS) /
                10000;
            sellerAmount -= marketplaceAmount;
            receiveTokens(listing, source, marketplaceAmount, false, false);
            feesCollected[listing.details.erc20] += marketplaceAmount;
        }
        if (listing.curationBPS > 0) {
            uint256 curationFeeAmount = (amount * listing.curationBPS) / 10000;
            sellerAmount -= curationFeeAmount;
            receiveTokens(listing, source, curationFeeAmount, false, false);
            feesCollected[listing.details.erc20] += curationFeeAmount;
        }

        if (
            !MarketplaceLib.sellerIsTokenCreator(listing.flags) &&
            !listing.token.lazy
        ) {
            // Handle royalties if not listed by token creator and not a lazy mint (lazy mints don't have royalties)
            try
                IRoyaltyEngineV1(royaltyEngineV1).getRoyalty(
                    listing.token.address_,
                    listing.token.id,
                    amount
                )
            returns (
                address payable[] memory recipients,
                uint256[] memory amounts
            ) {
                // Only pay royalties if properly configured
                if (
                    recipients.length > 1 ||
                    (recipients.length == 1 &&
                        recipients[0] != listing.seller &&
                        recipients[0] != address(0))
                ) {
                    for (uint i; i < recipients.length; ) {
                        if (recipients[i] != address(0) && amounts[i] > 0) {
                            sellerAmount -= amounts[i];
                            sendTokens(
                                listing.details.erc20,
                                source,
                                recipients[i],
                                amounts[i]
                            );
                        }
                        unchecked {
                            ++i;
                        }
                    }
                }
            } catch {}
        }
        distributeProceeds(listing, source, sellerAmount);
    }
}
"
