contract SWMetaversity is ERC20, ERC20Burnable, Ownable, ReentrancyGuard {

    uint256 public MAX_SWDM_TOKENS;
    uint256 public maxMint;
    uint256 public minMint;
	uint256 public ftETHPrice;
    uint256 public ftUSDTPrice;
    
    address public PrivateSale = 0x91ce3E3D73709A903a537cE3350CcD61dC52423B;
    address public PublicSale = 0x36450a3C424f01AeF042F9f657c2f24481299571;
    address public DEXLiquidity = 0x118436FC431F3546b439383a37db0563F42B17E8;
    address public Advisors = 0x4748034DbF4745797C92064ce56537dEdF9b5505;
    address public FoundersAndTeam = 0xA891fDF3624DFfE810999Abd8A48bCce34b76Ce3;
    address public Development = 0xFE98cA5503CE9f49D51A46F0D7738d1E899e1828;
    address public Operations = 0x73b0Ec653c5D33bd897e6091a94Bc6a931497F42;
    address public Staking = 0xD35cF3a866B115f46778d1833c30f176A6B5097C;
    address public InGameRewards = 0xD823c6454FC8612AcC99c6d34fe84ebBFE3131aC;
    address public Reserve = 0xB34B3152400D1311294d97A559aDF6d627F553f7;
    address public Marketing = 0x6d732794fE575b978EC44b83060b9dA30AEE27Fd;
    address public StakingRewards = 0x2c849e50d3597008012a04D3E95b891adD673150;

    address public buyUsingUSDT = 0xdAC17F958D2ee523a2206206994597C13D831ec7; // Contract Address of USDT Token
    USDTCalle usdt = USDTCalle(buyUsingUSDT);


    constructor(string memory _name, string memory _symbol, uint256 _MAX_SWDM_TOKENS,  uint256 _maxMint, uint256 _minMint, uint256 _ftETHPrice, uint256 _ftUSDTPrice) ERC20(_name, _symbol) {
        MAX_SWDM_TOKENS = _MAX_SWDM_TOKENS * 10 ** decimals();
        maxMint = _maxMint;
        minMint = _minMint;
        ftETHPrice = _ftETHPrice;
        ftUSDTPrice = _ftUSDTPrice;
        _mint(StakingRewards, 6000000 * 10 ** decimals()); // 4% SWDM Tokens of Total Supply will be preminted/reserved in StakingRewards Account
        _mint(DEXLiquidity, 15750000 * 10 ** decimals());  // 10.50% SWDM Tokens of Total Supply will be preminted/reserved in DEXLiquidity Account
        _mint(InGameRewards, 24000000 * 10 ** decimals()); // 16% SWDM Tokens of Total Supply will be preminted/reserved in InGameRewards Account
    }

    // This Function is used to Buy SWDM Token Via ETH
    function buyViaETH(address to, uint256 amount) external payable nonReentrant {
        require(amount > 0, "Token Amount Should be greater than zero");
        require(totalSupply() < MAX_SWDM_TOKENS,"All SWDM Tokens Have been minted.");
        require(totalSupply() + amount * 10 ** decimals() <= MAX_SWDM_TOKENS,"Token amount exceeds with available SWDM FT's.");
