    "src/ApeVineVouchers.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import {ERC1155P} from "ERC1155P/ERC1155P.sol";
import {Ownable} from "solady/auth/Ownable.sol";
import {ProofOfWine} from "./ProofOfWine.sol";
import {MembersOnly} from "./MembersOnly.sol";

struct VoucherConfig {
    uint16 totalMinted;               /// - [0..15]
    uint16 numRedeemed;               /// - [16..31]
    uint16 maxPerWallet;              /// - [32..47]
    uint16 maxSupply;                 /// - [48..63]
    uint32 redeemedStartTokenId;      /// - [64..95]
    bool mintIsActive;                /// - [96..103]
    bool redemptionIsActive;          /// - [104..111]
    uint72 pricePerBottle;            /// - [112..183]
    uint72 pricePerCase;              /// - [184..255]
}

contract ApeVineVouchers is ERC1155P, Ownable {
    error NonexistentToken();
    error VoucherNotMintable();
    error ExceedMaxPerWallet();
    error WrongValueSent();
    error FailedToWithdraw();
    error RedemptionNotActive();
    error ExceedMaxSupply();
    error InvalidConfig();
    error UnlaunchedToken();
    error NotAMember();

    uint256 internal _nextConfigId;
    string internal _uri;

    ProofOfWine public immutable proofOfWine;
    MembersOnly public immutable membersOnly;
    mapping(uint256 => VoucherConfig) public voucherConfigs;

    constructor(string memory vouchersUri, string memory membersOnlyUri, string memory proofOfWineUri) ERC1155P("ApeVineVouchers", "AVV") {
        _initializeOwner(tx.origin);

        _mint(tx.origin, _nextConfigId, 100, "");

        voucherConfigs[_nextConfigId] = VoucherConfig({
            totalMinted: 100,
            numRedeemed: 0,
            maxSupply: 1000,
            maxPerWallet: 1000,
            redeemedStartTokenId: 1,
            mintIsActive: false,
            redemptionIsActive: false,
            pricePerBottle: 0.045 ether,
            pricePerCase: 0.5 ether
        });

        unchecked { ++_nextConfigId; }

        membersOnly = new MembersOnly(membersOnlyUri);
        proofOfWine = new ProofOfWine(proofOfWineUri);

        _uri = vouchersUri;
    }

    function mint(uint256 id, uint16 amount) external payable {
        VoucherConfig memory config = voucherConfigs[id];

        if (!config.mintIsActive) revert VoucherNotMintable();
        if (id > 0 && membersOnly.balanceOf(msg.sender) == 0) revert NotAMember();
        if (amount + _numberMinted(msg.sender, id) > config.maxPerWallet) revert ExceedMaxPerWallet();
        if (msg.value != config.pricePerBottle * amount) revert WrongValueSent();
        if (amount + config.totalMinted > config.maxSupply) revert ExceedMaxSupply();

        unchecked {
            voucherConfigs[id].totalMinted += amount;
        }

        _mint(msg.sender, id, amount, "");
    }

    function mintCases(uint256 id, uint16 numberOfCases) external payable {
        VoucherConfig memory config = voucherConfigs[id];

        uint16 amount = numberOfCases * 12;

        if (!config.mintIsActive) revert VoucherNotMintable();
        if (amount + _numberMinted(msg.sender, id) > config.maxPerWallet) revert ExceedMaxPerWallet();
        if (msg.value != config.pricePerCase * numberOfCases) revert WrongValueSent();
        if (amount + config.totalMinted > config.maxSupply) revert ExceedMaxSupply();

        unchecked {
            voucherConfigs[id].totalMinted += amount;
        }

        _mint(msg.sender, id, amount, "");
    }

    function ownerMint(uint256 id, uint16 amount, address to) external onlyOwner {
        VoucherConfig memory config = voucherConfigs[id];

        if (amount + config.totalMinted > config.maxSupply) revert ExceedMaxSupply();

        unchecked {
            voucherConfigs[id].totalMinted += amount;
        }

        _mint(to, id, amount, "");
    }

    function burnAndRedeem(uint256 id, uint16 amount) external {
        if (amount == 0) revert MintZeroQuantity();
        VoucherConfig memory config = voucherConfigs[id];
        if (!config.redemptionIsActive) revert RedemptionNotActive();

        _burn(msg.sender, id, amount);

        unchecked { 
            voucherConfigs[id].numRedeemed += amount;
        }

        if (id == 0) {
            membersOnly.mint(msg.sender, amount);
        } else {
            for (uint256 i = 0; i < amount;) {
                proofOfWine.mint(msg.sender, config.redeemedStartTokenId + config.numRedeemed + i);
                unchecked { ++i; }
            }
        }
    }

    function setVoucherConfig(
        uint16 maxSupply,
        uint16 maxPerWallet,
        uint72 pricePerBottle,
        uint72 pricePerCase,
        bool immediateMintsAvailable,
        bool immediateRedemptionsAvailable
    ) external onlyOwner {
        if (pricePerBottle == 0 || pricePerCase == 0) revert InvalidConfig();

        uint32 redeemedStartTokenId;

        if (_nextConfigId == 1) {
            redeemedStartTokenId = 1;
        } else {
            VoucherConfig memory lastConfig = voucherConfigs[_nextConfigId - 1];
            redeemedStartTokenId = lastConfig.redeemedStartTokenId + lastConfig.maxSupply;
        }

        voucherConfigs[_nextConfigId] = VoucherConfig({
            totalMinted: 0,
            numRedeemed: 0,
            maxSupply: maxSupply,
            maxPerWallet: maxPerWallet,
            redeemedStartTokenId: redeemedStartTokenId,
            mintIsActive: immediateMintsAvailable,
            redemptionIsActive: immediateRedemptionsAvailable,
            pricePerBottle: pricePerBottle,
            pricePerCase: pricePerCase
        });

        unchecked { ++_nextConfigId; }
    }

    function setBaseUri(string calldata uri) external onlyOwner {
        _uri = uri;
    }

    function setMintIsActive(uint16 id, bool active) external onlyOwner {
        if (_unlaunchedToken(id)) revert UnlaunchedToken();

        voucherConfigs[id].mintIsActive = active;
    }

    function setRedemptionIsActive(uint16 id, bool active) external onlyOwner {
        if (_unlaunchedToken(id)) revert UnlaunchedToken();

        voucherConfigs[id].redemptionIsActive = active;
    }

    function withdraw() external onlyOwner {
        (bool success, ) = payable(msg.sender).call{value: address(this).balance}("");
        if (!success) revert FailedToWithdraw();
    }

    function totalSupply(uint16 tokenId) external view returns (uint256) {
        VoucherConfig memory config = voucherConfigs[tokenId];
        return uint256(config.totalMinted - config.numRedeemed);
    }

    function totalSupply() external view returns (uint256) {
        uint256 _totalSupply;

        uint256 currentTokenIndex;
        while (true) {
            VoucherConfig memory config = voucherConfigs[currentTokenIndex];
            if (config.pricePerBottle == 0) break; // this means it has not been launched yet

            _totalSupply += uint256(config.totalMinted - config.numRedeemed);

            unchecked { ++currentTokenIndex; }
        }

        return _totalSupply;
    }

    function _baseURI() internal view override returns (string memory) {
        return _uri;
    }

    function _unlaunchedToken(uint16 tokenId) internal view returns (bool) {
        VoucherConfig memory config = voucherConfigs[tokenId];

        return config.pricePerBottle == 0;
    }
}
"
