    "src/ProofOfWine.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import {ERC721} from "solady/tokens/ERC721.sol";
import {Ownable} from "solady/auth/Ownable.sol";
import {LibString} from "solady/utils/LibString.sol";

contract ProofOfWine is ERC721, Ownable {
    error ApprovalsNotAllowed();

    address private immutable _apeVineVouchers;
    string private _baseURI;
    bool private approvalsAllowed;

    uint256 public totalSupply;

    constructor(string memory uri) {
        _initializeOwner(tx.origin);
        _apeVineVouchers = msg.sender;
        _baseURI = uri;
    }

    function setApprovalForAll(address operator, bool isApproved) public override {
        if (!approvalsAllowed) revert ApprovalsNotAllowed();

        super.setApprovalForAll(operator, isApproved);
    }

    function approve(address account, uint256 id) public payable override {
        if (!approvalsAllowed) revert ApprovalsNotAllowed();

        super.approve(account, id);
    }

    function mint(address to, uint256 tokenId) external {
        if (msg.sender != _apeVineVouchers) revert Unauthorized();

        unchecked { ++totalSupply; }
        _mint(to, tokenId);
    }

    function setTokenURI(string calldata uri) external onlyOwner {
        _baseURI = uri;
    }

    /**
        @dev toggling approvals off after having previously been enabled will NOT clear existing approvals.
     */
    function setApprovalsAllowed(bool isAllowed) external onlyOwner {
        approvalsAllowed = isAllowed;
    }

    function tokenURI(uint256 tokenId) public view override returns (string memory) {
        if (!_exists(tokenId)) revert TokenDoesNotExist();

        string memory baseURI = _baseURI;
        return bytes(baseURI).length != 0 ? string(abi.encodePacked(baseURI, LibString.toString(tokenId))) : '';
    }

    function name() public view override returns (string memory) {
        return "ApeVine: Proof Of Wine";
    }

    function symbol() public view override returns (string memory) {
        return "POW";
    }
}

"
    },
    "src/MembersOnly.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import {ERC721A} from "ERC721A/ERC721A.sol";
import {Ownable} from "solady/auth/Ownable.sol";

contract MembersOnly is ERC721A, Ownable {
    address private immutable _apeVineVouchers;
    string private _uri;

    constructor(string memory uri) ERC721A("Apevine: Members Only", "AVMO") {
        _initializeOwner(tx.origin);
        _apeVineVouchers = msg.sender;
        _uri = uri;
    }

    function mint(address to, uint256 amount) external {
        if (msg.sender != _apeVineVouchers) revert Unauthorized();

        _mint(to, amount);
    }

    function _baseURI() internal view override returns (string memory) {
        return _uri;
    }

    function setBaseUri(string calldata uri) external onlyOwner {
        _uri = uri;
    }

    function _startTokenId() internal view override returns (uint256) {
        return 1;
    }
}

"
