pragma solidity =0.8.23;

import {SafeERC20, IERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {ECDSA} from "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import {MerkleProof} from "@openzeppelin/contracts/utils/cryptography/MerkleProof.sol";
import {OwnableUpgradeable} from "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import {PausableUpgradeable} from "@openzeppelin/contracts-upgradeable/utils/PausableUpgradeable.sol";

contract AirdropBase is PausableUpgradeable, OwnableUpgradeable {
    using SafeERC20 for IERC20;

    address public vault;
    address public token;
    bytes32 public merkleRoot;
    bytes32 public messageHash;
    uint256 public startTime;
    uint256 public endTime;
    mapping(bytes32 => bool) public claimed;
    mapping(address => bool) public blocked;

    event Claimed(address account, uint256 amount);

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    /// @notice Initializes the contract with necessary parameters.
    /// @param initialOwner_ The initial owner of the contract.
    /// @param vault_ The address of the vault holding the tokens to be airdropped.
    /// @param token_ The token to be airdropped.
    /// @param merkleRoot_ The root of the Merkle tree used for claim verification.
    /// @param messageHash_ The hash of the message for claim verification.
    /// @param startTime_ The start time for the airdrop.
    /// @param endTime_ The end time for the airdrop.
    function initialize(
        address initialOwner_,
        address vault_,
        address token_,
        bytes32 merkleRoot_,
        bytes32 messageHash_,
        uint256 startTime_,
        uint256 endTime_
    ) external initializer {
        __Pausable_init();
        __Ownable_init(initialOwner_);
        vault = vault_;
        token = token_;
        merkleRoot = merkleRoot_;
        messageHash = messageHash_;
        startTime = startTime_;
        endTime = endTime_;
    }

    /// @notice Pauses the contract, preventing claims.
    function pause() external onlyOwner {
        _pause();
    }

    /// @notice Unpauses the contract, allowing claims.
    function unpause() external onlyOwner {
        _unpause();
    }

    /// @notice Sets a new vault address.
    /// @param newVault The new vault address.
    function setVault(address newVault) external onlyOwner {
        vault = newVault;
    }

    /// @notice Sets the start time for the airdrop.
    /// @param timestamp The start time as a timestamp.
    function setStartTime(uint256 timestamp) external onlyOwner {
        require(timestamp < endTime, "Start time too late");
        startTime = timestamp;
    }

    /// @notice Sets the end time for the airdrop.
    /// @param timestamp The end time as a timestamp.
    function setEndTime(uint256 timestamp) external onlyOwner {
        require(startTime < timestamp, "End time too early");
        endTime = timestamp;
    }

    /// @notice Adds addresses to the blocklist.
    /// @param accounts The array of addresses.
    function blocklist(
        address[] calldata accounts,
        bool status
    ) external onlyOwner {
        for (uint256 i; i < accounts.length; i++) {
            blocked[accounts[i]] = status;
        }
    }

    /// @notice Updates the Merkle root.
    /// @param _merkleRoot The new Merkle root.
    function setMerkleRoot(bytes32 _merkleRoot) external onlyOwner {
        merkleRoot = _merkleRoot;
    }

    /// @notice Sets a new message hash for claim verification.
    /// @param _messageHash The new message hash.
    function setMessageHash(bytes32 _messageHash) external onlyOwner {
        messageHash = _messageHash;
    }
}

/// @title Airdrop Contract
/// @notice This contract allows for a token airdrop based on a Merkle tree for efficient proof of inclusion.
contract Airdrop is AirdropBase {
    using SafeERC20 for IERC20;

    /// @notice Allows eligible users to claim their airdrop if they agree to the T&Cs.
    /// @param amount The amount of tokens to claim.
    /// @param proof The Merkle proof to prove eligibility for the claim.
    /// @param agreedToTnC Must be true, signifies that the user agrees to the Terms & Conditions.
    function claim(
        uint256 amount,
        bytes32[] calldata proof,
        bool agreedToTnC
    ) external whenNotPaused {
        require(startTime <= block.timestamp, "Not started");
        require(endTime > block.timestamp, "Ended");
        require(!blocked[msg.sender], "Blocked");
        require(agreedToTnC, "T&C not accepted.");

        _claim(proof, amount);
    }

    /// @notice Allows eligible users to claim their airdrop with a signature for verification.
    /// @param amount The amount of tokens to claim.
    /// @param proof The Merkle proof to prove eligibility for the claim.
    /// @param v The recovery byte of the signature.
    /// @param r Half of the ECDSA signature pair.
    /// @param s Half of the ECDSA signature pair.
    function claim(
        uint256 amount,
        bytes32[] calldata proof,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external whenNotPaused {
        require(startTime <= block.timestamp, "Not started");
        require(endTime > block.timestamp, "Ended");

        address sender = msg.sender;
        require(!blocked[sender], "Blocked");
        require(
            ECDSA.recover(messageHash, v, r, s) == sender,
            "Sender mismatch"
        );
        _claim(proof, amount);
    }

    /// @dev Internal function to handle claims.
    /// @param proof The Merkle proof to prove eligibility for the claim.
    /// @param amount The amount of tokens to claim.
    function _claim(bytes32[] calldata proof, uint256 amount) internal {
        address sender = msg.sender;
        bytes32 leaf = _getLeaf(amount);
        require(!claimed[leaf], "Already claimed");
        require(
            MerkleProof.verifyCalldata(proof, merkleRoot, leaf),
            "Invalid proof"
        );

        claimed[leaf] = true;
        emit Claimed(sender, amount);
        IERC20(token).safeTransferFrom(vault, sender, amount);
    }

    /// @notice Verifies the Merkle proof for a claim.
    /// @param amount The amount of tokens to verify for the claim.
    /// @param proof The Merkle proof to verify.
    /// @return true if the proof is valid, otherwise false.
    function verifyCalldata(
        uint256 amount,
        bytes32[] calldata proof
    ) external view returns (bool) {
        return MerkleProof.verifyCalldata(proof, merkleRoot, _getLeaf(amount));
    }

    function _getLeaf(uint256 amount) internal view returns (bytes32) {
        return keccak256(abi.encodePacked(msg.sender, amount));
    }
}
