// the base implementation for the bridge-contract

contract CyberGlowBridge {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;
    using Address for address;

    struct PoolInfo {
        IERC20 token;
        uint256 amount;
        uint256 fee;
        uint256 min;
        bool enabled;
    }

    PoolInfo[] public poolInfo;

    address public weth;
    address public operator;
    address public feeTo;

    uint256 public nonce;

    mapping(uint256 => bool) public isNonceProcessed; // for storing the nonce process status using boolean and mapping

    enum Step { Burn, Mint }

    /*
     A custom event for bridge which will be emitted when a transaction is processed(burn/mint)
     */

    event Transfer(
        uint256 pid,
        address indexed from,
        address indexed to,
        uint256 amount,
        uint256 date,
        uint256 nonce,
        Step indexed step
    );

    constructor(
        address _operator,
        address _weth,
        address _feeTo
    ) {
        operator = _operator;
        feeTo = _feeTo;
        weth = _weth;
    }

    function burn(
        uint256 _pid,
        uint256 _amount
    ) public {
        require(!msg.sender.isContract(), "sender is not wallet");

        PoolInfo storage pool = poolInfo[_pid];

        require(address(pool.token) != weth, "pool is native");
        require(pool.enabled, "pool is not enabled");
        require(_amount >= pool.min, "not enough fee");

        pool.token.safeTransferFrom(msg.sender, address(this), _amount);
        pool.amount = IERC20(pool.token).balanceOf(address(this));

        emit Transfer(
            _pid,
            msg.sender,
            address(this),
            _amount,
            block.timestamp,
            nonce,
            Step.Burn
        );
        nonce++;
    }

    function burnETH(uint256 _pid) public payable {
        require(!msg.sender.isContract(), "sender is not wallet");

        PoolInfo storage pool = poolInfo[_pid];

        require(address(pool.token) == weth, "pool is not native");
        require(pool.enabled, "pool is not enabled");
        require(msg.value >= pool.min, "not enough fee");

        pool.amount = uint256(address(this).balance);

        emit Transfer(
            _pid,
            msg.sender,
            address(this),
            msg.value,
            block.timestamp,
            nonce,
            Step.Burn
        );
        nonce++;
    }

    function mint(
        uint256 _pid,
        uint256 _amount,
        address _reciever,
        uint256 _nonce
    ) external {
        require(msg.sender == operator, "only operator");
        require(!_reciever.isContract(), "receiver is not wallet");

        PoolInfo storage pool = poolInfo[_pid];

        require(address(pool.token) != weth, "pool is native");
        require(pool.enabled, "pool is not enabled");
        require(IERC20(pool.token).balanceOf(address(this)) >= _amount, "not enough pool amount");
        require(_amount >= pool.min, "not enough fee");

        uint256 fee = _amount.mul(pool.fee).div(10000);
        uint256 amount = _amount.sub(fee);

        require(
            isNonceProcessed[_nonce] == false,
            "transfer already processed"
        );

        isNonceProcessed[_nonce] = true;

        pool.token.safeTransfer(_reciever, amount);
        pool.token.safeTransfer(feeTo, fee);

        pool.amount = IERC20(pool.token).balanceOf(address(this));

        emit Transfer(
            _pid,
            msg.sender,
            _reciever,
            amount,
            block.timestamp,
            _nonce,
            Step.Mint
        );
    }

    function mintETH(
        uint256 _pid,
        uint256 _amount,
        address _reciever,
        uint256 _nonce
    ) external {
        require(msg.sender == operator, "only operator");
        require(!_reciever.isContract(), "receiver is not wallet");

        PoolInfo storage pool = poolInfo[_pid];

        require(address(pool.token) == weth, "pool is not native");
        require(pool.enabled, "pool is not enabled");
        require(uint256(address(this).balance) >= _amount, "not enough pool amount");
        require(_amount >= pool.min, "not enough fee");

        uint256 fee = _amount.mul(pool.fee).div(10000);
        uint256 amount = _amount.sub(fee);

        require(
            isNonceProcessed[_nonce] == false,
            "transfer already processed"
        );

        isNonceProcessed[_nonce] = true;

        payable(_reciever).transfer(amount);
        payable(feeTo).transfer(fee);

        pool.amount = uint256(address(this).balance);

        emit Transfer(
            _pid,
            msg.sender,
            _reciever,
            amount,
            block.timestamp,
            _nonce,
            Step.Mint
        );
    }

    function addPool(address _token, uint256 _fee, uint256 _min) external {
        require(msg.sender == operator, "only operator");
        require(_fee <= 100, "Fee can't exceed 1%");

        poolInfo.push(PoolInfo({
            token: IERC20(_token),
            amount: IERC20(_token).balanceOf(address(this)),
            fee: _fee,
            min: _min,
            enabled: true
        }));
    }

    function updatePool(uint256 _pid, uint256 _fee, uint256 _min, bool _enabled) external {
        require(msg.sender == operator, "only operator");
        require(_fee <= 100, "Fee can't exceed 1%");
        
        PoolInfo storage pool = poolInfo[_pid];

        pool.fee = _fee;
        pool.min = _min;
        pool.enabled = _enabled;
    }

    function updateOperator(address _operator) external {
        require(msg.sender == operator, "only operator");
        operator = _operator;
    }

    function updateFeeTo(address _feeTo) external {
        require(msg.sender == operator, "only operator");
        feeTo = _feeTo;
    }
}
