abstract contract Blacklist {
    using EnumerableSet for EnumerableSet.AddressSet;

    EnumerableSet.AddressSet private _blackList;

    event AddedToBlackList(address indexed account, uint256 indexed timestamp);
    event RemovedFromBlackList(
        address indexed account,
        uint256 indexed timestamp
    );

    modifier onlyNonBlackListed(address account) {
        require(!isBlackListed(account), "BlackList: Blacklisted");
        _;
    }

    function blackListLength() external view returns (uint256) {
        return _blackList.length();
    }

    function blackListByIndex(uint256 index) external view returns (address) {
        return _blackList.at(index);
    }

    function wholeBlackList() external view returns (address[] memory) {
        return _blackList.values();
    }

    function isBlackListed(address account) public view returns (bool) {
        return _blackList.contains(account);
    }

    function _addToBlackList(address account) internal {
        require(account != address(0), "addToBlackList: Zero Address");
        require(!isBlackListed(account), "addToBlackList: Already BlackListed");
        _blackList.add(account);
        emit AddedToBlackList(account, block.timestamp);
    }

    function _removeFromBlackList(address account) internal {
        require(account != address(0), "removeFromBlackList: Zero Address");
        require(isBlackListed(account), "removeFromBlackList: Not BlackListed");
        _blackList.remove(account);
        emit RemovedFromBlackList(account, block.timestamp);
    }

    function _addToBlackListBatch(address[] calldata account) internal {
        uint256 length = account.length;
        require(length != 0, "addToBlackListBatch: No address available");
        for (uint256 i; i < length; i++) {
            _addToBlackList(account[i]);
        }
    }

    function _removeFromBlackListBatch(address[] calldata account) internal {
        uint256 length = account.length;
        require(length != 0, "removeFromBlackListBatch: No address available");
        for (uint256 i; i < length; i++) {
            _removeFromBlackList(account[i]);
        }
    }
}

abstract contract Storage {
    uint256 public constant DIVISOR = 100e18;

    address public marketWallet;
    address public treasury;
    address public usd;
    address public vac;

    uint256 public tokenPerUsd;
    uint256 public liquidityFee = 25e18;
    uint256 public marketWalletFee;
    uint256 public totalUsdCollected;
    uint256 public totalVacSold;

    mapping(address => mapping(address => mapping(address => uint256)))
        public userData;

    event UpdateVac(address indexed oldVac, address indexed newVac);
    event UpdateUsd(address indexed oldUsd, address indexed newUsd);
    event UpdateFee(uint256 indexed oldFee, uint256 indexed newFee);
    event UpdateTokenPerUsd(uint256 indexed oldPrice, uint256 indexed newPrice);
    event UpdateMarketWallet(
        address indexed oldMarketWallet,
        address indexed newMarketWallet
    );
    event UpdateMarketWalletFee(
        uint256 indexed oldMarketWalletFee,
        uint256 indexed newMarketWalletFee
    );
    event TreasuryTransferred(
        address indexed previousTreasury,
        address indexed newTreasury
    );
    event BuyToken(
        address indexed user,
        uint256 indexed givenAmount,
        uint256 marketWalletAmount,
        uint256 liquidityAmount,
        uint256 indexed receivedAmount,
        address givenToken,
        address receivedToken
    );
    event Withdraw(
        address indexed token,
        address indexed to,
        uint256 indexed amount
    );
}

abstract contract MiddleWare {
    modifier onlyValidEoaAddr(address account) {
        require(
            account != address(0) && !Address.isContract(account),
            "Invalid EOA Address"
        );
        _;
    }

    modifier onlyValidContractAddr(address account) {
        require(
            account != address(0) && Address.isContract(account),
            "Invalid Contract Address"
        );
        _;
    }

    modifier onlyValidAddress(address account) {
        require(account != address(0), "Invalid Address");
        _;
    }

    modifier onlyValidAmt(uint256 amount) {
        require(amount > 0, "Invalid amount");
        _;
    }
}

abstract contract AdminPanel is
    Storage,
    MiddleWare,
    Ownable2Step,
    Blacklist,
    Pausable
{
    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

    function addToBlackListBatch(address[] calldata account)
        external
        onlyOwner
    {
        _addToBlackListBatch(account);
    }

    function removeFromBlackListBatch(address[] calldata account)
        external
        onlyOwner
    {
        _removeFromBlackListBatch(account);
    }

    function updateVac(address _newVac) external onlyOwner {
        _updateVac(_newVac);
    }

    function updateUsd(address _newUsd) external onlyOwner {
        _updateUsd(_newUsd);
    }

    function updateTokenPerUsd(uint256 _amount) external onlyOwner {
        _updateTokenPerUsd(_amount);
    }

    function updateMarketWalletFee(uint256 _newFee) external onlyOwner {
        _updateMarketWalletFee(_newFee);
    }

    function updateMarketWallet(address _newMarketWallet) external onlyOwner {
        _updateMarketWallet(_newMarketWallet);
    }

    function transferTreasury(address newTreasury) external onlyOwner {
        _transferTreasury(newTreasury);
    }

    function updateLiquidityFee(uint256 _newLiquidityFee)
        external
        onlyOwner
        onlyValidAmt(_newLiquidityFee)
    {
        require(
            _newLiquidityFee <= DIVISOR,
            "updateLiquidityFee: Fee exceeds divisor"
        );
        liquidityFee = _newLiquidityFee;
    }

    function withdraw(
        address token,
        address to,
        uint256 amount
    ) external onlyOwner onlyValidEoaAddr(to) onlyValidAmt(amount) {
        require(!isBlackListed(to), "withdraw: to is Blacklisted");
        if (token == address(0)) Address.sendValue(payable(to), amount);
        else {
            require(
                IERC20(token).balanceOf(address(this)) >= amount,
                "withdraw: Insufficient balance"
            );
            SafeERC20.safeTransfer(IERC20(token), to, amount);
        }
        emit Withdraw(token, to, amount);
    }

    function _updateVac(address _newVac)
        internal
        onlyValidContractAddr(_newVac)
    {
        emit UpdateVac(vac, _newVac);
        vac = _newVac;
    }

    function _updateUsd(address _newUsd)
        internal
        onlyValidContractAddr(_newUsd)
    {
        emit UpdateUsd(usd, _newUsd);
        usd = _newUsd;
    }

    function _updateTokenPerUsd(uint256 _amount)
        internal
        onlyValidAmt(_amount)
    {
        emit UpdateTokenPerUsd(tokenPerUsd, _amount);
        tokenPerUsd = _amount;
    }

    function _updateMarketWalletFee(uint256 _newMarketWalletFee)
        internal
        onlyValidAmt(_newMarketWalletFee)
    {
        require(
            _newMarketWalletFee <= DIVISOR,
            "updateFee: Fee exceeds divisor"
        );
        emit UpdateFee(marketWalletFee, _newMarketWalletFee);
        marketWalletFee = _newMarketWalletFee;
    }

    function _updateMarketWallet(address _newMarketWallet)
        internal
        onlyValidAddress(_newMarketWallet)
    {
        emit UpdateMarketWallet(marketWallet, _newMarketWallet);
        marketWallet = _newMarketWallet;
    }

    function _transferTreasury(address _newTreasury)
        internal
        onlyValidAddress(_newTreasury)
    {
        emit TreasuryTransferred(treasury, _newTreasury);
        treasury = _newTreasury;
    }
}

contract VacSale is AdminPanel {
    using SafeERC20 for IERC20;

    constructor(
        address _newOwner,
        address _newVac,
        address _newUsd,
        uint256 _newPrice,
        address _newTreasury,
        address _marketWallet,
        uint256 _marketWalletFee
    ) Ownable(_newOwner) {
        _updateVac(_newVac);
        _updateUsd(_newUsd);
        _updateTokenPerUsd(_newPrice);
        _transferTreasury(_newTreasury);
        _updateMarketWallet(_marketWallet);
        _updateMarketWalletFee(_marketWalletFee);
    }

    function buyToken(uint256 amount)
        external
        whenNotPaused
        onlyValidAmt(amount)
        onlyNonBlackListed(_msgSender())
    {
        address user = _msgSender();
        uint256 camt = calculateNumOfTokensPerUsd(amount);
        require(
            IERC20(vac).balanceOf(address(this)) >= camt && camt != 0,
            "buyToken: Insufficient balance || camt is 0"
        );

        totalUsdCollected += amount;
        uint256 marketWalletAmount = (amount * marketWalletFee) / DIVISOR;
        uint256 treasuryAmount = amount - marketWalletAmount;

        IERC20(usd).safeTransferFrom(user, marketWallet, marketWalletAmount);
        IERC20(usd).safeTransferFrom(user, treasury, treasuryAmount);

        userData[usd][vac][user] += camt;
        totalVacSold += camt;

        IERC20(vac).safeTransfer(user, camt);

        emit BuyToken(
            user,
            amount,
            marketWalletAmount,
            ((amount * liquidityFee) / DIVISOR),
            camt,
            usd,
            vac
        );
    }

    function calculateNumOfTokensPerUsd(uint256 amount)
        public
        view
        onlyValidContractAddr(usd)
        returns (uint256)
    {
        require(tokenPerUsd > 0, "Token price is not set");
        return ((amount * tokenPerUsd) / 10**IERC20Metadata(usd).decimals());
    }
}
