contract Clock8008 is ERC721Enumerable, ReentrancyGuard, Ownable {
    using SafeERC20 for IERC20;

    // Set UTC timezone
    mapping(uint256 => int8) timeLocalization;

    string[] private brands = [
        "Timex",
        "HMT",
