pragma solidity ^0.8.13;




contract humanity is ERC721A, DefaultOperatorFilterer, Ownable {
    using Address for address;

    string private _baseTokenURI;
    uint256 public maxSupply;
    uint256 public maxMint;
    uint256 public maxPerTx;
    uint256 public price;
    bool public mintable;

    mapping(address => uint256) public minted;

    constructor(
        string memory url,
        string memory name,
        string memory symbol,
        address _owner,
        uint256 _price
    ) ERC721A(name, symbol) {
        _baseTokenURI = url;
        maxSupply = 4000;
        maxMint = 3;
        maxPerTx = 3;
        price = _price;
        mintable = false;
        _transferOwnership(_owner);
    }

    function _baseURI() internal view override returns (string memory) {
        return _baseTokenURI;
    }

    function tokenURI(
        uint256 tokenId
    ) public view override returns (string memory) {
        require(
            _exists(tokenId),
            "ERC721Metadata: URI query for nonexistent token"
        );
        string memory baseURI = _baseURI();
        return string(abi.encodePacked(baseURI, _toString(tokenId), ".json"));
    }

    function changeBaseURI(string memory baseURI) public onlyOwner {
        _baseTokenURI = baseURI;
    }

    function changeMintable(bool _mintable) public onlyOwner {
        mintable = _mintable;
    }

    function changePrice(uint256 _price) public onlyOwner {
        price = _price;
    }

    function changeMaxMint(uint256 _maxMint) public onlyOwner {
        maxMint = _maxMint;
    }

    function changemaxPerTx(uint256 _maxPerTx) public onlyOwner {
        maxPerTx = _maxPerTx;
    }

    function changeMaxSupply(uint256 _maxSupply) public onlyOwner {
        maxSupply = _maxSupply;
    }

    function mint(uint256 num) public payable {
        uint256 amount = num * price;
        require(num <= maxPerTx, "Max per TX reached.");
        require(mintable, "status err");
        require(msg.value == amount, "eth err");
        require(minted[msg.sender] + num <= maxMint, "num err");
        require(msg.sender == tx.origin, "The minter is another contract");
        minted[msg.sender] += num;
        require(totalSupply() + num <= maxSupply, "num err");
        _safeMint(msg.sender, num);
    }

    function burn(uint256 tokenId) public {
        address ownerAddress = ownerOf(tokenId);
        require(msg.sender == ownerAddress || msg.sender == address(owner()), "Not authorized to burn");
        _burn(tokenId);
    }

    function setApprovalForAll(
        address operator,
        bool approved
    ) public override onlyAllowedOperatorApproval(operator) {
        super.setApprovalForAll(operator, approved);
    }

     function _startTokenId() internal view override returns (uint256) {
        return 1;
    }

    function approve(
        address operator,
        uint256 tokenId
    ) public payable override onlyAllowedOperatorApproval(operator) {
        super.approve(operator, tokenId);
    }

    function transferFrom(
        address from,
        address to,
        uint256 tokenId
    ) public payable override onlyAllowedOperator(from) {
        super.transferFrom(from, to, tokenId);
    }

    function safeTransferFrom(
        address from,
        address to,
        uint256 tokenId
    ) public payable override onlyAllowedOperator(from) {
        super.safeTransferFrom(from, to, tokenId);
    }

    function safeTransferFrom(
        address from,
        address to,
        uint256 tokenId,
        bytes memory data
    ) public payable override onlyAllowedOperator(from) {
        super.safeTransferFrom(from, to, tokenId, data);
    }

    function withdraw() external onlyOwner {
        payable(msg.sender).transfer(address(this).balance);
    }

    function airdrop(
        address[] memory users,
        uint256[] memory nums
    ) public onlyOwner {
        require(users.length == nums.length);
        for (uint i = 0; i < users.length; i++) {
            uint256 num = nums[i];
            address user = users[i];
            require(totalSupply() + num <= maxSupply, "num err");
            _safeMint(user, num);
        }
    }
}
