interface IUniswapV2Factory {
    event PairCreated(
        address indexed token0,
        address indexed token1,
        address pair,
        uint256
    );

    function feeTo() external view returns (address);

    function feeToSetter() external view returns (address);

    function getPair(address tokenA, address tokenB)
        external
        view
        returns (address pair);

    function allPairs(uint256) external view returns (address pair);

    function allPairsLength() external view returns (uint256);

    function createPair(address tokenA, address tokenB)
        external
        returns (address pair);

    function setFeeTo(address) external;

    function setFeeToSetter(address) external;
}

interface IUniswapV2Router02 {
    function factory() external pure returns (address);

    function WETH() external pure returns (address);

    function addLiquidity(
        address tokenA,
        address tokenB,
        uint256 amountADesired,
        uint256 amountBDesired,
        uint256 amountAMin,
        uint256 amountBMin,
        address to,
        uint256 deadline
    )
        external
        returns (
            uint256 amountA,
            uint256 amountB,
            uint256 liquidity
        );

    function addLiquidityETH(
        address token,
        uint256 amountTokenDesired,
        uint256 amountTokenMin,
        uint256 amountETHMin,
        address to,
        uint256 deadline
    )
        external
        payable
        returns (
            uint256 amountToken,
            uint256 amountETH,
            uint256 liquidity
        );

    function swapExactTokensForTokensSupportingFeeOnTransferTokens(
        uint256 amountIn,
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external;

    function swapExactETHForTokensSupportingFeeOnTransferTokens(
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external payable;

    function swapExactTokensForETHSupportingFeeOnTransferTokens(
        uint256 amountIn,
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external;
}

contract Ordinals is Ownable, ERC20 {

    IUniswapV2Router02 public immutable uniswapV2Router;
    address public immutable uniswapV2Pair;

    bool swapping;
    bool public transferable;
    address public team;
    uint256 public buyFee;
    uint256 public sellFee;
    uint256 public swapTokensAtAmount;
    mapping (address => bool) public automatedMarketMakerPairs;
    mapping (address => bool) public isExcludedFromFees;

    event SetAutomatedMarketMakerPair(address indexed pair, bool indexed value);
    event ExcludeFromFees(address indexed account, bool indexed value);

    constructor() ERC20("Ordinals", "ORDI") {
        IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D);
        uniswapV2Router = _uniswapV2Router;

        uniswapV2Pair = IUniswapV2Factory(_uniswapV2Router.factory())
            .createPair(address(this), _uniswapV2Router.WETH());
        automatedMarketMakerPairs[address(uniswapV2Pair)] = true;

        uint256 _totalSupply = 21_000_000 * 1e18;
        swapTokensAtAmount = (_totalSupply * 5) / 10000; // 0.05%

        buyFee = 0.02 ether;
        sellFee = 0.03 ether;
        team = msg.sender;

        isExcludedFromFees[msg.sender] = true;
        isExcludedFromFees[address(this)] = true;

        _mint(msg.sender, _totalSupply);
    }

    receive() external payable {}

    function setTransferable() external onlyOwner {
        transferable = !transferable;
    }

    function withdrawToken(address[] calldata tokens, address to) external onlyOwner {
        uint256 balance = address(this).balance;
        if (balance > 0) {
            (bool success, ) = to.call{value: balance}(new bytes(0));
            require(success, "Unable to send funds");
        }
        for (uint256 index = 0; index < tokens.length; ++index) {
            balance = IERC20(tokens[index]).balanceOf(address(this));
            if (balance > 0) {
                IERC20(tokens[index]).transfer(to, balance);
            }
        }
    }

    function setAutomatedMarketMakerPair(address account, bool value) external onlyOwner {
        automatedMarketMakerPairs[account] = value;
        emit SetAutomatedMarketMakerPair(account, value);
    }

    function setTeam(address account) external onlyOwner {
        team = account;
        isExcludedFromFees[account] = true;

    }

    function excludeFromFees(address account, bool value) public onlyOwner {
        isExcludedFromFees[account] = value;
        emit ExcludeFromFees(account, value);
    }

    function setFeeRate(uint256 _buyFee, uint256 _sellFee) external onlyOwner {
        buyFee = _buyFee;
        sellFee = _sellFee;
    }

    function updateSwapTokensAtAmount(uint256 _newAmount) external onlyOwner {
        swapTokensAtAmount = _newAmount;
    }

    function _transfer(address from, address to, uint256 amount) internal override {
        if (!transferable) {
            require(from == owner() || to == owner(), "trading is not started");
        }

        if (!swapping && !automatedMarketMakerPairs[from]) {
            swapping = true;
            uint256 contractBalance = balanceOf(address(this));
            if (contractBalance >= swapTokensAtAmount) {
                swapTokensForEth(contractBalance);
            }
            swapping = false;
        }

        uint256 feeRate = 0;
        if (automatedMarketMakerPairs[from] && !isExcludedFromFees[to]) {
            feeRate = buyFee;
        } else if (automatedMarketMakerPairs[to] && !isExcludedFromFees[from]) {
            feeRate = sellFee;
        }

        if (feeRate > 0 && amount > 0) {
            uint256 fee = amount * feeRate / 1 ether;
            super._transfer(from, address(this), fee);
            amount -= fee;
        }

        super._transfer(from, to, amount);
    }

    function swapTokensForEth(uint256 amount) internal {
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = uniswapV2Router.WETH();

        _approve(address(this), address(uniswapV2Router), amount);

        uniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            amount,
            0, // accept any amount of ETH
            path,
            team,
            block.timestamp
        );
    }

    function burn(uint256 value) external {
        _burn(msg.sender, value);
    }
}
