pragma solidity 0.8.17;

contract BullionFXGold is ERC20 {
    using SafeMath for uint256;

    /*//////////////////////////////////////////////////////////////
                           STATE VARIABLES
    //////////////////////////////////////////////////////////////*/

    // Maximum transfer fee percentage that can be set.
    uint256 public constant MAX_TRANSFER_FEE_PERCENT = 1000; // 10%

    // Represents 100% for percentage calculations.
    uint256 public constant FULL_PERCENTAGE = 10000; // 100%

    // Indicates whether the gold token contract is configured or not.
    bool public isConfigured;

    // Current transfer fee percentage.
    uint80 public transferFee;

    // Address that receives the transfer fees.
    address public feeRecipient;

    // Contract administrator's address.
    address public admin;

    // Tracks whether an address is exempt from transfer fee.
    mapping(address => bool) public isTransferFeeExempt;

    /*//////////////////////////////////////////////////////////////
                                EVENTS
    //////////////////////////////////////////////////////////////*/

    event AdminChanged(address oldAdmin, address newAdmin);
    event FeeRecipientChanged(address oldFeeRecipient, address newFeeRecipient);
    event TransferFeeChanged(uint256 oldTransferFee, uint256 newTransferFee);
    event TransferFeeExemptionModified(address account, bool isExempt);

    /*//////////////////////////////////////////////////////////////
                                 ERRORS
    //////////////////////////////////////////////////////////////*/

    error AccessDenied();
    error AccessStatusUnchanged();
    error AlreadyConfigured();
    error FeeExceedsMaxFeePercentage();
    error InvalidTransferFee();
    error NoChangesDetected();
    error ZeroAddress();

    /*//////////////////////////////////////////////////////////////
                                MODIFIER
    //////////////////////////////////////////////////////////////*/

    /// @dev Only allows the contract owner or administrator to call a function.
    modifier onlyOwnerOrAdmin() {
        if (msg.sender != admin && msg.sender != owner()) {
            revert AccessDenied();
        }
        _;
    }

    /*//////////////////////////////////////////////////////////////
                            CONFIG FUNCTION
    //////////////////////////////////////////////////////////////*/

    /// @notice Configure the contract with initial settings.
    /// @dev This function is meant to be called only once to initialize the contract.
    /// @param _transferFee The initial transfer fee percentage.
    /// @param _feeRecipient The address to receive transfer fees.
    /// @param _admin The administrator's address with privileged access.
    /// @param _addr1ToExempt An address to exempt from transfer fees.
    /// @param _addr2ToExempt Another address to exempt from transfer fees.
    function config(
        uint80 _transferFee,
        address _feeRecipient,
        address _admin,
        address _addr1ToExempt,
        address _addr2ToExempt,
        string calldata name_
    ) external {
        if (isConfigured) {
            revert AlreadyConfigured();
        }
        if (
            _admin == address(0) ||
            _feeRecipient == address(0) ||
            _addr1ToExempt == address(0) ||
            _addr2ToExempt == address(0)
        ) {
            revert ZeroAddress();
        }
        if (_transferFee == 0 || _transferFee > MAX_TRANSFER_FEE_PERCENT) {
            revert InvalidTransferFee();
        }

        isConfigured = true;
        transferFee = _transferFee;
        feeRecipient = _feeRecipient;
        admin = _admin;
        isTransferFeeExempt[_addr1ToExempt] = true;
        isTransferFeeExempt[_addr2ToExempt] = true;
        _name = name_;

        // Emit configuration change events.
        emit AdminChanged(address(0), _admin);
        emit FeeRecipientChanged(address(0), _feeRecipient);
        emit TransferFeeChanged(0, _transferFee);
        emit TransferFeeExemptionModified(_addr1ToExempt, true);
        emit TransferFeeExemptionModified(_addr2ToExempt, true);
    }

    /*//////////////////////////////////////////////////////////////
                      OWNER RESTRICTED FUNCTIONS
    //////////////////////////////////////////////////////////////*/

    /// @notice Burn a specified amount of tokens owned by the contract owner. Only callable by the owner.
    /// @param _amount The amount of tokens to burn.
    function burn(uint256 _amount) public onlyOwner returns (bool) {
        _burn(msg.sender, _amount);
        return true;
    }

    /// @notice Change the contract administrator address. Only callable by the owner.
    /// @param _newAdminAddress New administrator's address (or zero address to revoke admin privileges).
    function changeAdmin(address _newAdminAddress) external onlyOwner {
        address _oldAdminAddress = admin; // Cache the current admin for the event
        if (_oldAdminAddress == _newAdminAddress) {
            revert NoChangesDetected();
        }
        emit AdminChanged(_oldAdminAddress, _newAdminAddress);
        admin = _newAdminAddress;
    }

    /*//////////////////////////////////////////////////////////////
                     OWNER OR ADMIN RESTRICTED FUNCTIONS
    //////////////////////////////////////////////////////////////*/

    /// @notice Exclude or include an address from transfer fee calculations. Only callable by the owner or admin.
    /// @param _targetAddress The address to modify fee exclusion for.
    /// @param _exclude Whether to exclude (true) or include (false) the address from transfer fees.
    function toggleTransferFeeExclusion(
        address _targetAddress,
        bool _exclude
    ) external onlyOwnerOrAdmin {
        if (_targetAddress == address(0)) {
            revert ZeroAddress();
        }
        if (isTransferFeeExempt[_targetAddress] == _exclude) {
            revert AccessStatusUnchanged();
        }
        emit TransferFeeExemptionModified(_targetAddress, _exclude);
        isTransferFeeExempt[_targetAddress] = _exclude;
    }

    /// @notice Change the transfer fee percentage. Only callable by the owner or admin.
    /// @param _newTransferFee The new transfer fee percentage to set.
    /// @dev A transferFee value of zero implies no transfer fee will be deducted.
    function changeTransferFee(
        uint80 _newTransferFee
    ) external onlyOwnerOrAdmin {
        if (_newTransferFee > MAX_TRANSFER_FEE_PERCENT) {
            revert FeeExceedsMaxFeePercentage();
        }

        uint256 _oldTransferFee = transferFee; // Cache the current fee for the event
        if (_oldTransferFee == _newTransferFee) {
            revert NoChangesDetected();
        }

        emit TransferFeeChanged(_oldTransferFee, _newTransferFee);
        transferFee = _newTransferFee;
    }

    /// @notice Change the fee recipient address. Only callable by the owner or admin.
    /// @param _newFeeRecipient The address of the new fee recipient.
    function changeFeeRecipient(
        address _newFeeRecipient
    ) external onlyOwnerOrAdmin {
        if (_newFeeRecipient == address(0)) {
            revert ZeroAddress();
        }
        address oldFeeRecipient = feeRecipient; // Cache the current recipient for the event
        if (oldFeeRecipient == _newFeeRecipient) {
            revert NoChangesDetected();
        }
        emit FeeRecipientChanged(oldFeeRecipient, _newFeeRecipient);
        feeRecipient = _newFeeRecipient;
    }

    /*//////////////////////////////////////////////////////////////
                            ERC20 FUNCTIONS
    //////////////////////////////////////////////////////////////*/

    /// @notice Transfer tokens to a recipient, deducting a transfer fee if applicable.
    /// @param _recipient The address of the recipient.
    /// @param _amount The amount of tokens to transfer.
    function transfer(
        address _recipient,
        uint256 _amount
    ) public override returns (bool) {
        uint256 fee;

        // Check if a transfer fee is applicable and if neither the caller nor the recipient is exempt from fees.
        if (
            transferFee > 0 &&
            !isTransferFeeExempt[msg.sender] &&
            !isTransferFeeExempt[_recipient]
        ) {
            fee = getTransferFee(_amount);

            // Transfer the fee to the fee recipient.
            ERC20.transfer(feeRecipient, fee);
        }

        // Transfer the remaining tokens (or full if no fee applicable) to the recipient.
        ERC20.transfer(_recipient, _amount - fee);
        return true;
    }

    /// @notice Transfer tokens from one address to another, deducting a transfer fee if applicable.
    /// @param _sender The address from which tokens are being transferred.
    /// @param _recipient The address to which tokens are being transferred.
    /// @param _amount The amount of tokens to transfer.
    function transferFrom(
        address _sender,
        address _recipient,
        uint256 _amount
    ) public override returns (bool) {
        uint256 fee;

        // Check if a transfer fee is applicable and if neither the sender nor the recipient is exempt from fees.
        if (
            transferFee > 0 &&
            !isTransferFeeExempt[_sender] &&
            !isTransferFeeExempt[_recipient]
        ) {
            fee = getTransferFee(_amount);

            // Transfer the fee to the fee recipient.
            ERC20.transferFrom(_sender, feeRecipient, fee);
        }

        // Transfer the remaining tokens (or full amount if no fee is applicable) to the recipient.
        ERC20.transferFrom(_sender, _recipient, _amount - fee);
        return true;
    }

    /*//////////////////////////////////////////////////////////////
                            HELPER FUNCTION
    //////////////////////////////////////////////////////////////*/
    function getTransferFee(uint256 _amount) public view returns (uint256) {
        return (_amount * transferFee) / FULL_PERCENTAGE;
    }
}
