    "contracts/Mashabas.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

/**
 * Mashabas | 2023
 * Author: Josh Stow (jstow.com)
 */

import "erc721a/contracts/ERC721A.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Address.sol";
import "@openzeppelin/contracts/utils/cryptography/MerkleProof.sol";

contract Mashabas is ERC721A, Ownable {
  using Address for address payable;

  uint256 public constant MASH_PREMINT = 450;
  uint256 public constant MASH_PRIVATE = 2000;
  uint256 public constant MASH_PUBLIC = 7550;
  uint256 public constant MASH_MAX = MASH_PREMINT + MASH_PRIVATE + MASH_PUBLIC;  // 10,000
  uint256 public constant MASH_PER_WALLET = 20;
  uint256 public constant MASH_PRICE = 0.08 ether;

  mapping(address => uint256) public NOKO_addressToMinted;
  mapping(address => uint256) public DON_addressToMinted;
  uint256 public NOKO_privateMinted;
  uint256 public DON_privateMinted;

  string private _baseTokenURI;
  string private _contractURI;

  bool public NOKO_saleLive;
  bool public NOKO_presaleLive;
  bool public DON_saleLive;
  bool public DON_presaleLive;

  bool public DON_preminted;

  bytes32 public root;

  bool public locked;

  constructor(
    string memory newBaseTokenURI,
    string memory newContractURI,
    bytes32 _root
  )
    ERC721A("Mashabas", "MASH")
  {
    _baseTokenURI = newBaseTokenURI;
    _contractURI = newContractURI;
    root = _root;

    _mint(owner(), MASH_PREMINT);
  }

  modifier whenNotLocked {
    require(!locked, "Contract metadata is locked");
    _;
  }

  /**
   * @dev Mints number of tokens specified to wallet.
   * @param quantity uint256 Number of tokens to be minted
   */
  function buyNOKO(uint256 quantity) external payable {
    require(NOKO_saleLive, "Sale is not currently live");
    require(totalSupply() + quantity <= MASH_MAX, "Quantity exceeds remaining tokens");
    require(quantity <= MASH_PER_WALLET - NOKO_addressToMinted[msg.sender], "Wallet cannot mint any new tokens");
    require(msg.value >= quantity * MASH_PRICE, "Insufficient funds");

    NOKO_addressToMinted[msg.sender] += quantity;
    _mint(msg.sender, quantity);
  }

  /**
   * @dev Mints number of tokens specified to wallet during presale.
   * @param quantity uint256 Number of tokens to be minted
   */
  function presaleBuyNOKO(uint256 quantity, bytes32[] calldata proof) external payable {
    require(NOKO_presaleLive && !NOKO_saleLive, "Presale not currently live");

    bytes32 leaf = keccak256(abi.encodePacked(msg.sender));
    require(MerkleProof.verify(proof, root, leaf), "Caller is not eligible for presale");

    require(totalSupply() + quantity <= MASH_MAX &&
      NOKO_privateMinted + quantity <= MASH_PRIVATE, "Quantity exceeds remaining tokens");
    require(quantity <= MASH_PER_WALLET - NOKO_addressToMinted[msg.sender], "Wallet cannot mint any new tokens");
    require(msg.value >= quantity * MASH_PRICE, "Insufficient funds");

    NOKO_addressToMinted[msg.sender] += quantity;
    NOKO_privateMinted += quantity;
    _mint(msg.sender, quantity);
  }

  /**
   * @dev Mints number of tokens specified to wallet.
   * @param quantity uint256 Number of tokens to be minted
   */
  function buyDON(uint256 quantity) external payable {
    require(DON_saleLive, "Sale is not currently live");
    require(totalSupply() + quantity <= MASH_MAX, "Quantity exceeds remaining tokens");
    require(quantity <= MASH_PER_WALLET - DON_addressToMinted[msg.sender], "Wallet cannot mint any new tokens");
    require(msg.value >= quantity * MASH_PRICE, "Insufficient funds");

    DON_addressToMinted[msg.sender] += quantity;
    _mint(msg.sender, quantity);
  }

  /**
   * @dev Mints number of tokens specified to wallet during presale.
   * @param quantity uint256 Number of tokens to be minted
   */
  function presaleBuyDON(uint256 quantity, bytes32[] calldata proof) external payable {
    require(DON_presaleLive && !DON_saleLive, "Presale not currently live");
    require(totalSupply() >= MASH_MAX, "Secondary sale is not currently live");

    bytes32 leaf = keccak256(abi.encodePacked(msg.sender));
    require(MerkleProof.verify(proof, root, leaf), "Caller is not eligible for presale");

    require((totalSupply() - MASH_MAX) + quantity <= MASH_MAX &&
      DON_privateMinted + quantity <= MASH_PRIVATE, "Quantity exceeds remaining tokens");
    require(quantity <= MASH_PER_WALLET - DON_addressToMinted[msg.sender], "Wallet cannot mint any new tokens");
    require(msg.value >= quantity * MASH_PRICE, "Insufficient funds");

    DON_addressToMinted[msg.sender] += quantity;
    DON_privateMinted += quantity;
    _mint(msg.sender, quantity);
  }

  /**
   * @dev Premint tokens for secondary collection.
   */
  function premintDON() external onlyOwner {
    require(!DON_preminted, "Secondary collection has already been preminted");
    require(totalSupply() >= MASH_MAX, "Primary collection is not yet fully minted");

    _mint(owner(), MASH_PREMINT);
