    "src/interfaces/IDNft.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity =0.8.17;

interface IDNft {
  event MintedNft       (uint indexed id, address indexed to);
  event MintedInsiderNft(uint indexed id, address indexed to);
  event Drained         (address indexed to, uint amount);

  error InsiderMintsExceeded ();
  error InsufficientFunds    ();

  /**
   * @dev Mints an dNFT and transfers it to the given `to` address.
   * 
   * Requirements:
   * - msg.value exceeds the minting price
   *
   * Emits a {MintedNft} event on successful execution.
   *
   * @param to The address to which the minted NFT will be transferred.
   * @return id The ID of the minted NFT.
   *
   * Throws a {InsufficientFunds} error if the sender does not provide enough ETH to mint the NFT.
   */
  function mintNft(address to) external payable returns (uint id);

  /**
   * @notice Mint new insider DNft to `to` 
   * @dev Note:
   *      - An insider dNFT does not require buring ETH to mint
   * @dev Will revert:
   *      - If not called by contract owner
   *      - If the maximum number of insider mints has been reached
   *      - If `to` is the zero address
   * @dev Emits:
   *      - MintNft(address indexed to, uint indexed id)
   * @param to The address to mint the dNFT to
   * @return id Id of the new dNFT
   * 
   * Throws a {InsiderMintsExceeded} error if the maximum number of insider mints has been reached.
   */
  function mintInsiderNft(address to) external returns (uint id);

  /**
   * @notice Drain the contract balance to `to`
   * @dev Will revert:
   *      - If not called by contract owner
   * @dev Emits:
   *      - Drained(address indexed to, uint amount)
   * @param to The address to drain the contract balance to
   */
  function drain(address to) external;
}
"
    },
    "src/interfaces/IDyad.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity =0.8.17;

interface IDyad {

  error NotLicensed();
  error DNftDoesNotExist();

 /**
  * @notice Mints amount of DYAD through a dNFT and licensed vault manager 
  *         to a specified address.
  * @dev The caller must be a licensed vault manager. Vault manager get
  *      licensed by the 'sll'.
  * @param id ID of the dNFT.
  * @param to The address of the recipient who will receive the tokens.
  * @param amount The amount of tokens to be minted.
  */
  function mint(
      uint    id, 
      address to,
      uint    amount
  ) external;

 /**
  * @notice Burns amount of DYAD through a dNFT and licensed vault manager
  *         from a specified address.
  * @dev The caller must be a licensed vault manager. Vault manager get
  *      licensed by the 'sll'.
  * @param id ID of the dNFT.
  * @param from The address of the recipient who the tokens will be burnt
  *        from.
  * @param amount The amount of tokens to be burned.
  */
  function burn(
      uint    id, 
      address from,
      uint    amount
  ) external;
}
"
    },
    "src/interfaces/IVault.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity = 0.8.17;

interface IVault {
  event Withdraw (uint indexed from, address indexed to, uint amount);
  event Deposit  (uint indexed id, uint amount);
  event Move     (uint indexed from, uint indexed to, uint amount);

  error StaleData            ();
  error IncompleteRound      ();
  error NotVaultManager      ();
}
"
    },
    "src/interfaces/IVaultManager.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity = 0.8.17;

interface IVaultManager {
  event Added     (uint indexed id, address indexed vault);
  event Removed   (uint indexed id, address indexed vault);
  event MintDyad  (uint indexed id, uint amount, address indexed to);
  event BurnDyad  (uint indexed id, uint amount, address indexed from);
  event RedeemDyad(uint indexed id, address indexed vault, uint amount, address indexed to);
  event Liquidate (uint indexed id, address indexed from, uint indexed to);

  error NotOwner();
  error NotLicensed();
  error OnlyOwner();
  error VaultNotLicensed();
  error TooManyVaults();
  error VaultAlreadyAdded();
  error VaultNotAdded();
  error VaultHasAssets();
  error NotDNftVault();
  error InvalidDNft();
  error CrTooLow();
  error CrTooHigh();
}
"
