    "src/FxERC4626RootTunnel.sol": {
      "content": "// SPDX-License-Identifier: AGPL-3.0-or-later
pragma solidity ^0.8.15;

import {FxBaseRootTunnel} from "fx-portal/tunnel/FxBaseRootTunnel.sol";
import {ERC4626} from "solmate/mixins/ERC4626.sol";
import {ERC20} from "solmate/tokens/ERC20.sol";
import {ReentrancyGuard} from "solmate/utils/ReentrancyGuard.sol";

import {IFxERC4626Tunnel} from "./interfaces/IFxERC4626Tunnel.sol";
import {IFxERC20RootTunnel} from "./interfaces/polygon/IFxERC20RootTunnel.sol";
import {IRootChainManager} from "./interfaces/polygon/IRootChainManager.sol";

import {RLPReader} from "./utils/RLPReader.sol";
import {ExitPayloadReader} from "./utils/ExitPayloadReader.sol";
import {MerklePatriciaProof} from "./utils/MerklePatriciaProof.sol";

/**
 * @title  FxERC4626RootTunnel
 * @notice This is the main bridge contract on the root chain. It contains functions
 *         to withdraw a user’s stake and bridge them across to the child chain via
 *         the deposit mechanisms of its `ERC20TransferRootTunnel` contract.
 */
contract FxERC4626RootTunnel is IFxERC4626Tunnel, FxBaseRootTunnel, ReentrancyGuard {
    using ExitPayloadReader for bytes;
    using ExitPayloadReader for ExitPayloadReader.ExitPayload;

    address immutable admin;
    ERC20 public immutable rootToken;
    address public immutable tokenBridge;
    uint128 public immutable chainId;

    uint128 public initiatedTransactionId;
    ERC4626 public vault;

    mapping(address => bool) public adminApproval;

    constructor(
        address checkpointManager_,
        address fxRoot_,
        address tokenBridge_,
        address rootToken_,
        address vault_,
        address admin_,
        uint128 chainId_
    ) FxBaseRootTunnel(checkpointManager_, fxRoot_) {
        tokenBridge = tokenBridge_;
        rootToken = ERC20(rootToken_);
        vault = ERC4626(vault_);
        admin = admin_;
        chainId = chainId_;

        address childToken_;
        if (chainId == 1) {
            childToken_ = IRootChainManager(tokenBridge).rootToChildToken(rootToken_);
        } else {
            childToken_ = IFxERC20RootTunnel(tokenBridge).rootToChildTokens(rootToken_);
        }
        require(childToken_ != address(0), "FxERC20TransferRootTunnel:TOKEN_NOT_MAPPED");

        // Max approve assets for value deposit and bridge.
        rootToken.approve(vault_, type(uint256).max);
        rootToken.approve(tokenBridge_, type(uint256).max);
    }

    /**
     * inheritdoc IFxERC4626Tunnel
     */
    function setAdminApproval(bool approvalStatus_) external override {
        adminApproval[msg.sender] = approvalStatus_;
    }

    /**
     * inheritdoc IFxERC4626RootTunnel
     */
    function emergencyWithdraw(address account_, uint256, bytes calldata messageData_) external {
        require(msg.sender == admin, "FxERC4626RootTunnel:UNAPPROVED_ACCESS");
        require(adminApproval[account_], "FxERC4626RootTunnel:NO_ADMIN_APPROVAL");
        delete adminApproval[account_];

        bytes memory message_ = _validateAndExtractMessage(messageData_);
        (address recipient_, uint256 assets_, uint256 transactionId_) =
            abi.decode(message_, (address, uint256, uint256));
        require(account_ == recipient_, "FxERC4626RootTunnel:RECIPIENT_MISMATCH");

        if (assets_ > 0) {
            require(rootToken.balanceOf(address(this)) >= assets_, "FxERC4626RootTunnel:TOKENS_YET_TO_ARRIVE");
            rootToken.transfer(admin, assets_);
            emit EmergencyWithdraw(account_, assets_, transactionId_);
        }
    }

    /**
     * inheritdoc IFxERC4626Tunnel
     */
    function bridgeStake(uint256 shares_) external nonReentrant {
        // Firstly withdraw the underlying assets from the staking contract. This contract (StakeBridge) will be able
        // to withdraw to itself without incurring an instant withdrawal fee. This is important so that the user
        // performing the bridge receives an identical amount of underlying assets.
        uint256 assets_ = vault.redeem(shares_, address(this), msg.sender);

        // Then deposit the assets to the bridge that contains the mapped GET token.
        if (chainId == 1) {
            IRootChainManager(tokenBridge).depositFor(fxChildTunnel, address(rootToken), abi.encode(assets_));
        } else {
            IFxERC20RootTunnel(tokenBridge).deposit(address(rootToken), address(this), assets_, abi.encode(assets_));
        }

        // And finally submit this information as a message through the FxPortal to the child contract.
        uint256 transactionId_ = _pack(chainId, initiatedTransactionId);
        _sendMessageToChild(abi.encode(msg.sender, assets_, transactionId_));

        initiatedTransactionId += 1;

        emit BridgeInitiation(msg.sender, transactionId_, shares_, assets_);
    }

    function receiveStake(uint256 transactionId_, bytes calldata exitData_, bytes calldata messageData_) external nonReentrant {
        // We must first check to see if the exit has already been processed prior to attempting to run the exit
        // function. We cannot wrap this in a try call as it would mean that we could process the Fx message without
        // handling the exit in tandem. This protective check makes it impossible for the Fx message to be processed
        // after a successful run of this function while leaving the exit unprocessed.
        //
        // In other words this check ensures that the GET from this exit must already be on the contract in the case of
        // a publicly triggered exit, or will be exited as part of this transaction.
        //
        // See: https://github.com/maticnetwork/pos-portal/blob/master/contracts/root/RootChainManager/RootChainManager.sol#L343-L357
        ExitPayloadReader.ExitPayload memory payload = exitData_.toExitPayload();
        bytes memory branchMaskBytes = payload.getBranchMaskAsBytes();
        bytes32 exitHash = keccak256(
            abi.encodePacked(
                payload.getBlockNumber(),
                MerklePatriciaProof._getNibbleArray(branchMaskBytes),
                payload.getReceiptLogIndex()
            )
        );

        if (chainId == 1 && IRootChainManager(tokenBridge).processedExits(exitHash) == false) {
            IRootChainManager(tokenBridge).exit(exitData_);
        } else if (chainId == 5 && IFxERC20RootTunnel(tokenBridge).processedExits(exitHash) == false) {
            IFxERC20RootTunnel(tokenBridge).receiveMessage(exitData_);
        }

        // At this point we should expect to have confirmed that the specific token bridge from the user has been
        // completed and those tokens are now available on this contract address.
        //
        // We can guarantee that this is the first processing of the Fx message as subsequent attempts will trigger an
        // error. This is important as it means we can safely attempt to process the message when available.
        bytes memory message_ = _validateAndExtractMessage(messageData_);
        (address recipient_, uint256 assets_, uint256 recoveredTransactionId_) =
            abi.decode(message_, (address, uint256, uint256));
        require(assets_ > 0, "FxERC4626RootTunnel:NOT_ENOUGH_ASSETS");
        require(transactionId_ == recoveredTransactionId_, "FxERC4626RootTunnel:ID_MISMATCH");
        require(rootToken.balanceOf(address(this)) >= assets_, "FxERC4626RootTunnel:TOKENS_YET_TO_ARRIVE");

        uint256 shares_ = vault.deposit(assets_, recipient_);
        emit BridgeCompletion(recipient_, transactionId_, shares_, assets_);
    }

    function receiveMessage(bytes memory inputData) public virtual override {}

    /**
     * @inheritdoc FxBaseRootTunnel
     */
    function _processMessageFromChild(bytes memory data_) internal override {}

    function _pack(uint128 a, uint128 b) internal pure returns (uint256) {
        return (uint256(a) << 128) | uint256(b);
    }
}
"
