    "src/interfaces/IFxERC4626Tunnel.sol": {
      "content": "// SPDX-License-Identifier: AGPL-3.0-or-later
pragma solidity ^0.8.15;

interface IFxERC4626Tunnel {
    /**
     * @notice Emitted during a bridging transaction
     * @param account Address of account shares are staked for
     * @param transactionId User specific transaction id on child chain
     * @param shares Amount of shares for underlying asset
     * @param assets Amount of assets staked for shares
     */
    event BridgeInitiation(address account, uint256 transactionId, uint256 shares, uint256 assets);

    /**
     * @notice Emitted after a stake operation on root chain
     * @param account Address of account shares are staked for
     * @param transactionId User specific transaction id on child chain
     * @param shares Amount of shares for underlying asset
     * @param assets Amount of assets staked for shares
     */
    event BridgeCompletion(address account, uint256 transactionId, uint256 shares, uint256 assets);

    /**
     * @notice Emitted during an emergency withdrawal on behalf of an account by the ADMIN address
     * @param account Address withdrawal is made on behalf of
     * @param amount Amount of tokens being withdrawn
     * @param transactionId Id of the bridging transaction that initiated token movement
     */
    event EmergencyWithdraw(address account, uint256 amount, uint256 transactionId);

    /**
     * @notice Allows admin account to withdraw stuck assets on behalf of users
     * @param approvalStatus_ Boolean status of admin approval
     */
    function setAdminApproval(bool approvalStatus_) external;

    /**
     * @notice Bridges shares from source to destination chain
     * @dev User must have given appropriate approval to tunnel contract prior to call
     * @param shares_ Amount of shares to be bridged
     */
    function bridgeStake(uint256 shares_) external;

    /**
     * @notice Receives and (re)stakes the assets on the destination chain
     * @param transactionId_ the transactionId_ being processed, generated by the source bridge contract
     * @param exitData_ proof only used on Ethereum, to exit the assets from the network to the stake bridge
     * @param messageData_ proof of the instruction to re-stake the assets
     */
    function receiveStake(uint256 transactionId_, bytes calldata exitData_, bytes calldata messageData_) external;

    /**
     * @notice Withdrawal via ADMIN account for authorized addresses
     * @dev Only used in an edge-case scenerio when a user's stake has been bridged from the root chain
     *      and is unable to exit on the child chain. In a scenerio like this, the user's assets have
     *      been bridged to the ChildTunnel contract but can't be exited. The user will have to grant
     *      the admin approval rights via the `setAdminApproval` function to withdraw the tokens and
     *      manually stake them on behalf of the user. This operation is performed by the team.
     * @dev We never expect to use this function. It's put here as a safety net.
     * @param account_ Address on behalf of which token withdrawal is made
     * @param transactionId_ Bridging id from child chain
     * @param messageData_ Signed message data of withdrawal on root chain
     */
    function emergencyWithdraw(address account_, uint256 transactionId_, bytes calldata messageData_) external;
}
"
