    "contracts/nft/CoCreateERC1155.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity =0.8.17;

import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/common/ERC2981Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC1155/ERC1155Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC1155/extensions/ERC1155SupplyUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/cryptography/draft-EIP712Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/AddressUpgradeable.sol";

contract CoCreateERC1155 is
  OwnableUpgradeable,
  ERC2981Upgradeable,
  EIP712Upgradeable,
  ERC1155Upgradeable,
  ERC1155SupplyUpgradeable
{
  event AddressAddedToAllowList(address addr);
  event AddressRemovedFromAllowList(address addr);
  event TransferAllowListUpdated(bool isTransferAllowlisted);
  event URIUpdated(string URI);
  event RoyaltyUpdated(address recipient, uint96 feeNumerator);
  event BatchMintMetadata(bytes mintMetadata);

  event TokenPurchase(address indexed buyer, uint256 tokenId, uint256 price, address currency);

  // mapping from token id to native token price
  mapping(uint256 => uint256) private nativeTokenPrice;
  // mapping from token id to erc20 token price
  mapping(uint256 => uint256) private erc20TokenPrice;
  address private acceptedERC20Token;
  bool private isNFTForSale;

  // solhint-disable-next-line var-name-mixedcase
  bytes32 private constant _PERMIT_TYPEHASH = keccak256("Permit(address spender,uint256 deadline)");

  bool private isTransferAllowlistRestricted;
  mapping(address => bool) public transferAllowList;

  string private _name;
  string private _symbol;

  function initialize(
    string memory name_,
    string memory symbol_,
    string memory uri_,
    address royaltyRecipient_,
    uint96 royaltyFeeNumerator_,
    address owner_,
    bool _isTransferAllowlistRestricted,
    address[] memory initialAllowList
  ) public initializer {
    _name = name_;
    _symbol = symbol_;
    __EIP712_init_unchained(name_, "1");
    __Ownable_init();
    __ERC1155_init(uri_);
    __ERC1155Supply_init();
    _setDefaultRoyalty(royaltyRecipient_, royaltyFeeNumerator_);
    if (_isTransferAllowlistRestricted) {
      updateAllowlist(initialAllowList, new address[](0), _isTransferAllowlistRestricted);
    }
    _transferOwnership(owner_);
  }

  function name() public view virtual returns (string memory) {
    return _name;
  }

  function symbol() public view virtual returns (string memory) {
    return _symbol;
  }

  function uri(uint256 tokenId) public view virtual override returns (string memory) {
    string memory baseURI = super.uri(tokenId);
    return bytes(baseURI).length != 0 ? string(abi.encodePacked(baseURI, _toString(tokenId))) : "";
  }

  function permit(
    address spender,
    uint256 deadline,
    bytes memory sig
  ) public {
    require(block.timestamp <= deadline, "CoCreateERC1155Permit: expired deadline");
    require(spender != address(0), "CoCreateERC1155Permit: invalid spender");
    bytes32 structHash = keccak256(abi.encode(_PERMIT_TYPEHASH, spender, deadline));
    bytes32 hash = _hashTypedDataV4(structHash);
    address signer = ECDSAUpgradeable.recover(hash, sig);
    _setApprovalForAll(signer, spender, true);
  }

  function permitTransfer(
    address from,
    address to,
    uint256[] memory ids,
    uint256[] memory amounts,
    address permit_spender,
    uint256 permit_deadline,
    bytes memory permit_sig
  ) public onlyOwner {
    permit(permit_spender, permit_deadline, permit_sig);
    _safeBatchTransferFrom(from, to, ids, amounts, "");
  }

  function permitBurn(
    address from,
    uint256[] memory ids,
    uint256[] memory amounts,
    address permit_spender,
    uint256 permit_deadline,
    bytes memory permit_sig
  ) public onlyOwner {
    permit(permit_spender, permit_deadline, permit_sig);
    _burnBatch(from, ids, amounts);
  }

  // Set the default royalty fee and recipient.
  function setDefaultRoyalty(address recipient, uint96 feeNumerator) external onlyOwner {
    _setDefaultRoyalty(recipient, feeNumerator);
    emit RoyaltyUpdated(recipient, feeNumerator);
  }

  function _mintBatch(
    address[] calldata recipients,
    uint256[] calldata tokenIds,
    uint256[] calldata amounts
  ) internal virtual {
    require(recipients.length == amounts.length, "recipient and amount length mismatch");
    require(recipients.length == tokenIds.length, "recipient and amount length mismatch");
    for (uint256 i = 0; i < recipients.length; ++i) {
      _mint(recipients[i], tokenIds[i], amounts[i], "");
    }
  }

  function mintBatch(
    address[] calldata recipients,
    uint256[] calldata tokenIds,
    uint256[] calldata amounts
  ) public onlyOwner {
    _mintBatch(recipients, tokenIds, amounts);
  }

  function mintBatchWithMetadata(
    address[] calldata recipients,
    uint256[] calldata tokenIds,
    uint256[] calldata amounts,
    bytes memory mintMetadata
  ) public onlyOwner {
    _mintBatch(recipients, tokenIds, amounts);
    emit BatchMintMetadata(mintMetadata);
  }

  function updateAllowlist(
    address[] memory addAddresses,
    address[] memory removeAddresses,
    bool _isTransferAllowlistRestricted
  ) public onlyOwner {
    if (_isTransferAllowlistRestricted != isTransferAllowlistRestricted) {
      isTransferAllowlistRestricted = _isTransferAllowlistRestricted;
      emit TransferAllowListUpdated(_isTransferAllowlistRestricted);
    }
    for (uint256 i = 0; i < addAddresses.length; ++i) {
      require(addAddresses[i] != address(0), "invalid address");
      transferAllowList[addAddresses[i]] = true;
      emit AddressAddedToAllowList(addAddresses[i]);
    }
    for (uint256 i = 0; i < removeAddresses.length; ++i) {
      if (transferAllowList[removeAddresses[i]]) {
        transferAllowList[removeAddresses[i]] = false;
        emit AddressRemovedFromAllowList(removeAddresses[i]);
      }
    }
  }

  // support ERC1155 and ERC2981 interfaces
  function supportsInterface(bytes4 interfaceId)
    public
    view
    virtual
    override(ERC2981Upgradeable, ERC1155Upgradeable)
    returns (bool)
  {
    return ERC2981Upgradeable.supportsInterface(interfaceId) || ERC1155Upgradeable.supportsInterface(interfaceId);
  }

  // Set the base URI for all tokens.
  function setURI(string memory newUri) external onlyOwner {
    _setURI(newUri);
    emit URIUpdated(newUri);
  }

  function _beforeTokenTransfer(
    address operator,
    address from,
    address to,
    uint256[] memory ids,
    uint256[] memory amounts,
    bytes memory data
  ) internal virtual override(ERC1155Upgradeable, ERC1155SupplyUpgradeable) {
    validateTokenOperation(from, to);
    super._beforeTokenTransfer(operator, from, to, ids, amounts, data);
  }

  // A token transfer is valid under the following conditions.
  // This allows the owner to restrict transfers to a specific set of addresses if needed.
  // 1. isTransferAllowlistRestricted is false
  // 2. isTransferAllowlistRestricted is true and either the sender or receiver is on the allowlist
  function validateTokenOperation(address from, address to) internal view virtual {
    if (!isTransferAllowlistRestricted) {
      return;
    }

    bool isMintOrBurn = from == address(0) || to == address(0);
    bool isEitherAddressOwner = from == owner() || to == owner();
    bool isEitherAddressOnAllowlist = transferAllowList[from] || transferAllowList[to];

    // Allow the following:
    //  - Minting or burning of tokens
    //  - Transferring from/to contract owner
    //  - Transferring from/to allow listed address
    if (isMintOrBurn || isEitherAddressOwner || isEitherAddressOnAllowlist) {
      return;
    }

    revert("Transfer not allowed");
  }

  function setNFTForSale(
    bool isNFTForSale_,
    uint256 erc20TokenPrice_,
    uint256 nativeTokenPrice_,
    address acceptedERC20Token_,
    uint256 tokenId_
  ) external onlyOwner {
    isNFTForSale = isNFTForSale_;
    erc20TokenPrice[tokenId_] = erc20TokenPrice_;
    nativeTokenPrice[tokenId_] = nativeTokenPrice_;
    acceptedERC20Token = acceptedERC20Token_;
  }

  function purchaseWithNativeToken(address buyer_, uint256 tokenId_) external payable {
    require(isNFTForSale, "NFT is not for sale");
    require(nativeTokenPrice[tokenId_] != 0, "nativeTokenPrice token price is 0");
    require(msg.value == nativeTokenPrice[tokenId_], "Incorrect amount");
    _mint(buyer_, tokenId_, 1, "");
    AddressUpgradeable.sendValue(payable(owner()), nativeTokenPrice[tokenId_]);
    emit TokenPurchase(buyer_, tokenId_, nativeTokenPrice[tokenId_], address(0));
  }

  function purchaseWithERC20Token(address buyer_, uint256 tokenId_) external payable {
    require(isNFTForSale, "NFT is not for sale");
    require(erc20TokenPrice[tokenId_] != 0, "erc20TokenPrice token price is 0");
    require(acceptedERC20Token != address(0), "acceptedERC20Token is not set");
    IERC20Upgradeable(acceptedERC20Token).transferFrom(msg.sender, owner(), erc20TokenPrice[tokenId_]);
    _mint(buyer_, tokenId_, 1, "");
    emit TokenPurchase(buyer_, tokenId_, erc20TokenPrice[tokenId_], acceptedERC20Token);
  }

  /**
   * @dev Converts a uint256 to its ASCII string decimal representation.
   */
  function _toString(uint256 value) internal pure virtual returns (string memory str) {
    assembly {
      // The maximum value of a uint256 contains 78 digits (1 byte per digit), but
      // we allocate 0xa0 bytes to keep the free memory pointer 32-byte word aligned.
      // We will need 1 word for the trailing zeros padding, 1 word for the length,
      // and 3 words for a maximum of 78 digits. Total: 5 * 0x20 = 0xa0.
      let m := add(mload(0x40), 0xa0)
      // Update the free memory pointer to allocate.
      mstore(0x40, m)
      // Assign the `str` to the end.
      str := sub(m, 0x20)
      // Zeroize the slot after the string.
      mstore(str, 0)

      // Cache the end of the memory to calculate the length later.
      let end := str

      // We write the string from rightmost digit to leftmost digit.
      // The following is essentially a do-while loop that also handles the zero case.
      // prettier-ignore
      for { let temp := value } 1 {} {
                str := sub(str, 1)
                // Write the character to the pointer.
                // The ASCII index of the '0' character is 48.
                mstore8(str, add(48, mod(temp, 10)))
                // Keep dividing `temp` until zero.
                temp := div(temp, 10)
                // prettier-ignore
                if iszero(temp) { break }
            }

      let length := sub(end, str)
      // Move the pointer 32 bytes leftwards to make room for the length.
      str := sub(str, 0x20)
      // Store the length.
      mstore(str, length)
    }
  }
}
"
