    /// @param isVHolder: The current status of the v holder.
    bool isVHolder;
    /// @param holder: The holder address.
    address holder;
    /// @param sender: The token sender address from whom the token received.
    address sender;
    /// @param amount: The amount of token received.
    uint256 amount;
    /// @param lockTill: The lock-in period for that transfer.
    uint256 lockTill;
    /// @param transferrable: The amount user can transfer.
    uint256 transferrable;
    /// @param transferred: The amount user already transferred.
    uint256 transferred;
}

contract VYNKCHAIN is ERC20, Ownable {
    /// @dev Tracking team addresses.
    address public advisors;
    address public foundingTeam;
    address public ecosystemReserves;
    address public communityReserve;
    address public marketing;

    /// @dev array of vHolders;
    address[] private _vHoldersAddresses;
    /// @dev mapping from vHolderAddress => Details.
    mapping(address => VHolderDetails) public vHolderDetailsOf;

    /**
     * @dev Initializing the ERC20 contract and minting the `_totalSupply` of tokens.
     */
    constructor() ERC20("VYNK CHAIN", "VYNC") {
        /// @dev Assign addresses to teams.
        advisors = 0x33f9897C435ECd4F6D65632646bDEC6F86264575;
        foundingTeam = 0x4C4F9F77Ae440c2Ec3E759047F48B242E57A0D5F;
        ecosystemReserves = 0xEEfd7d640C2492c07975E0C539535a21Ef9E2873;

        communityReserve = 0x77AB30e9b19998e0A7BcE273e1Af1b7b5308Fe14;
        marketing = 0x9e21f0cBEbe80bE78E3B4695D37983dF703f67A5;

        /// @dev Minting the Tokenomics.
        // Community Reserve (25.26%): 25,000,000 VYNC
        _mint(communityReserve, 25_000_000 * (10 ** decimals()));
        // Market & Community (14.15%): 14,000,000 VYNC
        _mint(marketing, 14_000_000 * (10 ** decimals()));
        // Ecosystem Reserves (25.26%): 25,000,000 VYNC
        _mint(ecosystemReserves, 25_000_000 * (10 ** decimals()));
        // Founding Team (20.21%):  20,000,000 VYNC
        _mint(foundingTeam, 20_000_000 * (10 ** decimals()));
        // Advisors & Investors (15.16%): 15,000,000 VYNC
        _mint(advisors, 15_000_000 * (10 ** decimals()));
    }

    /**
     * @dev Overriding the `decimals` function to get user defined decimals.
     * @return uint8: Decimals value.
     */
    function decimals() public view virtual override returns (uint8) {
        return 4;
    }

    /**
     * @dev Minting tokens to an address.
     * Required: OnlyOwner.
     * @param account: The address to which you want to mint tokens.
     * @param amount: The amount of tokens you want to mint.
     */
    function mint(address account, uint256 amount) external onlyOwner {
        /// @dev Checking if supply crossing the limit.
        require(
            (totalSupply() + amount) <= _maxSupplyLimit(),
            "Supply99MillionReached"
        );

        /// @dev Minting amount to account.
        _mint(account, amount);
    }

    /**
     * @dev Burning tokens from an address.
     * Required: OnlyOwner.
     * @param account: The address from which you want to burn tokens.
     * @param amount: The amount of tokens you want to burn.
     */
    function burn(address account, uint256 amount) external onlyOwner {
        /// @dev Burning amount from account.
        _burn(account, amount);
    }

    /**
