                _details.lockTill = block.timestamp;
            }
        }
        /// If a vHolder transferring tokens to other
        else if (vHolderDetailsOf[from].isVHolder) {
            /// @dev Then update the details as to address will be a vHolder.
            VHolderDetails storage _details = vHolderDetailsOf[from];
            /// @dev Checking if vHolder in lock in period then revert.
            require(
                block.timestamp > _details.lockTill,
                "YouAreInLockInPeriod"
            );

            if (_details.transferrable != _details.amount) {
                /// @dev getting the no of quatre passed.
                uint256 noOfQuatrePassed = ((block.timestamp -
                    _details.lockTill) / 30 days) / 3;

                /// @dev Then add 10% of the amount of each quatre.
                uint256 transferrableAmount = (_details.amount *
                    (10 * (noOfQuatrePassed + 1))) / 100;

                /// @dev In case if v holder holds more than all the quatre.
                _details.transferrable = transferrableAmount >= _details.amount
                    ? _details.amount
                    : transferrableAmount;
            }

            /// @dev Getting the actual transferrable amount.
            uint256 canTransfer = _details.transferrable - _details.transferred;
            require(amount <= canTransfer, "VYNC: Transfer limit exceeded.");

            /// @dev Updating the transferred amount.
            _details.transferred += amount;
        }
    }
