    "contracts/OverpassSBT.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "solady/src/tokens/ERC721.sol";
import "solady/src/auth/Ownable.sol";
import "solady/src/utils/LibString.sol";

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/interfaces/IERC20Upgradeable.sol";

import "solidity-json-writer/contracts/JsonWriter.sol";

interface IRegistry {
    struct Agreement {
        // string contractName;
        // TODO: Should this point to an existing array of terms
        uint128 id; //template id for enterprise partners
        address offerer;
        uint128 flags;
        address promisor;
        uint32[] terms;
        address assetAddress;
        uint256 tokenId;
        uint128 expiration;
        address validatorModule;
        uint256 paymentAmount;
        string dynamicData;
        // uint256 nonce;
        uint256 chainId;
        address executorContract;
    }

    function getAgreementFromHash(
        bytes32 agreementHash
    ) external view returns (Agreement memory);
}

library OverpassSBTStorage {
    struct Layout {
        mapping(address minterAdress => bool enabled) minter;
        string baseImageURI;
    }

    bytes32 internal constant STORAGE_SLOT = keccak256(
        "OverpassSBT.storage"
    );

    function layout() internal pure returns (Layout storage l) {
        bytes32 slot = STORAGE_SLOT;
        assembly {
            l.slot := slot
        }
    }
}


interface IOverpassSBT {
    function mint(address to, uint256 tokenId) external;
}

library AttributeWriter{
    using JsonWriter for JsonWriter.Json;

    function writeAttributeUint(JsonWriter.Json memory writer, string memory trait_type, uint256 value) internal pure returns (JsonWriter.Json memory) {
        writer = writer.writeStartObject();
        writer = writer.writeStringProperty("trait_type", trait_type);
        writer = writer.writeUintProperty("value", value);
        writer = writer.writeEndObject();
        return writer;
    }

    function writeAttributeString(JsonWriter.Json memory writer, string memory trait_type, string memory value) internal pure returns (JsonWriter.Json memory) {
        writer = writer.writeStartObject();
        writer = writer.writeStringProperty("trait_type", trait_type);
        writer = writer.writeStringProperty("value", value);
        writer = writer.writeEndObject();
        return writer;
    }

    function writeAttributeAddress(JsonWriter.Json memory writer, string memory trait_type, address value) internal pure returns (JsonWriter.Json memory) {
        writer = writer.writeStartObject();
        writer = writer.writeStringProperty("trait_type", trait_type);
        writer = writer.writeStringProperty("value", LibString.toHexStringChecksummed(value));
        writer = writer.writeEndObject();
        return writer;
    }
}


/// @author Pudgy Penguins Engineering Team
/// @custom:oz-upgrades-unsafe-allow constructor state-variable-immutable
contract OverpassSBT is Initializable, UUPSUpgradeable, ERC721, Ownable, IOverpassSBT {
    using JsonWriter for JsonWriter.Json;
    using AttributeWriter for JsonWriter.Json;

    IRegistry public immutable registry;

    constructor(IRegistry _registry) {
        registry = _registry;

        _disableInitializers();
    }

    function initialize() public initializer {
        _initializeOwner(msg.sender);
    }

    function name() override public pure returns (string memory) {
        return "OverpassIP";
    }

    function symbol() override public pure returns (string memory) {
        return "OIP";
    }    

    function tokenURI(uint256 tokenId) override public view returns (string memory) {
        JsonWriter.Json memory writer;

        IRegistry.Agreement memory agreement = registry.getAgreementFromHash(bytes32(tokenId));

        writer = writer.writeStartObject();

        writer = writer.writeStringProperty("name", string(abi.encodePacked(
            "OverpassIP: ",
            LibString.toHexString(tokenId, 32)
        )));

        writer = writer.writeStringProperty("external_url", "https://overpassip.com");

        writer = writer.writeStringProperty("image", imageURI(tokenId));

        writer = writer.writeStartArray("attributes");

        writer = writer.writeAttributeString("Chain ID", LibString.toString(agreement.chainId));
        writer = writer.writeAttributeAddress("Asset Address", agreement.assetAddress);
        writer = writer.writeAttributeString("Token ID", LibString.toString(agreement.tokenId));
        writer = writer.writeAttributeAddress("Licensed From", agreement.offerer);
        writer = writer.writeAttributeAddress("Licensed To", agreement.promisor);

        writer = writer.writeEndArray();

        writer = writer.writeEndObject();

        return string(abi.encodePacked("data:application/json,", writer.value));
    }

    function imageURI(uint256 tokenId) public view returns (string memory) {
        return string(abi.encodePacked(
            OverpassSBTStorage.layout().baseImageURI,
            LibString.toHexString(tokenId, 32)
        ));
    }

    function setBaseImageURI(string calldata baseImageURI) public onlyOwner {
        OverpassSBTStorage.layout().baseImageURI = baseImageURI;
    }

    function setMinter(address minter, bool enabled) public onlyOwner {
        OverpassSBTStorage.layout().minter[minter] = enabled;
    }

    function mint(address to, uint256 tokenId) public override {
        require(OverpassSBTStorage.layout().minter[msg.sender], "OverpassSBT: Only minter can mint");
        _mint(to, tokenId);
    }

    function _authorizeUpgrade(address newImplementation)
        internal
        override
        onlyOwner
    {}

    function safeTransferFrom(
        address from,
        address to,
        uint256 tokenId,
        bytes calldata data
    ) override public payable {
        (from, to, tokenId, data);
        revert("OverpassSBT: Cannot transfer");
    }

    function transferFrom(
        address from,
        address to,
        uint256 tokenId
    ) override public payable {
        (from, to, tokenId);
        revert("OverpassSBT: Cannot transfer");
    }

    function approve(
        address to,
        uint256 tokenId
    ) override public payable {
        (to, tokenId);
        revert("OverpassSBT: Cannot approve");
    }

    function setApprovalForAll(
        address operator,
        bool approved
    ) override public pure {
        (operator, approved);
        revert("OverpassSBT: Cannot setApprovalForAll");
    }
}"
    },
