    "contracts/Collection/TrumpPrize2.sol": {
      "content": "// SPDX-License-Identifier: GPL-3.0-or-later
pragma solidity 0.8.13;

import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Enumerable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/interfaces/IERC2981.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "./IDT_Token.sol";
import { DefaultOperatorFilterer } from "operator-filter-registry/src/DefaultOperatorFilterer.sol";

/// @title TrumpPrize2 contract
/// @notice This contract is used to create an ERC721 token collection where tokens can be minted and royalties can be paid.
/// @dev This contract inherits from several OpenZeppelin contracts and interfaces, including ERC721Enumerable, IERC2981, and AccessControl.
/// @dev This contract also inherits from Opensea code to ensure royalties on Opensea
contract TrumpPrize2 is 

        ERC721Enumerable, 
        DefaultOperatorFilterer, 
        IERC2981, 
        AccessControl 
{
    using Strings for uint256;
    using SafeERC20 for IERC20;

    /// @dev For storing royalty shares.
    /// @param share Share percentage
    /// @param payee Address to which the share percentage will be payed.
    struct ShareData {
        uint16 share;
        address payee;
    }

    /// @dev bytes32 interpretation of MINTER_ROLE for AccessControl
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");

    /// @dev bytes32 interpretation of CONTRACT_ADMIN for AccessControl
    bytes32 public constant CONTRACT_ADMIN = keccak256("CONTRACT_ADMIN");

    uint256 constant TOTAL_SHARES = 1000;
    uint16 constant internal collectionRoyaltyAmount = 100;
    string private collectionURI;

    /// @dev Minting starts with token ID 1
    uint256 public nextToken = 1;

    /// @dev Base URI of token. Can be set in {setBaseURI}.
    string public tokenBaseURI;

    /// @dev True if metadata updates are frozen for this contract. See {freeze}.
    bool public frozen;

    /// @dev Stores shares.
    ShareData[] public shares;

    /// @dev Emitted when contract URI has been modified. See {setContractURI}.
    event ContractURIChanged(string uri);


    /// @dev Emitted when token base URI has been modified. See {setBaseURI}.
    event BaseURI(string baseURI);


    /// @dev Emitted in {receive} when receiving payment.
    event PaymentReceived(uint256 value);


    /// @dev Emitted when freezing metadata updates. See {freeze}.
    event MetadataFrozen();

    /// @dev The sum of all shares must equal TOTAL_SHARES. Reverts otherwise.
    /// @param _name The name of the token collection.
    /// @param _symbol The symbol of the token collection.
    /// @param _tokenBaseURI The base URI of the token collection.
    /// @param _shares An array of share data, where each share data includes a uint16 share and a payee address.

     constructor(
        string memory _name,
        string memory _symbol,
        string memory _tokenBaseURI,
        ShareData[] memory _shares
    ) 
        ERC721(_name, _symbol)
    {
        _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());
        _setupRole(CONTRACT_ADMIN, _msgSender());
        _setupRole(MINTER_ROLE, _msgSender());
        tokenBaseURI = _tokenBaseURI;
        uint256 count = 0;
        for (uint sh = 0; sh < _shares.length; sh++) {
            count += _shares[sh].share;
            shares.push(_shares[sh]);
        }
        require(count == TOTAL_SHARES, "total shares must equal 1000");
    }

    /// @dev This function allows the contract to receive payments.
    receive() external payable {
        emit PaymentReceived(msg.value);
    }

    /// @notice Mints a specified number of tokens to a given address. See internal {_mint} function.
    /// @dev Caller must have minter role
    /// @param _recipients The addresses to which the tokens will be minted.
    function mintSingleTokens(
        address[50] calldata _recipients
    ) 
        external 
        onlyRole(MINTER_ROLE) 
    {        
        uint256 pos = nextToken;
        require(pos == 1,"TrumpPrize2: Only 50 tokens can be minted");
        nextToken += 50;
        for (uint256 j = 0; j < 50; j++)
        {
            _mint(_recipients[j], pos++);
        }
    }

    /// @notice Freezes metadata updates for the contract.
    /// @dev Once frozen the baseURI of the contract can't be updated anymore.
    /// @dev Caller must have admin role.
    /// @dev Emits a {MetadataFrozen} event.
    function freeze() external onlyRole(DEFAULT_ADMIN_ROLE) {
        frozen = true;
        emit MetadataFrozen();
    }

    /// @notice Sets the base URI for the tokens of this collection.
    /// @dev Caller must have admin role.
    /// @dev Can't be invoked if the collection is already frozen.
    /// @dev Emits a {BaseURI} event.
    /// @param newBaseURI The new base URI for the tokens.
    function setBaseURI(string memory newBaseURI) external onlyRole(CONTRACT_ADMIN) {
        require(!frozen,"Collection is frozen");
        tokenBaseURI = newBaseURI;
        emit BaseURI(newBaseURI);
    }

    /// @notice Sets the URI for the collection-level metadata of the contract.
    /// @dev Caller must have admin role.
    /// @dev Emits a {ContractURIChanged} event.
    /// @param _uri The new collectionURI of the cotnract.
    function setContractURI(string memory _uri) external onlyRole(CONTRACT_ADMIN) {
        collectionURI = _uri;
        emit ContractURIChanged(_uri);
    }

    /// @dev Payment function for IERC2981 when we use the contract as a royalty splitter
    /// @dev Handles WETH, USDx etc
    /// @dev Splits the balance of a specified ERC20 token among the payees according to their shares and sends it to them with safeTrasnfer
    /// @param erc20 The address of the ERC20 token to be split.
    function SplitERC20Payment(address erc20) external {
        IERC20 token = IERC20(erc20);
        uint256 amount_to_pay = token.balanceOf(address(this));
        for (uint256 payee = 0; payee < shares.length; payee++) {
            token.safeTransfer(
                    shares[payee].payee,
                    shares[payee].share * amount_to_pay / TOTAL_SHARES
                );
        }
    }

    /// @dev Payment function for IERC2981 when we use the contract as a royalty splitter
    /// @dev Handles MATIC
    /// @dev Splits the balance of MATIC among the payees according to their shares and sends it to them with sendETH
    function splitPayment() external {
        uint256 amount_to_pay = address(this).balance;
        for (uint256 payee = 0; payee < shares.length; payee++) {
            sendETH(shares[payee].payee,shares[payee].share * amount_to_pay / TOTAL_SHARES);
        }
    }

    /// @notice Returns the contract URI associated with this NFT collection.
    /// @return A string representing the contract URI.
    function contractURI() external view returns (string memory) {
        return collectionURI;
    }

    /// @notice Checks if a token exists.
    ///@param tokenId The ID of the token to check for existence
    /// @return True if the token exists, false otherwise.
    function tokenExists(uint256 tokenId) external view returns (bool) {
        return _exists(tokenId);
    }

    /// @dev IERC2981 calculate the amount of royalties
    /// @dev Returns the amount of royalties and the recipient collection address
    /// @dev Royalty amount is 10%
    /// @param - tokenId - not used
    /// @param salePrice Amount the NFT was sold for
    /// @return receiver Receiver of royalties
    /// @return royaltyAmount Royalty amound that should be paid
    function royaltyInfo(uint256 , uint256 salePrice)
        external
        view
        returns(
            address receiver, 
            uint256 royaltyAmount
        )
    {
        uint256 _royaltyAmount = (salePrice * collectionRoyaltyAmount) / 1000;
        return (address(this), _royaltyAmount);
    }

    /// @dev Override required for opensea royalty enforcement 
    function setApprovalForAll(address operator, bool approved) 
        public 
        override (ERC721, IERC721)
        onlyAllowedOperatorApproval(operator) 
    {
        super.setApprovalForAll(operator, approved);
    }

    /// @dev Override required for opensea royalty enforcement 
    function approve(address operator, uint256 tokenId) 
        public 
        override  (ERC721,IERC721)
        onlyAllowedOperatorApproval(operator) 
    {
        super.approve(operator, tokenId);
    }

    /// @dev Override required for opensea royalty enforcement 
    function transferFrom(address from, address to, uint256 tokenId) 
        public 
        override  (ERC721, IERC721) 
        onlyAllowedOperator(from)
    {
        super.transferFrom(from, to, tokenId);
    }

    /// @dev Override required for opensea royalty enforcement 
    function safeTransferFrom(address from, address to, uint256 tokenId) 
        public 
        override  (ERC721, IERC721) 
        onlyAllowedOperator(from) 
    {
        super.safeTransferFrom(from, to, tokenId);
    }

    /// @dev Override required for opensea royalty enforcement 
    function safeTransferFrom(address from, address to, uint256 tokenId, bytes memory data)
        public
        override (ERC721, IERC721)
        onlyAllowedOperator(from) 
    {
        super.safeTransferFrom(from, to, tokenId, data);
    }


    /// @notice Returns the URI of a given token.
    /// @dev The tokenId is constructed by combining the baseUri, folder and tokenId
    /// @dev Data is reformatted to directory structure.
    /// @param tokenId The ID of the token to retrieve the URI for.
    /// @return A string representing the URI of the token.
    function tokenURI(uint256 tokenId) public view override returns (string memory) {
        require(_exists(tokenId), "ERC721Metadata: URI query for nonexistent token");
        string memory file = tokenId.toString();
        return string(
            abi.encodePacked(
                tokenBaseURI,
                file,
                ".json"
            )
        );
    }

    /// @dev See {IERC165-supportsInterface}.
    function supportsInterface(bytes4 interfaceId)
        public 
        view 
        virtual 
        override(AccessControl, ERC721Enumerable, IERC165) 
        returns(bool) 
    {
        return
            interfaceId == type(IERC721Enumerable).interfaceId ||
            interfaceId == type(AccessControl).interfaceId ||
            interfaceId == type(IERC165).interfaceId ||
            interfaceId == type(IERC2981).interfaceId ||
            super.supportsInterface(interfaceId);
    }

    /// @dev Sends the specified amount of Matic to the given destination address.
    /// @dev Reverts if could not send.
    /// @dev Don't use send or xfer (gas)
    /// @param dest The destination address to which the Matic will be sent.
    /// @param amount The amount of Matic to be sent, in wei.
    function sendETH(address dest, uint amount) internal {
        (bool sent, ) = payable(dest).call{value: amount}("");
        require(sent, "Failed to send Ether");
    }
}
"
    },
    "operator-filter-registry/src/DefaultOperatorFilterer.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.13;

import {OperatorFilterer} from "./OperatorFilterer.sol";
import {CANONICAL_CORI_SUBSCRIPTION} from "./lib/Constants.sol";
/**
 * @title  DefaultOperatorFilterer
 * @notice Inherits from OperatorFilterer and automatically subscribes to the default OpenSea subscription.
 * @dev    Please note that if your token contract does not provide an owner with EIP-173, it must provide
 *         administration methods on the contract itself to interact with the registry otherwise the subscription
 *         will be locked to the options set during construction.
 */

abstract contract DefaultOperatorFilterer is OperatorFilterer {
    /// @dev The constructor that is called when the contract is being deployed.
    constructor() OperatorFilterer(CANONICAL_CORI_SUBSCRIPTION, true) {}
}
"
