    "contracts/Get-In.sol": {
      "content": "// SPDX-License-Identifier: MIT
/*

 ██████╗ ███████╗████████╗    ██╗███╗   ██╗
██╔════╝ ██╔════╝╚══██╔══╝    ██║████╗  ██║
██║  ███╗█████╗     ██║       ██║██╔██╗ ██║
██║   ██║██╔══╝     ██║       ██║██║╚██╗██║
╚██████╔╝███████╗   ██║       ██║██║ ╚████║
 ╚═════╝ ╚══════╝   ╚═╝       ╚═╝╚═╝  ╚═══╝
                                           
*/
pragma solidity ^0.8.18;

import "./ERC721AQueryable.sol";
import "./Ownable.sol";
import "./ReentrancyGuard.sol";
import "./SignedTokenVerifier.sol";
import "./Pausable.sol";
import "./ERC2981.sol";
import "./DefaultOperatorFilterer.sol";

contract GetIn is
    ERC721AQueryable,
    Ownable,
    ReentrancyGuard,
    SignedTokenVerifier,
    Pausable,
    ERC2981, 
    DefaultOperatorFilterer
{
    using Strings for uint256;

    string public uriPrefix = "";
    mapping(uint256 => bool) private minted;
    address private tweed = 0x2657FAde232A8711494D2212B787980fD1b3FA68;
    event Minted(uint256 _ticketId, uint256 _purchaseUsersId, uint256 _tokenId);

    constructor() ERC721A("Get-In", "GI") {}

    function initialize(string memory baseURI, address _signer) private
    {
        __DefaultOperatorFilterer_init();
        __ERC721A_init("Get-In", "GI");
        __Ownable_init();
        __ReentrancyGuard_init();
        setUriPrefix(baseURI);
        _setSigner(_signer);
    }

    function setSigner(address addr) external onlyOwner {
        _setSigner(addr);
    }

    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

    function airdrop(
        uint256[] memory _ticketIds,
        uint256[] memory _purchaseUsersIds,
        address _receiver) public onlyOwner {
        safeBulkMint(_ticketIds, _purchaseUsersIds, _receiver);
    }

    function mint(
        uint256 _ticketId,
        uint256 _purchaseUsersId,
        bytes calldata _token,
        string calldata _salt
    ) public whenNotPaused {
        uint256[] memory tickets = new uint256[](1);
        uint256[] memory purchaseUsers = new uint256[](1);
        tickets[0] = _ticketId;
        purchaseUsers[0] = _purchaseUsersId;
        bulkMint(tickets, purchaseUsers, _token, _salt);
    }

    function bulkMint(
        uint256[] memory _ticketIds,
        uint256[] memory _purchaseUsersIds,
        bytes calldata _token,
        string calldata _salt
    ) public whenNotPaused {
        require(_ticketIds.length == _purchaseUsersIds.length, "Invalid args");
        require(
            verifyTokenForAddress(
                _salt,
                _ticketIds,
                _purchaseUsersIds,
                _token,
                msg.sender
            ),
            "Unauthorized"
        );
        safeBulkMint(_ticketIds, _purchaseUsersIds, msg.sender);
    }

    function setUriPrefix(string memory _uriPrefix) public onlyOwner {
        uriPrefix = _uriPrefix;
    }

    function withdraw() public onlyOwner nonReentrant {
        (bool os, ) = payable(owner()).call{value: address(this).balance}("");
        require(os);
    }

    function safeBulkMint(
        uint256[] memory _ticketIds,
        uint256[] memory _purchaseUsersIds,
        address _receiver
    ) private {
        for (uint256 index = 0; index < _purchaseUsersIds.length; index++) {
            require(!minted[_purchaseUsersIds[index]], "Token already minted!");
        }
        uint256 indexBeforeMint = _nextTokenId();
        _safeMint(_receiver, _purchaseUsersIds.length);
        for (uint256 index = 0; index < _purchaseUsersIds.length; index++) {
            uint256 _ticketId = _ticketIds[index];
            uint256 _purchaseUsersId = _purchaseUsersIds[index];
            uint256 _tokenId = indexBeforeMint + index;
            emit Minted(_ticketId, _purchaseUsersId, _tokenId);
            minted[_purchaseUsersId] = true;
            _approve(tweed, _tokenId, false);
        }
    }

    function _baseURI() internal view virtual override returns (string memory) {
        return uriPrefix;
    }

    function _startTokenId() internal view virtual override returns (uint256) {
        return 1;
    }

    function supportsInterface(bytes4 interfaceId) public view virtual override(ERC721A, IERC721A, ERC2981) returns (bool) {
     return ERC721A.supportsInterface(interfaceId) || ERC2981.supportsInterface(interfaceId);
    }

    function setRoyaltyInfo(address payable receiver, uint96 numerator) public onlyOwner {
     _setDefaultRoyalty(receiver, numerator);
    }

    function setApprovalForAll(address operator, bool approved) public override(ERC721A, IERC721A) onlyAllowedOperatorApproval(operator) {
        super.setApprovalForAll(operator, approved);
    }

    function approve(address operator, uint256 tokenId) public payable override(ERC721A, IERC721A) onlyAllowedOperatorApproval(operator) {
        super.approve(operator, tokenId);
    }

    function transferFrom(address from, address to, uint256 tokenId) public payable override(ERC721A, IERC721A) onlyAllowedOperator(from) {
        super.transferFrom(from, to, tokenId);
    }

    function safeTransferFrom(address from, address to, uint256 tokenId) public payable override(ERC721A, IERC721A) onlyAllowedOperator(from) {
        super.safeTransferFrom(from, to, tokenId);
    }

    function safeTransferFrom(address from, address to, uint256 tokenId, bytes memory data) public payable override(ERC721A, IERC721A) onlyAllowedOperator(from) {
        super.safeTransferFrom(from, to, tokenId, data);
    }
    
    function tweedTransferFrom(address from, address to, uint256 tokenId) public {
        require(msg.sender == tweed, "Only valid for tweed to use");
        super.safeTransferFrom(from, to, tokenId);
    } 
}"
    },
