      "content": "// SPDX-FileCopyrightText: © Courtyard Inc. (https://courtyard.io)
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

import "./ERC20PaymentProcessor.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/metatx/ERC2771Context.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

/**
 * @title AbstractSwapLegacyUSDCForNativeUSDC implements the core mechanics behind {SwapLegacyUSDCForNativeUSDC}.
 */
abstract contract AbstractSwapLegacyUSDCForNativeUSDC is ERC2771Context, ReentrancyGuard, Ownable, Pausable, ERC20PaymentProcessor {

    using SafeERC20 for IERC20;

    event SwappedLegacyUSDCForNativeUSDC(address indexed swapper, uint256 amount);

    address public immutable nativeUSDCAddress;
    address public nativeUsdcProvider; // the account that is used to provide Native USDC for this contract to operate the swap.
    uint256 constant MIN_AMOUNT_TO_SWAP = 1 * 10 ** 6; // Minimum 1 USDC to swap

    /**
     * @dev constructor
     * Parameters:
     *  - nativeUsdcProvider: the account that is used to provide Native USDC for this contract to operate the swap.
     * 
     * Requirements:
     *  - In order to use this swap contract, the {nativeUsdcProvider} must first authorize this contract to spend its Native USDC.
     */
    constructor(
        address _nativeUSDCAddress,
        address _legacyUSDCAddress,
        address _nativeUSDCProvider,
        address _legacyUSDCRecipient,
        address _trustedForwarder
    )
    Ownable()
    ERC2771Context(_trustedForwarder) 
    ERC20PaymentProcessor(_legacyUSDCAddress, _legacyUSDCRecipient) {
        nativeUSDCAddress = _nativeUSDCAddress;
        updateNativeUSDCProvider(_nativeUSDCProvider);
    }


    /* ========================================== ERC2771Context OVERRIDES ========================================== */

    /**
     * @dev Enforces the use of {ERC2771Context._msgSender} over {Context._msgSender}.
     */
    function _msgSender() internal view virtual override(ERC2771Context, Context) returns (address) {
        return ERC2771Context._msgSender();
    }

    /**
     * @dev Enforces the use of {ERC2771Context._msgData} over {Context._msgData}.
     */
    function _msgData() internal view virtual override(ERC2771Context, Context) returns (bytes calldata) {
        return ERC2771Context._msgData();
    }


    /* =============================================== PAUSE HELPERS =============================================== */

    /**
     * @dev see {Pausable._pause}
     */
    function pause() public onlyOwner whenNotPaused {
        _pause();
    }

    /**
     * @dev see {Pausable._unpause}
     */
    function unpause() public onlyOwner whenPaused {
        _unpause();
    }


    /* =========================================== SWAP HELPER ACCOUNTS =========================================== */

    /**
     * @dev helper to update the Native USDC provider address.
     */
    function updateNativeUSDCProvider(address _nativeUsdcProvider) public onlyOwner {
        nativeUsdcProvider = _nativeUsdcProvider;
    }

    /**
     * @dev helper to update recipient that receives the legacy UDSC.e funds sent for swapping.
     */
    function updateLegacyUSDCRecipient(address recipient) public onlyOwner {
        _setPaymentReceiverAddress(recipient);
    }


    /* =================================================== SWAP =================================================== */

    /**
     * @dev Check that the direct caller (given by msg.sender explicitly) is a trusted caller.
     */
    modifier onlyTrustedCaller() {
        require(_isTrustedCaller(msg.sender), "SwapLegacyUSDCForNativeUSDC: Direct caller is not authorized.");
        _ ;
    }

    /**
     * @dev Check whether or not a particular address is an trusted caller.
     * An implementation of should define this logic and would usually
     * want to include the {trustedForwarder} in the list of trusted callers.
     * Defaults to always return true
     */
    function _isTrustedCaller(address) internal view virtual returns (bool) {
        return true;
    }

    /**
     * @dev permit legacy USDC funds to be spent on behalf of an account, 
     * pull those funds and fund that same account with Native USDC in a single transaction.
     * 
     * Requirements:
     *  - The originating caller of this function (returned by _msgSender()) has to be the signer of the signature and swapper.
     *  - A signature from the {swapper} is required to approve the legacy USDC funds spending during the transaction
     *  - The signature has to correspond to the amount requested to swap
     *  - {expiration} must correspond to the expiration time encoded in the signature
     */
    function permitLegacyUSDCBalanceAndSwapForNativeUSDC(
        uint256 amount,                 // the quantity of legacy USDC to swap
        uint256 expiration,             // the expiration of the permit signature
        uint8 v, bytes32 r, bytes32 s   // the parameters of the permit signature
    ) external nonReentrant onlyTrustedCaller whenNotPaused {
        require(amount >= MIN_AMOUNT_TO_SWAP, "SwapLegacyUSDCForNativeUSDC: Minimum amount to swap not reached.");

        address originator = _msgSender(); // the originator of the call and subject of the swap

        // Pull the legacy USDC funds to swap
        _permitAndProcessPayment(
            originator, // the originator of the call and subject of the swap
            amount,     // the amount requested to transfer for swapping
            amount,     // the amount that is supposed to be approved for this transaction (and encoded in the permit signature)
            expiration, // the expiration of the permit signature
            v, r, s     // the parameters of the permit signature
        );

        // Send the corresponding amount of Native USDC to the swapper
        IERC20(nativeUSDCAddress).safeTransferFrom(nativeUsdcProvider, originator, amount);

        emit SwappedLegacyUSDCForNativeUSDC(originator, amount);
    }
}


/**
 * @title SwapLegacyUSDCForNativeUSDC allows an account to swap legacy USDC (USDC.e) for native USDC on Polygon.
 */
contract SwapLegacyUSDCForNativeUSDC is AbstractSwapLegacyUSDCForNativeUSDC {
    constructor(
        address _nativeUSDCProvider,
        address _legacyUSDCRecipient
    ) AbstractSwapLegacyUSDCForNativeUSDC(
        0x3c499c542cEF5E3811e1192ce70d8cC03d5c3359, // Native USDC on Polygon Mainnet
        0x2791Bca1f2de4661ED88A30C99A7a9449Aa84174, // USDC.e on Polygon Mainnet
        _nativeUSDCProvider,
        _legacyUSDCRecipient,
        0xd8253782c45a12053594b9deB72d8e8aB2Fca54c  // Gelato forwarder on all chains
    ) {}

    function _isTrustedCaller(address addr) internal view virtual override returns (bool) {
        if (addr == 0xd8253782c45a12053594b9deB72d8e8aB2Fca54c) return true;    // Gelato Relayer on Polygon Mainnet
        return false;
    }    
}"
