// File contracts/RewardVault.sol

pragma solidity ^0.8.0;
contract RewardVault is Ownable {
    IERC20 rewardToken = IERC20(0x06d21ef0c9e99850B2d0474C9a91a4B1ce37EA16);

    mapping(address => uint256) public rewards;
    mapping(address => bool) public spenderContracts;
    uint256 public claimTaxPerThousand;
    bool public claimEnabled;

    uint256 public totalRewardsAdded;
    uint256 public totalRewardsClaimed;
    uint256 public totalRewardsSpent;
    uint256 public totalRewardsTaxed;

    /////////////////////
    //    MODIFIERS    //
    /////////////////////
    modifier onlyClaimEnabled() {
        require(claimEnabled, "Claiming disabled");
        _;
    }

    modifier onlySpender() {
        require(spenderContracts[msg.sender], "Unauthorized request");
        _;
    }

    constructor() {
        claimTaxPerThousand = 0;
    }

    /////////////////////
    //      PUBLIC     //
    /////////////////////
    function spendFrom(address spender, uint256 _amount) external onlyClaimEnabled onlySpender {
        uint256 claimableTokens = rewards[spender];
        require(claimableTokens >= _amount, "Insufficient rewards to spend");

        rewards[spender] -= _amount;
        totalRewardsSpent += _amount;
    }

    function claim() external onlyClaimEnabled {
        uint256 claimableTokens = rewards[msg.sender];
        require(claimableTokens > 0, "Nothing to claim");
        rewards[msg.sender] = 0;

        uint256 tax = (claimableTokens / 1000) * claimTaxPerThousand;
        uint256 remaining = claimableTokens - tax;

        totalRewardsClaimed += remaining;
        totalRewardsTaxed += tax;

        rewardToken.transfer(msg.sender, remaining);
    }

    /////////////////////
    //    ONLY OWNER   //
    /////////////////////
    function addRewards(address[] memory _addressList, uint256[] memory _rewardsToAdd, uint256 totalCheck) public onlyOwner returns (uint256) {
        require(_addressList.length == _rewardsToAdd.length, "Input error");
        claimEnabled = false;

        uint256 length = _rewardsToAdd.length;
        uint256 total = 0;
        for (uint256 i = 0; i < length; i++) {
            uint256 reward = _rewardsToAdd[i];

            total += reward;
            rewards[_addressList[i]] += reward;
        }

        require(total == totalCheck, "Invalid Checksum");
        totalRewardsAdded += total;

        return total;
    }

    function resetRewards(address[] memory _addressList) public onlyOwner {
        uint256 length = _addressList.length;

        for (uint256 i = 0; i < length; i++) {
            rewards[_addressList[i]] = 0;
        }
    }

    function setClaimTax(uint256 _taxPerThousand) public onlyOwner {
        require(_taxPerThousand < 1000, "Claim tax cannot be higher than 1000");

        claimTaxPerThousand = _taxPerThousand;
    }

    function setSpender(address _contractAddress, bool _canSpend) public onlyOwner {
        spenderContracts[_contractAddress] = _canSpend;
    }

    function setClaimEnabled(bool _enabled) public onlyOwner {
        claimEnabled = _enabled;
    }

    /////////////////////
    //       VIEW      //
    /////////////////////
    function balanceAvailable() external view returns (uint256) {
        return rewardToken.balanceOf(address(this));
    }

    function unclaimedRewards() external view returns (uint256) {
        return totalRewardsAdded - (totalRewardsClaimed + totalRewardsSpent + totalRewardsTaxed);
    }

    //DONT RENOUNCE OWNERSHIP
    function renounceOwnership() public override onlyOwner {
        revert("Cannot be renounced");
    }
}
