      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.19;

import {Address} from "@openzeppelin/contracts/utils/Address.sol";
import {IERC721Upgradeable} from "@openzeppelin/contracts-upgradeable/token/ERC721/IERC721Upgradeable.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";

import {LibReentrancyGuard} from "contracts/Diamond/libraries/LibReentrancyGuard.sol";
import {LibBusinessId} from "contracts/Diamond/libraries/LibBusinessId.sol";
import {LibFactory} from "contracts/Diamond/libraries/LibFactory.sol";

import {Order} from "contracts/Diamond/interfaces/IFactory.sol";
import {IAsset, NFTBaseInfo} from "contracts/Diamond/interfaces/IAsset.sol";
import {Module} from "contracts/Diamond/interfaces/IController.sol";
import {IModule} from "contracts/Diamond/interfaces/IModule.sol";
import {Composition} from "contracts/Diamond/interfaces/IPortfolio.sol";
import {IFactoryB} from "contracts/Diamond/interfaces/IFactoryB.sol";

contract FactoryBFacet is IFactoryB {
    using Address for address;

    address private immutable original;

    /* ----------------------------- modifier ----------------------------- */

    modifier onlyDelegateCall() {
        require(address(this) != original);
        _;
    }

    modifier onlyNFTOwner(address _NFTAddr, uint256 _tokenId) {
        require(msg.sender == IERC721Upgradeable(_NFTAddr).ownerOf(_tokenId), "Factory: NOT_NFT_OWNER");
        _;
    }

    modifier AutoMate(address _NFTAddr, uint256 _tokenId) {
        require(
            msg.sender == LibFactory.getStorage().autoMeta
                || msg.sender == IERC721Upgradeable(_NFTAddr).ownerOf(_tokenId),
            "Factory: NOT_AUTO_META"
        );
        _;
    }
    /* ---------------------------- constructor ---------------------------- */

    constructor() {
        // Immutables are computed in the init code of the contract, and then inlined into the deployed bytecode.
        // In other words, this variable won't change when it's checked at runtime.
        original = address(this);
    }

    /* --------------------------- onlyNFTOwner ---------------------------- */

    function chargeNFTRoyalties(uint96 _royalties, address _NFTAddr, uint256 _NFTId)
        public
        onlyNFTOwner(_NFTAddr, _NFTId)
    {
        LibFactory.FactoryStorage storage fs = LibFactory.getStorage();
        require(_royalties <= fs.maxRoyaltyRatio, "SCF: Royalties too high");
        IAsset(_NFTAddr).chargeNFTRoyalties(_NFTId, _royalties);
        emit NFTRoyaltiesChanged(_NFTAddr, _NFTId, _royalties);
    }

    function chargeNFTSellPrice(uint256 _sellPrice, address _NFTAddr, uint256 _NFTId)
        public
        onlyNFTOwner(_NFTAddr, _NFTId)
    {
        IAsset(_NFTAddr).chargeNFTSellPrice(_NFTId, _sellPrice);
        emit NFTSellPriceChanged(_NFTAddr, _NFTId, _sellPrice);
    }

    function addModule(address _NFTAddr, uint256 _NFTId, Module calldata _module)
        external
        onlyNFTOwner(_NFTAddr, _NFTId)
    {
        LibReentrancyGuard.nonReentrantBefore();
        {
            uint256 originalAssetId = IAsset(_NFTAddr).originalAsset(_NFTId);
            if (originalAssetId == 0) {
                IAsset(_NFTAddr).addModule(originalAssetId, _module);
            } else {
                revert DisableChangeReplicatedToken(originalAssetId);
            }

            emit ModuleChanged(_NFTAddr, _NFTId);
        }
        LibReentrancyGuard.nonReentrantAfter();
    }

    function removeModule(address _NFTAddr, uint256 _NFTId, Module calldata _module)
        external
        onlyNFTOwner(_NFTAddr, _NFTId)
    {
        LibReentrancyGuard.nonReentrantBefore();
        {
            uint256 originalAssetId = IAsset(_NFTAddr).originalAsset(_NFTId);
            if (originalAssetId == 0) {
                IAsset(_NFTAddr).removeModule(originalAssetId, _module);
            } else {
                revert DisableChangeReplicatedToken(originalAssetId);
            }
            emit ModuleChanged(_NFTAddr, _NFTId);
        }
        LibReentrancyGuard.nonReentrantAfter();
    }

    function changeModule(address _NFTAddr, uint256 _NFTId, Module calldata _newModule)
        external
        onlyNFTOwner(_NFTAddr, _NFTId)
    {
        LibReentrancyGuard.nonReentrantBefore();
        {
            uint256 originalAssetId = IAsset(_NFTAddr).originalAsset(_NFTId);
            if (originalAssetId == 0) {
                IAsset(_NFTAddr).changeModuleDefaultParams(_NFTId, _newModule);
            } else {
                revert DisableChangeReplicatedToken(originalAssetId);
            }
            emit ModuleChanged(_NFTAddr, _NFTId);
        }
        LibReentrancyGuard.nonReentrantAfter();
    }

    function changePortfolio(address _NFTAddr, uint256 _NFTId, Composition calldata _composition)
        external
        onlyNFTOwner(_NFTAddr, _NFTId)
    {
        LibReentrancyGuard.nonReentrantBefore();
        {
            uint256 originalAssetId = IAsset(_NFTAddr).originalAsset(_NFTId);
            if (originalAssetId == 0) {
                emit PortfolioChanged(_NFTAddr, _NFTId);
                IAsset(_NFTAddr).changePortfolio(_NFTId, _composition);
            } else {
                revert DisableChangeReplicatedToken(originalAssetId);
            }
        }
        LibReentrancyGuard.nonReentrantAfter();
    }

    function deposit(bytes32 _businessId, address _NFTAddr, uint256 _NFTId, uint256 _FTAmount)
        public
        payable
        onlyNFTOwner(_NFTAddr, _NFTId)
    {
        LibBusinessId.checkIn(_businessId);

        LibReentrancyGuard.nonReentrantBefore();
        {
            LibFactory.FactoryStorage storage fs = LibFactory.getStorage();

            uint256 originalAssetId = IAsset(_NFTAddr).originalAsset(_NFTId);

            NFTBaseInfo memory nftBaseInfo;
            originalAssetId == 0
                ? nftBaseInfo = IAsset(_NFTAddr).getNFTBaseInfo(_NFTId)
                : nftBaseInfo = IAsset(_NFTAddr).getNFTBaseInfo(originalAssetId);

            uint256 reserveAmount = _FTAmount;
            uint256 entryFeeAmount;
            uint256 royalty;
            uint256 royaltyFeeAmount;

            {
                entryFeeAmount = (fs.entryFeeRatio * _FTAmount) / 10000;
                reserveAmount -= entryFeeAmount;
                LibFactory.charge(msg.sender, fs.feeSplitter, nftBaseInfo.budgetToken, entryFeeAmount);
            }

            if (originalAssetId != 0) {
                address replicatedNFTOwner = IERC721Upgradeable(_NFTAddr).ownerOf(originalAssetId);
                royalty = (nftBaseInfo.royalties * _FTAmount) / 10000;
                reserveAmount -= royalty;
                royaltyFeeAmount = (fs.royaltyFeeRatio * royalty) / 10000;
                if (royaltyFeeAmount != 0) {
                    LibFactory.charge(msg.sender, fs.feeSplitter, nftBaseInfo.budgetToken, royaltyFeeAmount);
                }
                if (royalty - royaltyFeeAmount != 0) {
                    LibFactory.charge(
                        msg.sender, replicatedNFTOwner, nftBaseInfo.budgetToken, royalty - royaltyFeeAmount
                    );
                }
            }

            {
                address reserve = address(fs.reserve);
                LibFactory.charge(msg.sender, reserve, nftBaseInfo.budgetToken, reserveAmount);
                fs.record.add(true, _NFTAddr, _NFTId, nftBaseInfo.budgetToken, reserveAmount, reserve);
            }

            {
                if (msg.value > 0) {
                    fs.autoCoffer.deposit{value: msg.value}(_businessId, _NFTAddr, _NFTId);
                }
            }

            emit Deposit(
                _businessId,
                _NFTAddr,
                _NFTId,
                nftBaseInfo.budgetToken,
                reserveAmount,
                royalty - royaltyFeeAmount,
                royaltyFeeAmount,
                entryFeeAmount
            );
        }
        LibReentrancyGuard.nonReentrantAfter();
    }

    function withdraw(bytes32 _businessId, address _NFTAddr, uint256 _NFTId, uint256 _FTAmount)
        public
        onlyNFTOwner(_NFTAddr, _NFTId)
    {
        LibBusinessId.checkIn(_businessId);
        LibReentrancyGuard.nonReentrantBefore();
        {
            LibFactory.FactoryStorage storage fs = LibFactory.getStorage();

            uint256 originalAssetId = IAsset(_NFTAddr).originalAsset(_NFTId);
            NFTBaseInfo memory nftBaseInfo;
            originalAssetId == 0
                ? nftBaseInfo = IAsset(_NFTAddr).getNFTBaseInfo(_NFTId)
                : nftBaseInfo = IAsset(_NFTAddr).getNFTBaseInfo(originalAssetId);

            require(nftBaseInfo.budgetToken != address(0), "SCF: ZERO_BUDGET_TOKEN");
            fs.record.sub(true, _NFTAddr, _NFTId, nftBaseInfo.budgetToken, _FTAmount);
            fs.reserve.transfer(msg.sender, IERC20(nftBaseInfo.budgetToken), _FTAmount);
            emit Withdraw(_businessId, _NFTAddr, _NFTId, nftBaseInfo.budgetToken, _FTAmount, tx.origin);
        }
        LibReentrancyGuard.nonReentrantAfter();
    }

    function execOrder(address NFTAddr, uint256 NFTId, Order[] calldata orders) public onlyNFTOwner(NFTAddr, NFTId) {
        LibReentrancyGuard.nonReentrantBefore();
        {
            uint256 length = orders.length;
            for (uint256 i = 0; i < length;) {
                Order memory order = orders[i];
                _execOrder(NFTAddr, NFTId, order);
                unchecked {
                    ++i;
                }
            }
        }
        LibReentrancyGuard.nonReentrantAfter();
    }

    function autoExecModule(address NFTAddr, uint256 NFTId, Order calldata order) public AutoMate(NFTAddr, NFTId) {
        LibReentrancyGuard.nonReentrantBefore();
        {
            address module = IAsset(NFTAddr).getModuleAddress(NFTId);
            // 检查此次交易是否符合策略
            if (IModule(module).check(NFTAddr, NFTId, order)) {
                _execOrder(NFTAddr, NFTId, order);
            } else {
                // 如果不符合策略，直接回滚
                revert ModuleCheckFailed();
            }
        }
        LibReentrancyGuard.nonReentrantAfter();
    }

    function _execOrder(address NFTAddr, uint256 NFTId, Order memory order) internal {
        LibFactory.FactoryStorage storage fs = LibFactory.getStorage();
        require(fs.operatorResolver.isOperator(order.operator.addr), "SCF: INVALID_OPERATOR");

        {
            uint256 amount = fs.record.getTokenAmount(NFTAddr, NFTId, order.input.token, order.input.isBudget);
            require(amount >= order.input.amount, "SCF: INSUFFICIENT_INPUT");
            fs.reserve.withdraw(IERC20(order.input.token), order.input.amount);
            fs.record.sub(order.input.isBudget, NFTAddr, NFTId, order.input.token, order.input.amount);
        }

        bytes memory data = order.operator.addr.functionDelegateCall(order.operator.callData);
        (uint256[] memory amounts, address[] memory tokens) = abi.decode(data, (uint256[], address[]));

        // amountInMax
        if (order.input.amount != amounts[1]) {
            uint256 refundAmount = order.input.amount - amounts[1];
            LibFactory.charge(address(this), address(fs.reserve), order.input.token, refundAmount);
            fs.record.add(order.input.isBudget, NFTAddr, NFTId, order.input.token, refundAmount, address(fs.reserve));
        }

        {
            LibFactory.charge(address(this), address(fs.reserve), order.output.token, amounts[0]);
            fs.record.add(order.output.isBudget, NFTAddr, NFTId, order.output.token, amounts[0], address(fs.reserve));
        }

        emit OrderSwap(
            NFTAddr, NFTId, order.input.isBudget, tokens[1], amounts[1], order.output.isBudget, tokens[0], amounts[0]
        );
    }
}
