      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import {SafeERC20, IERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {IController} from "contracts/Diamond/interfaces/IController.sol";
import {IOperatorResolver} from "contracts/Diamond/interfaces/IOperatorResolver.sol";
import {IRecord} from "contracts/Diamond/interfaces/IRecord.sol";
import {IReserve} from "contracts/Diamond/interfaces/IReserve.sol";
import {IAutoCoffer} from "contracts/Diamond/interfaces/IAutomationCoffer.sol";

library LibFactory {
    using SafeERC20 for IERC20;

    bytes32 constant FACTORY_POSITION = keccak256("factory.storage");

    struct FactoryStorage {
        IController controller;
        IOperatorResolver operatorResolver;
        address feeSplitter;
        IRecord record;
        IReserve reserve;
        IAutoCoffer autoCoffer;
        address autoMeta;
        /// @dev Address of the contract that when create a NFT, it will charge a fee
        address createFeeToken;
        /// @dev Fees when NFT are created
        /// createFees is a fixed amount
        uint256 createFees;
        /// @dev Fees when NFT are replicated
        ///      From 1 to 10,000 (0.01% to 100%)
        uint256 replicateFeeRatio;
        /// @dev Fees when funds stay in portfolios
        ///      From 1 to 10,000 (0.01% to 100%)
        uint256 entryFeeRatio;
        /// @dev Fees when funds are withdrawed
        ///      From 1 to 10,000 (0.01% to 100%)
        uint256 exitFeeRatio;
        /// @dev Max royalty ratio
        ///      From 1 to 10,000 (0.01% to 100%)
        uint256 maxRoyaltyRatio;
        /// @dev The ratio of the royalty fee
        ///      From 1 to 10,000 (0.01% to 100%)
        uint256 royaltyFeeRatio;
        /// @dev The ratio of the investment fee
        ///      From 1 to 10,000 (0.01% to 100%)
        uint256 investmentFeeRatio;
    }

    function getStorage() internal pure returns (FactoryStorage storage fs) {
        bytes32 position = FACTORY_POSITION;
        assembly {
            fs.slot := position
        }
    }

    /**
     * _controller = someAddresses[0] The address of the controller
     * _operatorResolver = someAddresses[1] The address of the operator resolver
     * _feeSplitter = someAddresses[2] The address of the fee splitter
     * _record = someAddresses[3] The address of the record
     * _reserve = someAddresses[4] The address of the reserve
     * _autoMeta = someAddresses[5] The address of the auto meta
     * _autoCoffer = someAddresses[6] The address of the auto coffer
     * _createFeeToken = someAddresses[7]，The address of the create fee token
     * _createFees = someUint256s[0], The fees when NFT are created
     * _replicateFees = someUint256s[1], The fees when NFT are replicated
     * _entryFees = someUint256s[2], The fees when funds stay in budgets
     * _exitFees = someUint256s[3], The fees when funds are withdrawed from budgets
     * _maxRoyaltyRatio = someUint256s[4], The max royalty ratio
     * _royaltyFeeRatio = someUint256s[5], The ratio of the royalty fee
     * _investmentFeeRatio = someUint256s[6], The ratio of the investment fee
     */
    function setInitStatus(address[] calldata someAddresses, uint256[] calldata someUint256s) internal {
        require(someAddresses.length == 8, "SCF: INVALID_ADDRESSES_LENGTH");

        FactoryStorage storage fs = getStorage();
        fs.controller = IController(someAddresses[0]);
        fs.operatorResolver = IOperatorResolver(someAddresses[1]);
        fs.feeSplitter = someAddresses[2];
        fs.record = IRecord(someAddresses[3]);
        fs.reserve = IReserve(payable(someAddresses[4]));
        fs.autoMeta = someAddresses[5];
        fs.autoCoffer = IAutoCoffer(someAddresses[6]);
        fs.createFeeToken = someAddresses[7];
        fs.createFees = someUint256s[0];
        fs.replicateFeeRatio = someUint256s[1];
        fs.entryFeeRatio = someUint256s[2];
        fs.exitFeeRatio = someUint256s[3];
        fs.maxRoyaltyRatio = someUint256s[4];
        fs.royaltyFeeRatio = someUint256s[5];
        fs.investmentFeeRatio = someUint256s[6];
    }

    function charge(address _from, address _recipient, address _tokenAddr, uint256 _tokenAmount) internal {
        if (_tokenAmount > 0) {
            if (_tokenAddr == address(0)) {
                // never happen
                require(msg.value == _tokenAmount, "SCF: INVALID_ETH_VALUE");
                (bool success,) = payable(_recipient).call{value: _tokenAmount}("");
                if (!success) {
                    revert("SCF: FEE_SPLITTER_CALL_FAILED");
                }
            } else {
                uint256 balanceBefore = IERC20(_tokenAddr).balanceOf(address(_recipient));
                if (_from == address(this)) {
                    IERC20(_tokenAddr).safeTransfer(_recipient, _tokenAmount);
                } else {
                    IERC20(_tokenAddr).safeTransferFrom(_from, address(_recipient), _tokenAmount);
                }

                uint256 balanceAfter = IERC20(_tokenAddr).balanceOf(address(_recipient));
                if (balanceAfter != balanceBefore + _tokenAmount) {
                    revert("SCF: SAFE_TRANSFERFROM_Failed");
                }
            }
        }
    }
}
"
    },
