    function setFeeSplitter(address _feeSplitter) external;

    function feeRatio() external view returns (uint256);

    function feeSplitter() external view returns (address);

    function balanceOf(address NFTAddr, uint256 NFTId) external view returns (uint256);

    function deposit(bytes32 businessId, address NFTAddr, uint256 NFTId) external payable;

    function withdraw(bytes32 businessId, address NFTAddr, uint256 NFTId, uint256 amount, address payable receiver)
        external;
}
