// File: contracts/AnmolNFTV2.sol



pragma solidity ^0.8.0;






interface MouldFactoryNotify {
    function notifyIndexers(address from, address to, uint256 tokenId, uint256 mouldId) external;

    function treasury() external view returns (address);

    function anmolTokenAddress() external view returns (address);
}


    struct Layer {
        string name; /// name of the layer
        uint256 rarityPercentage; /// rarity percentage of the layer
    }

/** @title Contract AnmolNFTV2 inherits the functionality of ERC721 and ReentrancyGuard contracts */
contract AnmolNFTV2 is ERC721, ContextMixin, NativeMetaTransaction {

    string public baseTokenURI;

    uint256 public lifecycle;
    uint256 public ownerFeePerMint;
    uint256 public totalMinted = 0;
    uint256 public coprime = 117529;
    uint256 public random_value;
    uint256 public mouldId;

    bool public attributesSet;
    uint256 public mintType;
    address public factory;
    mapping(string => Layer[]) public attributes;

    /// event for logging change in the base token uri
    event URI(string uri);

    constructor(
        string memory _mouldName,
        string memory _symbol,
        uint256 _mintType,
        address _ownerAddress,
        uint256 _ownerFeePerMint,
        uint256 _lifecycle,
        uint256 _mouldId
    ) ERC721(_mouldName, _symbol) {
        require(_ownerAddress != address(0), "constructor: owner address cannot be null");
        baseTokenURI = string(abi.encodePacked("https://api.anmol.network/nft/", Strings.toString(_mouldId), "/"));
        lifecycle = _lifecycle;
        ownerFeePerMint = _ownerFeePerMint;
        mintType = _mintType;
        random_value = _randomNumber();
        mouldId = _mouldId;
        factory = msg.sender;
        _mint(_ownerAddress, 0);

    }

    modifier onlyFactory {
        require(msg.sender == factory);
        _;
    }

    modifier onlyMouldOwner {
        require(msg.sender == ownerOf(0));
        _;
    }


    /**
    * @dev validate attributes and stores them into attributes mapping
    * @param _attributeNames, array of attribute name
    * @param _attributesLayers, attribute layers based on each attribute in attributeNames array
    * @param _layersRarity, layers Rarity based on each attribute layer in attributesLayer array
    */
    function setAttributes(string[] memory _attributeNames, string[][] memory _attributesLayers, uint256[][] memory _layersRarity) external onlyFactory {
        require(_attributesLayers.length == _attributeNames.length, "setAttributes: input arrays length mismatch");
        require(_layersRarity.length == _attributeNames.length, "setAttributes: input arrays length mismatch");
        require(!attributesSet, "setAttributes: attributes already set");
        for (uint256 attributeIndex = 0; attributeIndex < _attributeNames.length; attributeIndex++) {
            require(_attributesLayers[attributeIndex].length == _layersRarity[attributeIndex].length, "setAttributes: input arrays length mismatch");
        }
        attributesSet = true;
        for (uint256 attributeIndex = 0; attributeIndex < _attributeNames.length; attributeIndex++) {
            string[] memory layers = _attributesLayers[attributeIndex];
            uint256[] memory rarity = _layersRarity[attributeIndex];
            for (uint256 layersIndex = 0; layersIndex < layers.length; layersIndex++) {
                attributes[_attributeNames[attributeIndex]].push(Layer(layers[layersIndex], rarity[layersIndex]));
            }
        }
    }

    /**
    * @dev update the newly owner fee per mint
    * @param _newOwnerFeePerMint, new owner fee per mint
    */
    function setOwnerFeePerMint(uint256 _newOwnerFeePerMint) external onlyMouldOwner {
        ownerFeePerMint = _newOwnerFeePerMint;
    }

    /**
    * @dev update base Token URI
    * @param _uri, new uri
    */
    function setURI(string memory _uri) external onlyMouldOwner {
        baseTokenURI = _uri;
        emit URI(_uri);
    }


    function mintBatch(uint256 quantity, uint256[] memory _tokens, address sender) public onlyFactory {
        require(quantity + totalMinted <= lifecycle, "mint: quantity exceeds the mint_limit");
        if (mintType == 1) {
            require(_tokens.length != 0, "mint: tokens array cannot be empty for sequential mint");
            require(_tokens.length == quantity, "mint: length of token array should be equal to quantity");
            require(_validateTokenIds(_tokens), "mint: invalid token id in the array");
            _mintSequentially(_tokens, sender);
        } else {
            require(_tokens.length == 0, "mint: tokens array should be empty for random minting");
            _mintRandomly(quantity, sender);
        }
    }
    /**
    * @dev mint nft sequentially
    * @param _tokens, array of token nfts that to be minted
    */
    function _mintSequentially(uint256[] memory _tokens, address dest) internal {
        for (uint256 i = 0; i < _tokens.length; i++) {
            totalMinted++;
            _mint(dest, _tokens[i]);
        }
    }

    /**
    * @dev mint nft by picking random token value
    * @param _quantity, number of nfts to be minted
    */
    function _mintRandomly(uint256 _quantity, address dest) internal {
        for (uint256 i = 0; i < _quantity; i++) {
            totalMinted++;
            _mint(dest, _getTokenId());
        }
    }


    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 tokenId
    ) internal override {
        MouldFactoryNotify(factory).notifyIndexers(from, to, tokenId, mouldId);
    }

    /**
    * @dev validate token IDs in the given array
    * @param _tokenIds, array of token Ids to be validated
    * @return areValid, if all tokenIds in the array are valid then true, else false
    */
    function _validateTokenIds(uint256[] memory _tokenIds) internal view returns (bool) {
        bool areValid = true;
        for (uint256 i = 0; i < _tokenIds.length; i++) {
            if (_tokenIds[i] > lifecycle || _tokenIds[i] == 0 || _exists(_tokenIds[i])) {
                areValid = false;
                break;
            }
        }

        return areValid;
    }

    /**
    * @dev generate the tokenIds in random order
    * @return randomNFT, return the generated random nft for mint
    */
    function _getTokenId() internal view returns (uint256)  {
        uint256 randomNFT = ((coprime * totalMinted) + random_value) % lifecycle;
        if (randomNFT == 0) {
            randomNFT = lifecycle;
        }

        return randomNFT;
    }

    /**
    * @dev generate the random number through block.difficulty block.timestamp block.coinbase and taking mod with coprime
    * @return randomNumber, generated random number
    */
    function _randomNumber() internal view returns (uint256) {
        uint256 randomHash = uint256(keccak256(abi.encodePacked(block.difficulty, block.timestamp, block.coinbase)));
        return randomHash % coprime;
    }

    /**
    * @dev get the base token uri
    * @return baseTokenURI, base token uri
     */
    function _baseURI() internal view override returns (string memory) {
        return baseTokenURI;
    }

    function tokenURI(uint256 tokenId) public view virtual override returns (string memory) {
        _requireMinted(tokenId);

        string memory baseURI = _baseURI();
        return bytes(baseURI).length > 0 ? string(abi.encodePacked(baseURI, Strings.toString(tokenId))) : "";
    }

    function isApprovedForAll(
        address _owner,
        address _operator
    ) public override view returns (bool isOperator) {
        // if OpenSea's ERC721 Proxy Address is detected, auto-return true
        // for Polygon's Mumbai testnet, use 0xff7Ca10aF37178BdD056628eF42fD7F799fAc77c
        if (_operator == address(0x58807baD0B376efc12F5AD86aAc70E78ed67deaE)) {
            return true;
        }

        // otherwise, use the default ERC721.isApprovedForAll()
        return ERC721.isApprovedForAll(_owner, _operator);
    }
    /**
    * This is used instead of msg.sender as transactions won't be sent by the original token owner, but by OpenSea.
    */
    function _msgSender()
    internal
    override
    view
    returns (address sender)
    {
        return ContextMixin.msgSender();
    }
}
