        bytes32 keyHash;
    }

    // see https://docs.chain.link/docs/vrf-contracts/#configurations
    bytes32 private keyHash;
    VRFCoordinatorV2Interface private coordinator;
    uint64 public subscriptionId;

    //additional payment for chainlink services
    uint public feePerGas;
    address payable public paymentReceiver;

    constructor(
        address payable paymentReceiver_,
        ChainlinkParams memory params_
    ) VRFConsumerBaseV2(params_.coordinator) {
        paymentReceiver = paymentReceiver_;

        coordinator = VRFCoordinatorV2Interface(params_.coordinator);
        keyHash = params_.keyHash;
        subscriptionId = params_.subscriptionId;
    }


    function setPaymentReciever(
        address payable newAddress
    ) external onlyGovernance {
        paymentReceiver = newAddress;
    }

    function setSubscriptionId(uint64 newId) external onlyGovernance {
        subscriptionId = newId;
    }

    function setFeePerGas(uint newValue) external onlyGovernance {
        feePerGas = newValue;
    }

    function requestRandomWords(uint32 gas) internal virtual returns (uint id) {
        uint gasFee = feePerGas;
        if (gasFee > 0) {
            Address.sendValue(paymentReceiver, gasFee*gas);
        }

        // Will revert if subscription is not set and funded.
        id = coordinator.requestRandomWords(
            keyHash,
            subscriptionId,
            5,
            gas,
            1
        );
    }

    function fulfillRandomWords(
        uint256 requestId,
        uint256[] memory randomWords
    ) internal virtual override;
}
"
    },
    "common-contracts/contracts/ERC721Warden.sol": {
      "content": "//SPDX-License-Identifier: AGPL-3.0-only
pragma solidity >=0.8.0;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "./Interfaces/ITokenWarden.sol";

abstract contract ERC721Warden is ERC721 {
    address public tokenWarden;

    function _setTokenWarden(address newWarden) internal {
        tokenWarden = newWarden;
    }

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 firstTokenId,
        uint256 batchSize
    ) internal virtual override {
        super._beforeTokenTransfer(from, to, firstTokenId, batchSize);
        if(tokenWarden != address(0) && !ITokenWarden(tokenWarden).transferPermitted(from, to, firstTokenId)){
            revert("Not permitted to transfer");
        }
    }
}"
