    "common-contracts/contracts/Tokens/ERC4907Collection.sol": {
      "content": "// SPDX-License-Identifier: AGPL-3.0-only
pragma solidity >=0.8.0;
import "../Interfaces/IERC4907.sol";
import "@openzeppelin/contracts/token/ERC721/ERC721.sol";

abstract contract ERC4907Collection is IERC4907, ERC721 {

    struct LendInfo{
        address user;
        uint64 expireTime;
    }

    mapping (uint => LendInfo) private lendInfo;

    function setUser(uint256 tokenId, address user, uint64 expires) external override virtual{
        address owner = ownerOf(tokenId);

        require(owner == msg.sender || isApprovedForAll(owner, msg.sender), "NOT_AUTHORIZED");
        require(userOf(tokenId) == address(0), "ALREADY_IN_RENT");

        lendInfo[tokenId] = LendInfo(user, expires);

        emit UpdateUser(tokenId, user, expires);
    }

    function userOf(uint256 tokenId) public override virtual view returns(address){
        LendInfo memory lendUser = lendInfo[tokenId];

        if(lendUser.expireTime < block.timestamp){
            return address(0);
        }

        return lendUser.user;
    }

    function userExpires(uint256 tokenId) external override virtual view returns(uint256){
        uint64 expireTime = lendInfo[tokenId].expireTime;

        if(expireTime < block.timestamp){
            return 0;
        }

        return expireTime;
    }
}"
