    "contracts/Robots/EquipUpgrade.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import "./RobotsEquipment.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "common-contracts/contracts/Governable.sol";
import "common-contracts/contracts/CompensatedRandom.sol";

contract EquipUpgrade is CompensatedRandom{
    struct UpgradeInfo{
        uint32 upgradeId;
        uint32 burnedId;
        uint32 newId;
        uint16 chance;
        address sender;
        UpgradeStatus status;
    }

    enum UpgradeStatus{
        None,
        Pending,
        Success,
        Fail
    }

    event UpgradeTry(address indexed user, uint requestId, uint32 upgradeId, uint32 burnedId);
    event UpgradeResult(address indexed user, uint requestId, bool success);

    RobotsEquipment public equip;
    address public signerAddress;
    uint32 public constant GAS_LIMIT = 210000;
    mapping(uint => address) public requests;
    mapping(address => UpgradeInfo) public lastUpgrade;

    constructor(
        RobotsEquipment _equip, 
        address _signerAddress, 
        address payable paymentReceiver,
        ChainlinkParams memory params
    ) CompensatedRandom(paymentReceiver, params){
        signerAddress = _signerAddress;
        equip = _equip;
    }

    function upgrade(uint32 upgradeId, uint32 burnedId, uint32 newId, uint16 chanceBps, uint expireTime, bytes memory signature) external payable { 
        bytes32 upgradeHash = keccak256(abi.encode(upgradeId, burnedId, newId, chanceBps, expireTime));

        //since we burn at least 1 of these ids, there is no possibility of replay
        require(expireTime > block.timestamp, "upgrade: signature is expired");
        require(lastUpgrade[msg.sender].status != UpgradeStatus.Pending, "upgrade: already pending");
        require(recoverSigner(signature, upgradeHash) == signerAddress, "upgrade: not approved");
        require(equip.ownerOf(upgradeId) == msg.sender, "upgrade: not owner of upgradeId");
        require(equip.ownerOf(burnedId) == msg.sender, "upgrade: not owner of burnedId");

        equip.transferFrom(msg.sender, address(this), upgradeId);
        equip.burn(burnedId);

        uint requestId = requestRandomWords(GAS_LIMIT);
        requests[requestId] = msg.sender;
        lastUpgrade[msg.sender] = UpgradeInfo(upgradeId, burnedId, newId, chanceBps, msg.sender, UpgradeStatus.Pending);

        emit UpgradeTry(msg.sender, requestId, upgradeId, burnedId);
    }


    function setSignerAddress(address newAddress) external onlyGovernance{
        signerAddress = newAddress;
    }

    /**
     * @dev return address that signed bytes32 hash
     *
     * @param signature 65 bytes of signature
     * @param hash hash that signed
     */
    function recoverSigner(
        bytes memory signature,
        bytes32 hash
    ) internal pure returns (address) {
        return ECDSA.recover(ECDSA.toEthSignedMessageHash(hash), signature);
    }

    function forceFulfill(uint requestId) external onlyGovernance{
        uint[] memory randomWords = new uint[](1);
        randomWords[0] = uint(blockhash(block.number - 1));
        fulfillRandomWords(requestId, randomWords);
    }


    function fulfillRandomWords(
        uint256 requestId,
        uint256[] memory randomWords
    ) internal override{
        UpgradeInfo memory info = lastUpgrade[requests[requestId]];
        require(info.status == UpgradeStatus.Pending, "fulfillRandomWords: not pending");

        bool success = randomWords[0] % 10000 <  info.chance;
        if(success){
            equip.burn(info.upgradeId);
            equip.mint(info.sender, info.newId);
        }
        else{
            equip.transferFrom(address(this), info.sender, info.upgradeId);
        }

        lastUpgrade[info.sender].status = success ? UpgradeStatus.Success : UpgradeStatus.Fail;
        delete requests[requestId];

        emit UpgradeResult(info.sender, requestId, success);
    }

}"
    },
    "contracts/Robots/RobotsEquipment.sol": {
      "content": "// SPDX-License-Identifier: AGPL-3.0-only
pragma solidity >=0.8.0;

import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Enumerable.sol";
import "common-contracts/contracts/Tokens/ERC4907Collection.sol";
import "common-contracts/contracts/Governable.sol";
import "common-contracts/contracts/ERC721Warden.sol";
import "common-contracts/contracts/Royalty.sol";

contract RobotsEquipment is
    ERC721Enumerable,
    ERC4907Collection,
    ERC721Warden,
    Royalty,
    Governable
{
    string public baseURI;

    constructor() ERC721("Robots Equipment", "DRE") {}

    function mint(address to, uint id) external onlyGovernance {
        _mint(to, id);
    }

    function mintBatch(
        address to,
        uint32[] calldata ids
    ) external onlyGovernance {
        for (uint i; i < ids.length; ) {
            _mint(to, ids[i]);

            unchecked {
                ++i;
            }
        }
    }

    function setTokenWarden(address newWarden) external onlyOwner {
        _setTokenWarden(newWarden);
    }

    function setBaseURI(string memory _uri) external onlyOwner {
        baseURI = _uri;
    }

    function setFees(
        address payable[] calldata _feeRecipients,
        uint16[] calldata _feeBps
    ) external onlyOwner {
        _setFees(_feeRecipients, _feeBps);
    }

    function burn(uint id) external {
        address owner = ownerOf(id);
        require(msg.sender == owner || isApprovedForAll(owner, msg.sender));
        _burn(id);
    }

    function supportsInterface(
        bytes4 interfaceId
    ) public view override(ERC721Enumerable, ERC721, Royalty) returns (bool) {
        return
            interfaceId == type(IERC4907).interfaceId ||
            super.supportsInterface(interfaceId);
    }

    function _baseURI() internal view override returns (string memory) {
        return baseURI;
    }

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 firstTokenId,
        uint256 batchSize
    ) internal virtual override(ERC721, ERC721Enumerable, ERC721Warden) {
        super._beforeTokenTransfer(from, to, firstTokenId, batchSize);
    }
}
