      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

import "ERC721Enumerable.sol";
import "Ownable.sol";
import "LinkTokenInterface.sol";
import "VRFCoordinatorV2Interface.sol";
import "VRFConsumerBaseV2.sol";

contract PepeLamboClub is ERC721Enumerable, Ownable, VRFConsumerBaseV2 {

	using Strings for uint256;

    event RequestSent(
		uint256 requestId,
		address forUser,
		uint8 numberMinted
	);
    event RequestFulfilled(
		uint256 requestId,
		address forUser,
		uint8 numberMinted,
		uint256 withRandomNumber
	);

	uint256 public maxSupply;
	uint256[] private ids;
	uint256 public price; 
	uint256 public whitelistMintPrice = 0; 
	address public ancapSpain = 0x790C496919C9C1fD94d739c9f08CD1416d41D9fA;

	uint8 public maxMintsPerBatch = 10;

	bool public mintPaused = true;


	/* Chainlink subscription vars */
	address private vrfCoordinator;
	address private link;
	VRFCoordinatorV2Interface private vrfCoordinatorContract;
	LinkTokenInterface private linktoken;

	bytes32 private keyHash;
	uint32 private callbackGasLimit;
	uint64 private subId;

	mapping(uint256 => address) private fromRequestIdToAddress;
	mapping(uint256 => uint8) private fromRequestIdToNumMinting;


    constructor(
        string memory _name,
        string memory _symbol,
		uint256 _maxSupply, 
		uint256 _price,
		address _vrfCoordinator,
		address _link
	) ERC721(_name, _symbol) VRFConsumerBaseV2(_vrfCoordinator) { 
		maxSupply = _maxSupply;
		price = _price;

		vrfCoordinatorContract = VRFCoordinatorV2Interface(_vrfCoordinator);
		linktoken = LinkTokenInterface(_link);
	}

	/**
	 * @dev should be called during deployment
	 */
	function configureSubscription(
		bytes32 _keyHash,
		uint32 _callbackGasLimit,
		uint64 _subId
	) public onlyOwner {
		keyHash = _keyHash;
		callbackGasLimit = _callbackGasLimit;
		subId = _subId;
	}


	// Minting logic

	function ownerMint(uint256 idIndex) public onlyOwner {
        _safeMint(msg.sender, ids[idIndex]);
		removeFromIdsIndex(idIndex);
	}

	function mint(uint8 _mintAmount) public payable {
		mintRequirementsAndWlUpdate(_mintAmount);

		for (uint8 i = 0; i < _mintAmount/maxMintsPerBatch; i++ )
			_mintBatch(msg.sender, maxMintsPerBatch);

		uint8 extramints = _mintAmount % maxMintsPerBatch;
		if (extramints > 0)
			_mintBatch(msg.sender, extramints);
    }

	function mintRequirementsAndWlUpdate(uint8 _mintAmount) private {
        require(!mintPaused, "Sale must be active to mint tokens");
        require(_mintAmount > 0, "Mint amount must be greater than zero");
        require(ids.length >= _mintAmount, "Mint amount exceeds max supply");

		uint256 wls = whitelist[msg.sender];
		if(wls >= _mintAmount) {
			require(
				msg.value >= whitelistMintPrice * _mintAmount, 
				"You have to pay the token price!"
			);
			whitelist[msg.sender] -= _mintAmount;
		} else {
			require(
				msg.value >= (price * (_mintAmount - wls)) + (whitelistMintPrice * wls),
				"You have to pay the token price!"
			);
			whitelist[msg.sender] = 0;
		}
	}

	function _mintBatch(
		address minter,
		uint8 amountMinting
	) private {
		uint256 requestId = vrfCoordinatorContract.requestRandomWords(
			keyHash, subId, 3, callbackGasLimit, 1
		);
		fromRequestIdToAddress[requestId] = minter;
		fromRequestIdToNumMinting[requestId] = amountMinting;
		emit RequestSent(requestId, msg.sender, amountMinting);
	}

	function fulfillRandomWords(
		uint256 requestId, uint256[] memory randomWords
	) internal override {
		uint256 random = randomWords[0];
		address addrToFulfill = fromRequestIdToAddress[requestId];
		uint8 numToMint = fromRequestIdToNumMinting[requestId];

		for(uint8 i = 0; i < numToMint; i++) {
			uint256 indexToMint = random % ids.length;
        	_safeMint(addrToFulfill, ids[indexToMint]);
			removeFromIdsIndex(indexToMint);
		}

		delete fromRequestIdToAddress[requestId];
		delete fromRequestIdToNumMinting[requestId];
		emit RequestFulfilled(requestId, addrToFulfill, numToMint, random);
	}

	function removeFromIdsIndex(uint256 i) private {
		ids[i] = ids[ids.length - 1];
		ids.pop();
	}

	bool private idsLocked = false; 

	/**
	 * @dev should be called during deployment
	 */
	function addIds(uint256[] calldata newids) public onlyOwner {
		require(newids.length + ids.length <= maxSupply && !idsLocked);
		for (uint256 i = 0; i < newids.length; i++) {
			ids.push(newids[i]);
		}
		if(ids.length == maxSupply) idsLocked = true;
	}


	// Metadata Logic

	string public baseURI;
	string public baseExtension = ".json";
	string public notRevealedUri;

	bool public tokensAlreadyRevealed = false;

	function tokenURI(uint256 tokenId) 
		public 
		view 
		virtual 
		override 
		returns (string memory)
	{
		if(!tokensAlreadyRevealed)
			return notRevealedUri;

    	return bytes(baseURI).length > 0 ? string(abi.encodePacked(
			baseURI, tokenId.toString(), baseExtension
		)) : "";
	}


	// WL logic

	mapping (address => uint256) private whitelist;

	function setWhitelist(
		address[] calldata addresses, uint256 numAllowedToMintWithWL 
	) external onlyOwner {
		for (uint256 i = 0; i < addresses.length; i++) {
			whitelist[addresses[i]] = numAllowedToMintWithWL;
		}
	}

	function whitelistSpotsAvailable(address addr) 
		external
		view
		returns (uint256) 
	{
		return whitelist[addr];
    }

	function setWhitelistPrice(uint256 newprice) public onlyOwner {
		whitelistMintPrice = newprice;
	}


	// General contract state manipulation 

	function setNotRevealedURI(
		string memory _notRevealedURI
	) public onlyOwner {
        notRevealedUri = _notRevealedURI;
	}

	function setBaseURI(string memory _newBaseURI) public onlyOwner {
		baseURI = _newBaseURI;
	}

	function setBaseExtension(
		string memory _newBaseExtension
	) public onlyOwner {
		baseExtension = _newBaseExtension;
	}

	function switchPause() public onlyOwner {
		mintPaused = !mintPaused;
	}

	function switchRevealed() public onlyOwner {
		tokensAlreadyRevealed = !tokensAlreadyRevealed;
	}

	function withdraw() public onlyOwner {
		require(payable(ancapSpain).send(address(this).balance));
	}

	function changeDev(address newDev) public onlyOwner {
		ancapSpain = newDev;
	}

	function getIds() public view returns (uint256[] memory) {
		return ids;
	}

	function getIds(uint256 index) public view returns (uint256) {
		return ids[index];
	}

	function getIdsLen() public view returns (uint256) {
		return ids.length;
	}

	function setMaxMintsPerBatch(uint8 max) public onlyOwner {
		require(max > 0);
		maxMintsPerBatch = max;
	}

	function setPrice(uint256 newprice) public onlyOwner {
		price = newprice;
	}

}
"
