contract BatchTransfer is Ownable {

    function batchTransfer(address[] calldata accounts, uint256[] calldata amounts) external payable {
        require(amounts.length == accounts.length, "BatchTransfer: addresses number should equal to amounts");
        uint256 length = accounts.length;
        uint256 totalAmount;

        for(uint256 i; i < length; i++) {
            sendValue(payable (accounts[i]), amounts[i]);
            totalAmount += amounts[i];
        }

        require(totalAmount == msg.value, "BatchTransfer: too much ether received");

    }

    function batchTransferERC20(IERC20 token, address[] calldata accounts, uint256[] calldata amounts) external {
        require(amounts.length == accounts.length, "BatchTransfer: addresses number should equal to amounts");
        uint256 length = accounts.length;

        for(uint256 i; i < length; i++) {
            safeTransferFrom(token, msg.sender, accounts[i], amounts[i]);
        }
    }

    function emergencyWithdraw() external onlyOwner {
        sendValue(payable(owner()), address(this).balance);
    }

    function safeTransferFrom(IERC20 token, address from, address to, uint256 value) internal {
