      "content": "pragma solidity ^0.8.9;
// SPDX-License-Identifier: MIT

struct SignedCSVoucher {
    CSVoucher message;
    bytes signature;
    address signer;
}

bytes32 constant signedcsvoucherTypehash = keccak256(
    "SignedCSVoucher(CSVoucher message,bytes signature,address signer)CSVoucher(uint256 id,uint256 amount,address minter)"
);

struct EIP712Domain {
    string name;
    string version;
    uint256 chainId;
    address verifyingContract;
}

bytes32 constant eip712domainTypehash = keccak256(
    "EIP712Domain(string name,string version,uint256 chainId,address verifyingContract)"
);

struct CSVoucher {
    uint256 id;
    uint256 amount;
    address minter;
}

bytes32 constant csvoucherTypehash = keccak256(
    "CSVoucher(uint256 id,uint256 amount,address minter)"
);

abstract contract ERC1271Contract {
    /**
     * @dev Should return whether the signature provided is valid for the provided hash
     * @param _hash      Hash of the data to be signed
     * @param _signature Signature byte array associated with _hash
     *
     * MUST return the bytes4 magic value 0x1626ba7e when function passes.
     * MUST NOT modify state (using STATICCALL for solc < 0.5, view modifier for solc > 0.5)
     * MUST allow external calls
     */
    function isValidSignature(
        bytes32 _hash,
        bytes memory _signature
    ) public view virtual returns (bytes4 magicValue);
}

abstract contract EIP712Decoder {
    function getDomainHash() internal view virtual returns (bytes32);

    /**
     * @dev Recover signer address from a message by using their signature
     * @param hash bytes32 message, the hash is the signed message. What is recovered is the signer address.
     * @param sig bytes signature, the signature is generated using web3.eth.sign()
     */
    function recover(
        bytes32 hash,
        bytes memory sig
    ) internal pure returns (address) {
        bytes32 r;
        bytes32 s;
        uint8 v;

        // Check the signature length
        if (sig.length != 65) {
            return (address(0));
        }

        // Divide the signature in r, s and v variables
        assembly {
            r := mload(add(sig, 32))
            s := mload(add(sig, 64))
            v := byte(0, mload(add(sig, 96)))
        }
        // Version of signature should be 27 or 28, but 0 and 1 are also possible versions
        if (v < 27) {
            v += 27;
        }

        // If the version is correct return the signer address
        if (v != 27 && v != 28) {
            return (address(0));
        } else {
            return ecrecover(hash, v, r, s);
        }
    }

    function getSignedcsvoucherPacketHash(
        SignedCSVoucher memory _input
    ) internal pure returns (bytes32) {
        bytes memory encoded = abi.encode(
            signedcsvoucherTypehash,
            getCsvoucherPacketHash(_input.message),
            keccak256(_input.signature),
            _input.signer
        );
        return keccak256(encoded);
    }

    function getEip712DomainPacketHash(
        EIP712Domain memory _input
    ) internal pure returns (bytes32) {
        bytes memory encoded = abi.encode(
            eip712domainTypehash,
            keccak256(bytes(_input.name)),
            keccak256(bytes(_input.version)),
            _input.chainId,
            _input.verifyingContract
        );
        return keccak256(encoded);
    }

    function getCsvoucherPacketHash(
        CSVoucher memory _input
    ) internal pure returns (bytes32) {
        bytes memory encoded = abi.encode(
            csvoucherTypehash,
            _input.id,
            _input.amount,
            _input.minter
        );
        return keccak256(encoded);
    }

    function verifySignedCSVoucher(
        SignedCSVoucher memory _input
    ) internal view returns (address) {
        bytes32 packetHash = getCsvoucherPacketHash(_input.message);
        bytes32 digest = keccak256(
            abi.encodePacked("\x19\x01", getDomainHash(), packetHash)
        );

        if (_input.signer == 0x0000000000000000000000000000000000000000) {
            address recoveredSigner = recover(digest, _input.signature);
            return recoveredSigner;
        } else {
            // EIP-1271 signature verification
            bytes4 result = ERC1271Contract(_input.signer).isValidSignature(
                digest,
                _input.signature
            );
            require(result == 0x1626ba7e, "INVALID_SIGNATURE");
            return _input.signer;
        }
    }
}
