// SPDX-License-Identifier: MIT
pragma solidity ^0.8.18;

import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";

/**
 * @author  Alejandro Garrido - Guardians of the Ball.
 * @title   Guardians of the Ball - GOBValidCurrencies.
 * @dev     Helper to manage a list of accepted currencies in inherited contracts.
 * Child contracts can call _addCurrency(), _removeCurrency(), and use the modifier validCurrency().
 * They can also access the list in the internal AddressSet `_currencies`.
 */

abstract contract GOBValidCurrencies {
	using EnumerableSet for EnumerableSet.AddressSet;

	/**
	 * @dev Emitted when new valid currency is added.
	 */
	event CurrencyRemoved(address indexed newCurrency);

	/**
	 * @dev Emitted when a valid currency is removed.
	 */
	event CurrencyAdded(address indexed newCurrency);

	modifier validCurrency(address currency) {
		require(_currencies.contains(currency), "GOBValidCurrencies: Invalid currency");
		_;
	}

	// Accepted currencies.
	EnumerableSet.AddressSet internal _currencies;

	/**
	 * @notice Returns all ERC20 accepted as payment.
	 * @return Array with all ERC20 addresses.
	 */
	function validCurrencies() external view returns (address[] memory) {
		return _currencies.values();
	}

	/**
	 * @notice Check if a currency is listed.
	 * @param currency The ERC20 address.
	 * @return Whether the ERC20 is listed or not.
	 */
	function isValidCurrency(address currency) external view returns (bool) {
		return _currencies.contains(currency);
	}

	/**
	 * @notice Adds a new currency to the list of ERC20s accepted.
	 *
	 * Emits {CurrencyAdded}.
	 *
	 * @param newCurrency ERC20 address (ie: usdc).
	 */
	function _addCurrency(address newCurrency) internal {
		require(newCurrency != address(0), "GOBValidCurrencies: Zero address");
		require(!_currencies.contains(newCurrency), "GOBValidCurrencies: Currency already exists");
		_currencies.add(newCurrency);
		emit CurrencyAdded(newCurrency);
	}

	/**
	 * @notice Removes an ERC20 from the accepted list of currencies.
	 *
	 * Emits {CurrencyRemoved}.
	 *
	 * @param currency ERC20 address.
	 */
	function _removeCurrency(address currency) internal validCurrency(currency) {
		require(_currencies.length() > 1, "GOBValidCurrencies: Only one currency left");
		_currencies.remove(currency);
		emit CurrencyRemoved(currency);
	}
}
"
    },
