pragma solidity ^0.8.18;

import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/Context.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";

/**
 * @author  Matias Arazi - Guardians of the Ball.
 * @title   Guardians of the Ball - GOBWithdraw.
 * @dev     Helper to manage withdraw functions in inherited contracts.
 * Child contracts can call _withdraw(), _withdrawSet().
 */

abstract contract GOBWithdraw is Context {
	using EnumerableSet for EnumerableSet.AddressSet;
	using SafeERC20 for IERC20;

	/**
	 * @dev Emitted when funds are withdrawn from the contract for a specific tokenAddress.
	 * @param callerAddress The address of the caller who initiated the withdrawal.
	 * @param tokenAddress The address of the tokenAddress from which funds are withdrawn.
	 * @param amount The amount of tokenAddress withdrawn.
	 */
	event Withdrawn(address indexed callerAddress, address indexed tokenAddress, uint256 amount);

	/**
	 * @notice Withdraw funds from the contract for a specific tokenAddress.
	 * @param token The address of the tokenAddress to withdraw from.
	 * @param amount The amount of tokenAddress withdrawn.
	 *
	 * Requirements:
	 * - The token address must be a valid ERC20 token. (address must be an erc20)
	 */
	function _withdraw(address token, uint256 amount) internal {
		IERC20 currency = IERC20(token);
		currency.safeTransfer(msg.sender, amount);
		emit Withdrawn(_msgSender(), token, amount);
	}

	/**
	 * @notice Withdraw funds from the contract for multiple token addresses.
	 * @param tokenSet AddressSet of tokens in storage.
	 * @return isWithdrawn Withdrawn at least one of the tokens in `tokenSet`
	 */
	function _withdrawSet(
		EnumerableSet.AddressSet storage tokenSet
	) internal returns (bool isWithdrawn) {
		uint256 balance;
		uint256 len = tokenSet.length();
		IERC20 currency;

		for (uint256 i = 0; i < len; ) {
			currency = IERC20(tokenSet.at(i));
			balance = currency.balanceOf(address(this));
			if (balance > 0) {
				_withdraw(address(currency), balance);
				isWithdrawn = true;
			}
			unchecked {
				i++;
			}
		}
	}
}
"
