pragma solidity ^0.8.18;

/**
 * @title Guardians of the Ball - IOTCController
 * @author Guardians of the Ball
 * @notice OTCController contract Interface
 */

interface IOTCController {
	/**
	 * @dev Emitted when the price for purchasing tokens is set.
	 * @param callerAddress The address of the caller who set the price.
	 * @param price The new price in stablecoins.
	 */
	event PriceSetted(address indexed callerAddress, uint256 price);

	/**
	 * @dev Emitted when the token sale is opened for purchases.
	 */
	event SaleOpened();

	/**
	 * @dev Emitted when the token sale is closed, preventing further purchases.
	 */
	event SaleClosed();

	/**
	 * @dev Returns if sales are open or not.
	 */
	function isSaleOpen() external view returns (bool);

	/**
	 * @notice Purchase tokens using a stablecoin.
	 * @param _stablecoin The address of the stablecoin to use for the purchase.
	 * @param amount The amount of stablecoins to exchange for tokens.
	 *
	 * Requirements:
	 * - The stablecoin address must be valid.
	 * - The caller must pay with a valid stablecoin.
	 */
	function buyTokens(address _stablecoin, uint256 amount) external;
}
