// SPDX-License-Identifier: MIT
pragma solidity ^0.8.18;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";

import "../../base/GOBValidCurrencies/GOBValidCurrencies.sol";
import "../../base/GOBWithdraw/GOBWithdraw.sol";
import "../vesting/Vesting.sol";
import "./IOTCController.sol";

/**
 * @title Guardians of the Ball - OTC_Controller
 * @author Matias Arazi - Guardians of the Ball
 * @notice It is a smart contract designed to sell a token in the OTC phase using stablecoins of the dollar.
 */
contract OTCController is IOTCController, AccessControl, GOBValidCurrencies, GOBWithdraw {
	using SafeERC20 for IERC20Metadata;
	using EnumerableSet for EnumerableSet.AddressSet;

	// Flags to manage the sale state
	bool public isSaleOpen = false;
	bool public alreadyOpen = false;

	// Role constants
	bytes32 public constant SALES_TRIGGERER_ROLE = keccak256("SALES_TRIGGERER_ROLE");
	bytes32 public constant PRICE_MANAGER_ROLE = keccak256("PRICE_MANAGER_ROLE");
	bytes32 public constant WITHDRAW_ROLE = keccak256("WITHDRAW_ROLE");

	// Decimal precision for token amounts
	uint256 public immutable decimals;

	// Addresses and contracts
	address public vesting;
	address public immutable fundingWallet;
	IERC20Metadata public immutable sellingToken;
	uint256 public price;

	/**
	 * @dev Emitted when tokens are sold to a beneficiary using a stablecoin.
	 * @param beneficiary The address of the beneficiary who purchased tokens.
	 * @param stablecoinAddress The address of the stablecoin used for the purchase.
	 * @param amount The amount of tokens purchased.
	 */
	event Sold(address indexed beneficiary, address indexed stablecoinAddress, uint256 amount);

	/**
	 * @dev Modifier to check if the sale is open.
	 * This modifier verifies whether the `isSaleOpen` flag is true before allowing the function to proceed.
	 * If the sale is not open, the function call will be reverted.
	 */
	modifier saleIsOpen() {
		require(isSaleOpen, "OTCController: Sales are closed");
		_;
	}

	/**
	 * @notice Constructor to initialize the contract.
	 * @param decimals_ Decimal precision for token price.
	 * @param fundingWalletAddress_ Address of the wallet containing funds for sales.
	 * @param sellingTokenAddress_ Address of the token to be sold.
	 *
	 * Requirements:
	 * - The stablecoins address must be unique in the list.
	 * - The stablecoins address decimals MUST be less than 'decimals_'.
	 */
	constructor(
		address[] memory stablecoins_,
		uint256 decimals_,
		address fundingWalletAddress_,
		address sellingTokenAddress_
	) {
		sellingToken = IERC20Metadata(sellingTokenAddress_);

		// Cannot be greater than 6 to be able to work with USDT and USDC
		require(decimals_ <= 6, "Price decimals must be <= 6");
		require(
			decimals_ <= sellingToken.decimals(),
			"Price decimals must be <= sellingToken decimals"
		);

		// Assign the contract deployer as the default admin role
		_grantRole(DEFAULT_ADMIN_ROLE, _msgSender());

		decimals = decimals_;
		fundingWallet = fundingWalletAddress_;

		// Add the initial stablecoins to the set
		for (uint256 i = 0; i < stablecoins_.length; ) {
			require(!_currencies.contains(stablecoins_[i]), "Currency already added");
			_addCurrencyDec(stablecoins_[i]);
			unchecked {
				++i;
			}
		}
	}

	/**
	 * @notice Check if the sale is available.
	 * @return A boolean indicating whether fundingWallet has funds and has allowance.
	 */
	function checkAvailability() external view returns (bool) {
		uint256 minPurchase = 10 ** (sellingToken.decimals() - decimals);
		return
			sellingToken.balanceOf(fundingWallet) > minPurchase &&
			sellingToken.allowance(fundingWallet, address(this)) > minPurchase;
	}

	/**
	 * @notice Purchase tokens using a stablecoin.
	 * @param currency The address of the stablecoin to use for the purchase.
	 * @param amount The amount of tokens you want to buy in 'decimals'.
	 * @dev If you want to buy 14.5 tokens, amount should be equal to 14.5 * 10^decimals
	 *
	 *
	 * Requirements:
	 * - The stablecoin address must be valid.
	 * - The caller must pay with a valid stablecoin.
	 */
	function buyTokens(
		address currency,
		uint256 amount
	) external validCurrency(currency) saleIsOpen {
		require(amount != 0, "OTCController: amount is zero");
		require(price != 0, "OTCController: price is zero");
		IERC20Metadata _currency = IERC20Metadata(currency);

		uint256 finalPrice = (price * amount * (10 ** (_currency.decimals()))) /
			(10 ** (2 * decimals));
		_currency.safeTransferFrom(_msgSender(), address(this), finalPrice);

		uint tokenAmount = amount * 10 ** (sellingToken.decimals() - decimals);
		sellingToken.safeTransferFrom(fundingWallet, vesting, tokenAmount);

		IVesting vestingContract = IVesting(vesting);
		vestingContract.createAgenda(_msgSender(), tokenAmount);
		emit Sold(_msgSender(), currency, tokenAmount);
	}

	/**
	 * @notice Set the price for purchasing tokens.
	 * @param _price The new price in stablecoins.
	 *
	 * Requirements:
	 * - The new price must be greater than zero.
	 * - The caller must have the PRICE_MANAGER_ROLE.
	 */
	function setPrice(uint256 _price) external onlyRole(PRICE_MANAGER_ROLE) {
		require(_price > 0, "OTCController: Price MUST be greater than zero");

		price = _price;
		emit PriceSetted(_msgSender(), _price);
	}

	/**
	 * @notice Add a stablecoin to the list of accepted stablecoins.
	 * @param currency The address of the stablecoin to add.
	 *
	 * Requirements:
	 * - The token address must not be an accepted stablecoin.
	 * - The token address must be a valid ERC20 token.
	 * - The token address decimals MUST be less than 'decimals'.
	 * - The caller must have the PRICE_MANAGER_ROLE.
	 */
	function addCurrency(address currency) external onlyRole(PRICE_MANAGER_ROLE) {
		_addCurrencyDec(currency);
	}

	/**
	 * @notice Remove a stablecoin from the list of accepted stablecoins.
	 * @param currency The address of the stablecoin to remove.
	 *
	 * Requirements:
	 * - The caller must have the PRICE_MANAGER_ROLE.
	 */
	function removeCurrency(address currency) external onlyRole(PRICE_MANAGER_ROLE) {
		_removeCurrency(currency);
	}

	/**
	 * @notice Open the sale, allowing token purchases.
	 * @param initialAuthorizers Addresses initially authorized to interact with the vesting contract.
	 * @param initialAuthorizedSpenders Addresses initially authorized to spend tokens from the vesting contract.
	 * @param releaseTime The release time for the vesting contract.
	 *
	 * Requirements:
	 * - The funding wallet must have allocated allowance to this contract.
	 * - Sales must not have been opened previously.
	 * - Sales must not have been closed previously.
	 * - A price must be assigned.
	 * - At least one stablecoin must be added.
	 * - The caller must have the SALES_TRIGGERER_ROLE.
	 */
	function openSale(
		address[] calldata initialAuthorizers,
		address[] calldata initialAuthorizedSpenders,
		uint256 releaseTime
	) external onlyRole(SALES_TRIGGERER_ROLE) {
		require(
			sellingToken.allowance(fundingWallet, address(this)) > 0,
			"OTCController: Controller needs allowance"
		);
		require(!isSaleOpen, "OTCController: Sales are already opened");
		require(!alreadyOpen, "OTCController: Unable to open sales again");
		require(price > 0, "OTCController: Price is not assigned");
		require(_currencies.length() > 0, "OTCController: Stablecoin not added");

		Vesting vestingContract = new Vesting(
			address(sellingToken),
			initialAuthorizers,
			initialAuthorizedSpenders,
			releaseTime
		);
		vesting = address(vestingContract);
		isSaleOpen = true;
		alreadyOpen = true;
		emit SaleOpened();
	}

	/**
	 * @notice Close the token sale, preventing further purchases.
	 *
	 * Requirements:
	 * - Sales must have been opened previously.
	 * - The caller must have the SALES_TRIGGERER_ROLE.
	 */
	function closeSale() external onlyRole(SALES_TRIGGERER_ROLE) saleIsOpen {
		isSaleOpen = false;
		emit SaleClosed();
	}

	/**
	 * @notice  See {GOBWithdraw-_withdrawSet}.
	 */
	function withdrawCurencies() external onlyRole(WITHDRAW_ROLE) {
		require(_withdrawSet(_currencies), "OTCController: No balance available");
	}

	/**
	 * @notice  See {GOBWithdraw-_withdraw}.
	 */
	function withdraw(address token, uint256 amount) external onlyRole(WITHDRAW_ROLE) {
		require(amount > 0, "OTCController: Amount cannot be zero");
		_withdraw(token, amount);
	}

	/**
	 * @notice  Vaidates newCurrency decimals.
	 */
	function _addCurrencyDec(address newCurrency) private {
		require(
			IERC20Metadata(newCurrency).decimals() >= decimals,
			"OTCController: Currency decimals too low"
		);
		_addCurrency(newCurrency);
	}
}
