// SPDX-License-Identifier: MIT
pragma solidity ^0.8.18;

/**
 * @title Guardians of the Ball - IVesting
 * @author Guardians of the Ball
 * @notice Vesting contract Interface
 */
interface IVesting {
	/**
	 * @dev Agenda struct.
	 * @param account The address of the agenda owner.
	 * @param amount The amount of tokens currently being locked, amount is substracted for each withdrawal/authorizedWithdrawal.
	 * @param endTime The end time of the vesting in unix timestamp.
	 */
	struct Agenda {
		uint256 amount;
		uint256 endTime;
	}

	/**
	 * @notice Emitted when a new agenda is created by the `OTC_CONTROLLER_ROLE`.
	 * @param account The address of the agenda owner.
	 * @param amount The amount of tokens to be vested.
	 * @param agendaId the hash identifier of the agenda
	 * @param start The start time of the vesting in unix timestamp.
	 * @param end The end time of the vesting in unix timestamp.
	 */
	event AgendaCreated(
		address indexed account,
		uint256 amount,
		bytes32 agendaId,
		uint256 start,
		uint256 end
	);

	/**
	 * @notice Emitted when a withdrawal or authorized withdrawal is made.
	 * @param account The address of the agenda owner.
	 * @param operator The address of the caller, if `operator` == `account` then it is a withdrawal, else, an authorized withdrawal.
	 * @param amount The amount of tokens withdrawn.
	 */
	event Withdrawn(address indexed account, address indexed operator, uint256 amount);

	/**
	 * @dev Returns the address of the token being vested.
	 */
	function token() external view returns (address);

	/**
	 * @dev Returns available balance of `account`.
	 * @param account The address to query.
	 */
	function balanceOf(address account) external view returns (uint256);

	/**
	 * @dev Returns locked balance of `account`.
	 * @param account The address to query.
	 */
	function totalLocked(address account) external view returns (uint256);

	/**
	 * @dev Returns all vested balance of `account`.
	 * @param account The address to query.
	 */
	function totalVested(address account) external view returns (uint256);

	/**
	 * @dev withdraws all available tokens for the sender.
	 *
	 * Emits a {Withdrawn} event.
	 *
	 * Requirements:
	 * - the agenda must be unlocked.
	 * - the sender must have a non-zero balance.
	 */
	function withdraw() external;

	/**
	 * @dev withdraws `amount` tokens for `agendaOwner`.
	 * @param agendaOwner address to spend `amount` tokens from
	 * @param amount The amount of tokens to spend.
	 *
	 * Emits a {Withdrawn} event.
	 *
	 * Requirements:
	 * - `agendaOwner` must have a non-zero balance.
	 * - Must be called by `AUTHORIZED_SPENDER_ROLE`
	 * - `agendaOwner` must have approved operator or caller to spend `amount` tokens
	 */
	function withdrawFrom(address agendaOwner, uint256 amount) external;

	/**
	 * @dev Creates a new agenda that locks `amount` tokens for `beneficiary` until `block.timestamp` + `durationPerAgenda`.
	 * @param beneficiary The address of the agenda owner.
	 * @param amount The amount of tokens to be vested.
	 *
	 * Emits a {AgendaCreated} event.
	 *
	 * Requirements:
	 * - Must be called by `OTC_CONTROLLER_ROLE`
	 * - `beneficiary` must not be the zero address.
	 * - `amount` must be non-zero.
	 * - `beneficiary` must have approved contract to spend `amount` tokens.
	 */
	function createAgenda(address beneficiary, uint256 amount) external;
}
