//......&&&&&...@@@@@,*&@@@@////@@@@@,,&&&&&...&&&&&......
//......&&&&&...&&&&&,,&&&&@***,&&&&&,.&&&&&...&&&&&......
//......%%%&&...&&&&&,,%&&&&,,,,&&&&&..&&&%%...&&&&&......
//......%%%%%%%%%%%%%..#%%%%%%%%%%%%#..%%%%%%%%%%%%%......
//........................................................

// SPDX-License-Identifier: MIT
pragma solidity ^0.8.18;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/Address.sol";

import "./IVesting.sol";
import "../otc/IOTCController.sol";

/**
 * @title Guardians of the Ball - Vesting
 * @author Guardians of the Ball
 * @notice The Vesting contract allows the management of locked tokens and their scheduled release in a given period of time.
 * 		   The tokens can be transferred to a list of authorized addresses, possibly just one that is the Marketplace,
 *         allowing the buyer to purchase NFTs during the lock-in period.
 *         This contract only accepts the blocking of a specific ERC20 token.
 * 		   Each block (or vesting) is known internally as an “agenda”.
 */
contract Vesting is IVesting, AccessControl {
	using Address for address;
	using SafeERC20 for IERC20;

	bytes32 public constant AUTHORIZER_ROLE = keccak256("AUTHORIZER_ROLE");
	bytes32 public constant AUTHORIZED_SPENDER_ROLE = keccak256("AUTHORIZED_SPENDER_ROLE");
	bytes32 public constant OTC_CONTROLLER_ROLE = keccak256("OTC_CONTROLLER_ROLE");

	uint8 public constant AGENDAS_MAX = 255;
	uint256 internal constant UINT256_MAX = 2 ** 256 - 1;

	/**
	 * @notice ERC20 token to vest.
	 * @dev See {IVesting-token}.
	 */
	address public immutable token;

	/// @dev Duration of each agenda or Vesting.
	uint256 public immutable durationPerAgenda;

	/// @dev beneficiary => Agenda Nonce
	mapping(address => uint256) private _agendaNonce;
	/// @dev beneficiary => Agenda Nonce Start
	mapping(address => uint256) private _agendaNonceStart;
	/// @dev Agenda Id => Agenda (AgendaId = hash(address,nonce))
	mapping(bytes32 => Agenda) private _agendas;

	/// @dev Controller contract address.
	IOTCController public immutable controller;

	/**
	 * @param token_ ERC20 to vest
	 * @param initialAuthorizers  Initial addresses that will have AUTHORIZER_ROLE
	 * @param initialAuthorizedSpenders Initial addresses that will have AUTHORIZED_SPENDER_ROLE
	 * @param durationPerAgenda_  Duration of each agenda or Vesting in unix timestamp.
	 */
	constructor(
		address token_,
		address[] memory initialAuthorizers,
		address[] memory initialAuthorizedSpenders,
		uint256 durationPerAgenda_
	) {
		require(initialAuthorizers.length != 0, "Vesting: initialAuthorizers is empty");

		_setRoleAdmin(AUTHORIZED_SPENDER_ROLE, AUTHORIZER_ROLE);
		_grantRole(OTC_CONTROLLER_ROLE, _msgSender());
		controller = IOTCController(_msgSender());

		token = token_;
		durationPerAgenda = durationPerAgenda_;

		for (uint256 i = 0; i < initialAuthorizedSpenders.length; ) {
			_grantRole(AUTHORIZED_SPENDER_ROLE, initialAuthorizedSpenders[i]);
			unchecked {
				++i;
			}
		}
		for (uint256 i = 0; i < initialAuthorizers.length; ) {
			_grantRole(AUTHORIZER_ROLE, initialAuthorizers[i]);
			unchecked {
				++i;
			}
		}
	}

	/**
	 * @dev See {IVesting-balanceOf}
	 */
	function balanceOf(address account) external view returns (uint256) {
		(uint256 availableBalance, ) = _getBalances(account);
		return availableBalance;
	}

	/**
	 * @dev See {IVesting-totalLocked}
	 */
	function totalLocked(address account) external view returns (uint256) {
		(uint256 availableBalance, uint256 totalVestedBalance) = _getBalances(account);
		return totalVestedBalance - availableBalance;
	}

	/**
	 * @dev See {IVesting-totalVested}
	 */
	function totalVested(address account) external view returns (uint256) {
		(, uint256 totalVestedBalance) = _getBalances(account);
		return totalVestedBalance;
	}

	/**
	 * @dev See {IVesting-withdraw}
	 */
	function withdraw() external {
		address account = _msgSender();

		uint256 amountSubstracted = _substractBalances(account, UINT256_MAX, false);

		IERC20(token).safeTransfer(account, amountSubstracted);

		emit Withdrawn(account, account, amountSubstracted);
	}

	/**
	 * @dev See {IVesting-withdrawFrom}
	 */
	function withdrawFrom(
		address agendaOwner,
		uint256 amount
	) external onlyRole(AUTHORIZED_SPENDER_ROLE) {
		require(amount > 0, "Vesting: amount is zero");
		require(agendaOwner != address(0), "Vesting: agendaOwner is the zero address");

		address operator = _msgSender();

		require(
			_substractBalances(agendaOwner, amount, true) == amount,
			"Vesting: amount exceeds balance"
		);

		IERC20(token).safeTransfer(operator, amount);

		emit Withdrawn(agendaOwner, operator, amount);
	}

	/**
	 * @dev See {IVesting-createAgenda}
	 */
	function createAgenda(
		address beneficiary,
		uint256 amount
	) external onlyRole(OTC_CONTROLLER_ROLE) {
		require(beneficiary != address(0), "Vesting: beneficiary is the zero address");
		require(amount != 0, "Vesting: amount is zero");

		uint256 agendaNonce = _agendaNonce[beneficiary];
		require(
			agendaNonce - _agendaNonceStart[beneficiary] <= AGENDAS_MAX,
			"Vesting: Max agendas reached for beneficiary"
		);

		uint256 timestamp = block.timestamp;
		uint256 endTime = timestamp + durationPerAgenda;

		Agenda memory agenda = Agenda(amount, endTime);
		bytes32 agendaId = _hashAgenda(beneficiary, agendaNonce);

		_agendaNonce[beneficiary]++;
		_agendas[agendaId] = agenda;

		emit AgendaCreated(beneficiary, amount, agendaId, timestamp, endTime);
	}

	/**
	 * @dev Iterates over all `account` agendas and returns the total available balance and the total locked balance.
	 * @param account The address to query.
	 * @return totalAvailableBalance The total available balance.
	 * @return totalVestedBalance The total vested balance.
	 * @dev total locked balance = total vested balance - total available balance
	 */
	function _getBalances(
		address account
	) private view returns (uint256 totalAvailableBalance, uint256 totalVestedBalance) {
		uint256 nAgendas = _agendaNonce[account];
		uint256 nsAgendas = _agendaNonceStart[account];
		uint256 timestamp = block.timestamp;
		for (uint256 i = nsAgendas; i < nAgendas; ) {
			bytes32 agendaId = _hashAgenda(account, i);
			Agenda storage agenda = _agendas[agendaId];
			if (timestamp >= agenda.endTime) {
				totalAvailableBalance += agenda.amount;
			}
			totalVestedBalance += agenda.amount;
			unchecked {
				++i;
			}
		}
	}

	/**
	 * @dev Iterates over all `account` agendas and subtracts `amountToSubtract` from them.
	 * @param account The address to query.
	 * @param amountToSubtract The amount to subtract.
	 * @param substractLocked If true, subtracts from locked balances, otherwise from available balances.
	 * @return amountSubstracted The total amount subtracted, it can be less than `amountToSubtract` if there is not enough balance.
	 *                           If it is zero, it means that there is no balance.
	 */
	function _substractBalances(
		address account,
		uint256 amountToSubtract,
		bool substractLocked
	) private returns (uint256 amountSubstracted) {
		uint256 nAgendas = _agendaNonce[account];
		uint256 nsAgendas = _agendaNonceStart[account];
		uint256 timestamp = block.timestamp;
		for (uint256 i = nsAgendas; i < nAgendas; i++) {
			if (amountToSubtract == 0) break;
			bytes32 agendaId = _hashAgenda(account, i);
			Agenda storage agenda = _agendas[agendaId];
			// If agenda does not exist, continue
			if (agenda.endTime == 0) continue;
			if (!substractLocked && timestamp < agenda.endTime) continue;
			if (agenda.amount >= amountToSubtract) {
				agenda.amount -= amountToSubtract;
				amountSubstracted += amountToSubtract;
				amountToSubtract = 0;
				// If agenda is empty, delete it
				if (agenda.amount == 0) {
					_agendaNonceStart[account]++;
					delete _agendas[agendaId];
				}
			} else {
				amountToSubtract -= agenda.amount;
				amountSubstracted += agenda.amount;
				_agendaNonceStart[account]++;
				delete _agendas[agendaId];
			}
		}
		require(amountSubstracted > 0, "Vesting: balance is zero");
	}

	/**
	 * @dev Creates a unique identifier for a given agenda.
	 * @param account the owner of the agenda
	 * @param agendaNonce nonce, should be equal to `_agendaNonce[account]`
	 */
	function _hashAgenda(address account, uint256 agendaNonce) private pure returns (bytes32) {
		return keccak256(abi.encodePacked(account, agendaNonce));
	}
}
"
