// SPDX-License-Identifier: MIT
// CIS Crypto Lottery [ciscrypto.site]
// One Matic Lottery

pragma solidity ^0.8.19;

contract OneMaticLottery {
    address public manager;
    address payable[] public players;
    address payable public winner; 
    uint[] myArray;
    constructor(){
        manager=msg.sender;
    }
    function participate() public payable {
        require(msg.value==1 ether, "Please pay 1 Matic only");
        players.push(payable(msg.sender));
    }
    function getBalance() public view returns(uint){
        // require(manager==msg.sender, "You a not a manager");
        return address(this).balance;
    }
    function random() internal view returns(uint){
        return uint(keccak256(abi.encodePacked(block.prevrandao, block.timestamp, players.length)));
    }

    function pickWinner() public {
        // require(manager==msg.sender, "You a not a manager");
        require(players.length>=5, "Players are less than 5" );
        uint r = random();
        uint index = r%players.length;
        winner = players[index];
        winner.transfer(getBalance());
        players = new address payable [](0);
    }
    function showCountParticipate() public view returns (uint) {
        // require(manager==msg.sender, "You a not a manager");
        uint index = players.length;
        return (index);
    }
    function showAddresses() public view returns (address payable[] memory) {
        // require(manager==msg.sender, "You a not a manager");
        return players;
   }

}
