      "content": "//SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC1155/IERC1155.sol";
import "@openzeppelin/contracts/security/Pausable.sol";

import "./Managable.sol";
import "./LibLazyReward.sol";

contract Rewards is Managable, Pausable {
    using SafeERC20 for IERC20;

    // Structures
    struct Rewarder {
        address addr;
        RewardType typ;
    }
    enum RewardType{ERC20, ERC1155}

    // Storages
    bytes32 private domainSeparator;
    address public signerAddress;
    string[] public rewards;
    mapping(string => Rewarder) public rewarders;
    mapping(uint256 => bool) private claimedRewards;

    // Events
    event ChangedSignerAddress(address _addr);
    event RewarderAdded(string _name, address _addr, RewardType _type);
    event RewarderDeleted(string _name);
    event ClaimedReward(uint256 indexed _id);

    constructor(
        address _signer,
        string memory _appName,
        string memory _version
    ) {
        domainSeparator = keccak256(abi.encode(
            keccak256("EIP712Domain(string name,string version,uint256 chainId,address verifyingContract)"),
            keccak256(bytes(_appName)),
            keccak256(bytes(_version)),
            block.chainid,
            address(this)
        ));
        _setSignerAddress(_signer);
        _addManager(msg.sender);
    }

    function rewardNames() view external returns(string[] memory) {
        return rewards;
    }

    function togglePause() external onlyManager {
        if (paused()) {
            _unpause();
        } else {
            _pause();
        }
    }

    function claim(LibLazyReward.Reward memory _reward, bytes memory _signature) whenNotPaused external {
        require(verifyTypedDataHash(domainSeparator, _reward, _signature, signerAddress), "bad sig");
        require(claimedRewards[_reward.id] == false, "already claimed");
        require(_reward.addr == msg.sender, "not owner");

        for(uint256 i = 0; i < _reward.parts.length; i++) {
            LibLazyReward.Part memory _part = _reward.parts[i];
            Rewarder memory _rewarder = rewarders[_part.name];

            if (_rewarder.typ == RewardType.ERC20) {
                IERC20(_rewarder.addr).safeTransfer(msg.sender, _part.amount);
            } else if (_rewarder.typ == RewardType.ERC1155) {
                IERC1155(_rewarder.addr).safeTransferFrom(address(this), msg.sender, _part.id, _part.amount, "");
            } else {
                revert("Unknown type of reward");
            }
        }

        claimedRewards[_reward.id] = true;
        emit ClaimedReward(_reward.id);
    }

    function setSignerAddress(address _addr) public onlyManager {
        _setSignerAddress(_addr);
    }

    function addReward(string memory _name, address _addr, RewardType _type) public onlyManager {
        _addReward(_name, _addr, _type);
    }

    function deleteReward(string memory _name) public onlyManager {
        _deleteReward(_name);
    }

    function _setSignerAddress(address _addr) internal {
        signerAddress = _addr;
        emit ChangedSignerAddress(_addr);
    }

    function _addReward(string memory _name, address _addr, RewardType _type) internal {
        require(_addr != address(0), "zero address");
        require(bytes(_name).length > 0, "empty name");

        rewarders[_name] = Rewarder({
            addr: _addr,
            typ: _type
        });
        rewards.push(_name);

        emit RewarderAdded(_name, _addr, _type);
    }

    function _deleteReward(string memory _name) internal {
        require(rewarders[_name].addr != address(0), "no reward");

        uint idx = 0;
        for(uint i = 0; i < rewards.length; i++) {
            if (compareStrings(rewards[i], _name)) {
                idx = i;
                break;
            }
        }

        for(uint i = idx; i < rewards.length-1; i++){
            rewards[i] = rewards[i+1];      
        }        

        rewards.pop();
        delete(rewarders[_name]);

        emit RewarderDeleted(_name);
    }

    function verifyTypedDataHash(bytes32 _domainSeparator, LibLazyReward.Reward memory _reward, bytes memory _signature, address _owner) internal pure returns (bool) {
        bytes32 digest = ECDSA.toTypedDataHash(_domainSeparator, LibLazyReward.hash(_reward));
        address _signer = ECDSA.recover(digest, _signature);

        return _signer == _owner;
    }    

    function compareStrings(string memory a, string memory b) internal pure returns (bool) {
        return (keccak256(abi.encodePacked((a))) == keccak256(abi.encodePacked((b))));
    }
}"
