library LibLazyReward {

    bytes32 public constant TYPE_HASH = keccak256("Reward(uint256 id,address addr,Part[] parts)Part(string name,uint256 id,uint256 amount)");
    bytes32 public constant PART_TYPE_HASH = keccak256("Part(string name,uint256 id,uint256 amount)");

