        bytes32[] memory partsBytes = new bytes32[](_reward.parts.length);
        for (uint i = 0; i < _reward.parts.length; i++) {
            partsBytes[i] = hashPart(_reward.parts[i]);
        }

        return keccak256(abi.encode(
            TYPE_HASH,
            _reward.id,
            _reward.addr,
            keccak256(abi.encodePacked(partsBytes))
        ));
    }    

    function hashPart(Part memory _part) internal pure returns (bytes32) {
        return keccak256(abi.encode(
            PART_TYPE_HASH,
            keccak256(bytes(_part.name)),
            _part.id, 
            _part.amount
        ));
    }
