// File: UniswapV2Handler.sol


pragma solidity ^0.8.17;








using SafeMath for uint256;


interface IERC20Decimals is IERC20 {
    function decimals() external view returns (uint8);
}


contract UniswapV2Handler is Ownable{

    address public  _rewardsWallet = 0xF6eBd66c6D3849508BA5fda52CfCF21B289E8E07;
    uint256 public rewardsPool;

    address public _usdtAddress = 0xc2132D05D31c914a87C6611C10748AEb04B58e8F; // USDT address on polygon
    address public _nx7Address = 0x97d45Ec651d25b90701ec49c959E75efe4E1F36c; // WMATIC address on polygon, switch with NX7 contract

    /// @notice Uniswap V2 Polygon address
    address public constant quickswapRouterAddressPolygon = 0xa5E0829CaCEd8fFDD4De3c43696c57F7D7A678ff;

    
    /// @notice Uniswap Router Contract
    IUniswapV2Router02 public uniswapRouter;

    /// @notice Uniswap Factory contract
    IUniswapV2Factory public factory;

    
    /// @notice Uniswap Router address Polygon Mainnet
    address public routerAddress;

    /// @notice Uniswap Factory Contract address Polygon Mainnet
    address public factoryAddress;

    address public stakingSystemAddress;  
    address public Buybackaddress;   // To be added after deployment of this contract
    
    event CreatedContract(address uniswapRouter, address factory);
    event Swap(uint amountIn, uint amountOut);


    /// @notice Restricts access to only the staking contract address
    modifier onlyStakingSystem() {
        require(msg.sender == stakingSystemAddress, "Caller is not the staking system");
        _;
    }

     modifier onlyBuybackSystem() {
        require(msg.sender == Buybackaddress, "Caller is not the staking system");
        _;
    }
    
    
    // use _rewardsReserveToken = 0xc2132D05D31c914a87C6611C10748AEb04B58e8F //usdtAddress
    // use _nx7Address = 0x0d500B1d8E8eF31E21C99d1Db9A6444d3ADf1270 //replace with nx7Address (currently wmatic)
    constructor(address _rewardsReserveToken, address _protocolNativeToken) {

        routerAddress = quickswapRouterAddressPolygon;
        uniswapRouter = IUniswapV2Router02(routerAddress);
        factoryAddress = uniswapRouter.factory();
        factory = IUniswapV2Factory(factoryAddress);

        // Set reserve token
        _usdtAddress = _rewardsReserveToken;

        // Set native token to swap to 
        _nx7Address = _protocolNativeToken;

        emit CreatedContract(routerAddress, factoryAddress);
    }


    /// @notice Fetches the uniswap pair address for two tokens
    /// @param tokenA address of tokenA 
    /// @param tokenB address of tokenB
    /// @return pair address of pool pair
    function getPairAddress(address tokenA, address tokenB) public view returns (address pair) {
        (address token0, address token1) = tokenA < tokenB ? (tokenA, tokenB) : (tokenB, tokenA);
        pair = factory.getPair(token0, token1);

        return pair;
    }

    /// @notice Fetches the reserves for the uniswap pair
    /// @param pairAddress address of the token pair on uniswap
    /// @return reserve0 reserve of token0
    /// @return reserve1 reserve of token1
    /// @return blockTimestampLast last update of reserves
    function getPairReserves(address pairAddress) public view returns (uint112 reserve0, uint112 reserve1, uint32 blockTimestampLast) {
        (reserve0, reserve1, blockTimestampLast) = IUniswapV2Pair(pairAddress).getReserves();
        return (reserve0, reserve1, blockTimestampLast);
    }


    /// @notice Fetches the price for amount of tokenIn wrt tokenOut
    /// @param amountIn amount in WEI of tokenIn
    /// @param tokenIn address of tokenOut
    /// @param tokenOut address of tokenOut
    function getPrice(
        uint amountIn,
        address tokenIn,
        address tokenOut
    ) public view returns (uint[] memory amounts) {
        address[] memory path = new address[](2);
        path[0] = tokenIn;
        path[1] = tokenOut;

        amounts = uniswapRouter.getAmountsOut(amountIn, path);

        return amounts;
    }


    
    /// @notice Triggers a transfer from "rewards wallet" to this contract by owner
    /// @param amountIn amount to receive
    function fundContract(uint amountIn) public onlyOwner {
        address tokenIn = _usdtAddress;

        require(IERC20(tokenIn).balanceOf(_rewardsWallet) >= amountIn, "Insufficient funds in _rewardsWallet.");
        require(IERC20(tokenIn).transferFrom(_rewardsWallet, address(this), amountIn), "Cannot fund contract from _rewardsWallet.");
        rewardsPool += amountIn;
    }

    /// @notice Retrieves the balance of token for this contract
    /// @param token address of the token in question
    /// @return balance amount in WEI of the token in this contract
    function getBalance(address token) public view returns(uint256 balance) {
        balance = IERC20(token).balanceOf(address(this));
        return balance;
    }

    /// @notice Withdraws the balance of the specified token by owner to the "rewards wallet"
    /// @param token address of the token in question
    function withdrawBalance(address token) public onlyOwner {
        uint256 tokenBalance = IERC20(token).balanceOf(address(this));
        require(IERC20(token).transfer(_rewardsWallet, tokenBalance), "Failed to withdraw balance of token.");
    }

    /// @notice Withdraws the Ether balance of the contract to the rewards wallet
    function withdrawEther() public onlyOwner {
        (bool success, ) = _rewardsWallet.call{value: address(this).balance}("");
        require(success, "Failed to send ether");
    }


    /// @notice Sets the staking system contract address
    /// @param _stakingSystemAddress address of the staking contract
    function setStakingSystemAddress(address _stakingSystemAddress) public onlyOwner {
        stakingSystemAddress = _stakingSystemAddress;
    }
function setrewardsWallet(address rewardsWallet) external onlyOwner {
        _rewardsWallet = rewardsWallet;
    }
    /// @notice Swaps tokenIn for tokenOut and transfers it to the specified address
    /// @param amountIn amount in WEI, of tokenIn, to swap
    /// @param to receiver address of the resulting token
    function swapTokensWithFees(
        uint amountIn,
        address to
    ) external onlyStakingSystem {
        address tokenIn = _usdtAddress; 
        address tokenOut = _nx7Address; 

        require(rewardsPool >= amountIn, "Insufficient funds in rewards pool.");
        require(IERC20(tokenIn).approve(routerAddress, amountIn), "Failed to approve to uniswap router contract.");
        
        uint[] memory amounts = new uint[](2);

        amounts = getPrice(amountIn, tokenIn, tokenOut);

        uint amountOutMin = amounts[1];

        address[] memory path = new address[](2);
        path[0] = tokenIn;
        path[1] = tokenOut;

        uniswapRouter.swapExactTokensForTokensSupportingFeeOnTransferTokens(amountIn, amountOutMin, path, to, block.timestamp);

        rewardsPool -= amountIn;

        emit Swap(amountIn, amounts[1]);
    }
    function swapTokensWithFeesBuyback(
        uint amountIn,
        address to
    ) external onlyBuybackSystem {
        address tokenIn = _usdtAddress; 
        address tokenOut = _nx7Address; 

        require(rewardsPool >= amountIn, "Insufficient funds in rewards pool.");
        require(IERC20(tokenIn).approve(routerAddress, amountIn), "Failed to approve to uniswap router contract.");
        
        uint[] memory amounts = new uint[](2);

        amounts = getPrice(amountIn, tokenIn, tokenOut);

        uint amountOutMin = amounts[1];

        address[] memory path = new address[](2);
        path[0] = tokenIn;
        path[1] = tokenOut;

        uniswapRouter.swapExactTokensForTokensSupportingFeeOnTransferTokens(amountIn, amountOutMin, path, to, block.timestamp);

        rewardsPool -= amountIn;

        emit Swap(amountIn, amounts[1]);
    }

    
}
