      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

import "../interfaces/IERC165.sol";
import "../interfaces/IERC20.sol";
import "../interfaces/IERC1363.sol";
import "../interfaces/IERC1363Receiver.sol";
import "../interfaces/IERC1363Spender.sol";
import "../token/ERC20/ERC20.sol";
import "../utils/Address.sol";

contract TubeToken is IERC165, ERC20, IERC1363 {
    using Address for address;

    event TubeEvent(address indexed from, bytes32 eventType);

    constructor(uint256 totalSupply, address holderContract)
        ERC20("Tube", "TUBE")
    {
        _mint(holderContract, totalSupply);
    }

    function supportsInterface(bytes4 interfaceId)
        external
        pure
        override(IERC165)
        returns (bool)
    {
        return
            interfaceId == type(IERC165).interfaceId ||
            interfaceId == type(IERC20).interfaceId ||
            interfaceId == type(IERC20Metadata).interfaceId ||
            interfaceId == type(IERC1363).interfaceId;
    }

    function transferAndCall(address recipient, uint256 amount)
        public
        override
        returns (bool)
    {
        return transferAndCall(recipient, amount, "");
    }

    function transferAndCall(
        address recipient,
        uint256 amount,
        bytes memory data
    ) public override returns (bool) {
        transfer(recipient, amount);
        require(
            _checkOnTransferReceived(_msgSender(), recipient, amount, data),
            "ERC1363: _checkAndCallTransfer reverts"
        );
        return true;
    }

    function transferFromAndCall(
        address sender,
        address recipient,
        uint256 amount
    ) public override returns (bool) {
        return transferFromAndCall(sender, recipient, amount, "");
    }

    function transferFromAndCall(
        address sender,
        address recipient,
        uint256 amount,
        bytes memory data
    ) public override returns (bool) {
        transferFrom(sender, recipient, amount);
        require(
            _checkOnTransferReceived(sender, recipient, amount, data),
            "ERC1363: _checkAndCallTransfer reverts"
        );
        return true;
    }

    function approveAndCall(address spender, uint256 amount)
        public
        override
        returns (bool)
    {
        return approveAndCall(spender, amount, "");
    }

    function approveAndCall(
        address spender,
        uint256 amount,
        bytes memory data
    ) public override returns (bool) {
        approve(spender, amount);
        require(
            _checkOnApprovalReceived(spender, amount, data),
            "ERC1363: _checkAndCallApprove reverts"
        );
        return true;
    }

    function _checkOnTransferReceived(
        address sender,
        address recipient,
        uint256 amount,
        bytes memory data
    ) internal returns (bool) {
        if (!recipient.isContract()) {
            return false;
        }

        bytes4 retval = IERC1363Receiver(recipient).onTransferReceived(
            msg.sender,
            sender,
            amount,
            data
        );
        return (retval ==
            IERC1363Receiver(recipient).onTransferReceived.selector);
    }

    function _checkOnApprovalReceived(
        address spender,
        uint256 amount,
        bytes memory data
    ) internal returns (bool) {
        if (!spender.isContract()) {
            return false;
        }
        bytes4 retval = IERC1363Spender(spender).onApprovalReceived(
            _msgSender(),
            amount,
            data
        );
        return (retval == IERC1363Spender(spender).onApprovalReceived.selector);
    }

    function tubeEvent(bytes32 eventType) public {
        emit TubeEvent(_msgSender(), bytes32(eventType));
    }
}
"
