    "contracts/nft/ClaimTicket.sol": {
      "content": "//SPDX-License-Identifier: MIT
pragma solidity 0.8.4;

import "@openzeppelin/contracts/token/ERC1155/IERC1155.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts/token/ERC1155/utils/ERC1155Holder.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "../nft/TicketItem.sol";
import "../common/TransferHelper.sol";
import "../common/AccessController.sol";

contract ClaimTicket is AccessController, ERC1155Holder, ReentrancyGuard, Pausable {
  using Counters for Counters.Counter;
  using ECDSA for bytes32;

  bytes32 private constant CLAIM_MANAGEMENT = keccak256("CLAIM_MANAGEMENT");
  IERC1155 public ticketAddress;
  
  struct ClaimItem {
    string claimId;
    address userClaim;
    uint256 ticketId;
    uint256 tradeItem;
  }

  mapping(address => Counters.Counter) private _claimItemIds;
  mapping(string => ClaimItem) private _orderClaim;
  mapping(string => bool) private _isExcuteOrder;
  address public verifyAddress;


  constructor(IERC1155 _ticketAddress) {
     ticketAddress = _ticketAddress;
    _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());
  }

  event CLAIMORDERTICKET(string claimId, address userClaim, uint256 timeStamp);
  event CANCLECLAIMORDERTICKET(string claimId, address userClaim, address adminRejected, uint256 timeStamp);
  event ACCEPTCLAIMORDERTICKET(string claimId, address userClaim, address adminAccepted, uint256 timeStamp);

  modifier onlyClaimManagement() {
    require(hasRole(CLAIM_MANAGEMENT, _msgSender()), "Restricted to claim management.");
    _;
  }

  function pause() external onlyAdmin {
    _pause();
  }

  function unpause() external onlyAdmin {
    _unpause();
  }

  function setVerifyAddress(address _verifyAddress) external onlyAdmin {
    verifyAddress = _verifyAddress; 
  }

  function setClaimer(address[] calldata _wallets) external onlyAdmin {
    for(uint256 i = 0; i < _wallets.length; i++){
      _setupRole(CLAIM_MANAGEMENT, _wallets[i]);
    }
  }

  function setTicketAddress(IERC1155 _ticketAddress) external onlyAdmin {
     ticketAddress = _ticketAddress;
  }

  function burnTicketItem(string calldata _orderId, string calldata _claimId, bytes calldata _signature) external onlyClaimManagement {
    require(_orderClaim[_claimId].ticketId != 0, "NFT_NOT_FOUND");
    require(!_isExcuteOrder[_orderId], "OrderId was excuted");
    ClaimItem memory claimInfo = _orderClaim[_claimId];
    require(_validateSignatureClaimNft(_orderId, _claimId, claimInfo.tradeItem, claimInfo.ticketId, _signature), "INVALID_INPUT");
    _isExcuteOrder[_orderId] = true;
    TicketItem(address(ticketAddress)).burnItem(claimInfo.ticketId, 1, address(this));
    _orderClaim[_claimId].ticketId = 0;
    delete _orderClaim[_claimId];
    emit ACCEPTCLAIMORDERTICKET(_claimId, claimInfo.userClaim, _msgSender(), block.timestamp);
  }

  function claimRealItem(uint256 _nftId, string calldata _claimId, uint256 _tradeItem) external {
    uint256 balance = ticketAddress.balanceOf(_msgSender(), _nftId);
    require(balance > 0, "BALANCE_NOT_ENOUGH");
    _orderClaim[_claimId] = ClaimItem({
      claimId: _claimId,
      ticketId: _nftId,
      userClaim: _msgSender(),
      tradeItem: _tradeItem
    });
    ticketAddress.safeTransferFrom(_msgSender(), address(this), _nftId, 1, "");
    _claimItemIds[_msgSender()].increment();
    emit CLAIMORDERTICKET(_claimId, _msgSender(), block.timestamp);
  }

  function cancelClaimItem(string calldata _orderId, string calldata _claimId,  bytes calldata _signature) external onlyClaimManagement {
    require(_orderClaim[_claimId].ticketId != 0, "NFT_NOT_FOUND");
    require(!_isExcuteOrder[_orderId], "OrderId was excuted");
    ClaimItem memory claimInfo = _orderClaim[_claimId];
    require(_validateSignatureClaimNft(_orderId, _claimId, claimInfo.tradeItem, claimInfo.ticketId, _signature), "INVALID_INPUT");
    _isExcuteOrder[_orderId] = true;
    ticketAddress.safeTransferFrom(address(this), claimInfo.userClaim, _orderClaim[_claimId].ticketId, 1, "");
    _orderClaim[_claimId].ticketId = 0;
    delete _orderClaim[_claimId];
    emit CANCLECLAIMORDERTICKET(_claimId, _orderClaim[_claimId].userClaim, _msgSender(), block.timestamp);
  }

  function _validateSignatureClaimNft(string calldata _orderId, string calldata _claimId, uint256 _tradeItem, uint256 _ticketId, bytes calldata _signature) private view returns (bool) {
    bytes32 hashValue = keccak256(
        abi.encodePacked(_orderId, _claimId, _tradeItem, _ticketId)
    );
    address recover = hashValue.toEthSignedMessageHash().recover(_signature);
    return recover == verifyAddress;
    }

  function supportsInterface(bytes4 interfaceId)
    public
    view
    virtual
    override(AccessControl, ERC1155Receiver)
    returns (bool)
  {
    return super.supportsInterface(interfaceId);
  }
}
"
    },
