    "contracts/nft/TicketItem.sol": {
      "content": "//SPDX-License-Identifier: MIT
pragma solidity 0.8.4;

import "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "../common/AccessController.sol";

contract TicketItem is ERC1155, AccessController {
  using ECDSA for bytes32;

  mapping(uint256 => uint256) public limitAmountByNftId;
  mapping(uint256 => uint256) public mintedAmountByNftId;
  uint256[] private _mintedAddress;
  string private baseUri;

  constructor(string memory _uri) ERC1155(_uri) {
    baseUri = _uri;
    _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());
  }

  function setBaseUri(string calldata _baseUri) external onlyAdmin {
    baseUri = _baseUri;
  }

  function setLimitAmountByNftId(uint256[] calldata nftIds, uint256[] calldata limit) external onlyAdmin {
    require(nftIds.length == limit.length, "Invalid input");
    for(uint256 index = 0; index < nftIds.length; index++){
      limitAmountByNftId[nftIds[index]] = limit[index];
    }
  }

  function mintItemFromMarket(uint256 _nftId, uint256 _amount, address _wallet) external onlyOperator {
    require(mintedAmountByNftId[_nftId] + _amount <= limitAmountByNftId[_nftId], "Exceed maximum item supply of nftId");
    _mint(_wallet, _nftId, _amount, "");
    mintedAmountByNftId[_nftId] += _amount;
    if (existedId(_nftId) != true) {
      _mintedAddress.push(_nftId);
    }
  }

  function existedId(uint256 _nftId) private view returns (bool) {
    bool isExisted = false;
    for (uint256 i = 0; i < _mintedAddress.length; i++) {
      if (_mintedAddress[i] == _nftId) {
        isExisted = true;
      }
    }
    return isExisted;
  }

  function burnItem(uint256 _nftId, uint256 _amount, address _wallet) external {
    require(hasRole(OPERATOR, _msgSender()) || hasRole(DEFAULT_ADMIN_ROLE, _msgSender()), "PERMISSION_DENIED");
    _burn(_wallet, _nftId, _amount);
  }

  function uri(uint256 _tokenId) override public view returns (string memory) {
    return string(
      abi.encodePacked(baseUri, Strings.toString(_tokenId))
    );
  }

  function name() public pure returns(string memory) {
    return "GENSO.EC-Ticket";
  }

  function getTokensOfUser(address _user)
    external
    view
    returns
    (uint256[] memory, uint256[] memory, string[] memory)
  {
    uint256 _counter;
    for (uint256 i = 0; i < _mintedAddress.length; i++) {
      if (balanceOf(_user, _mintedAddress[i]) != 0) {
        _counter++;
      }
    }

    uint256[] memory ticketIds = new uint256[](_counter);
    string[] memory metadata = new string[](_counter);
    uint256[] memory balances = new uint256[](_counter);
    uint256 index;

    for (uint256 i = 0; i < _mintedAddress.length; i++) {
      uint256 balance = balanceOf(_user, _mintedAddress[i]);
      if (balance != 0) {
        ticketIds[index] = _mintedAddress[i];
        metadata[index] = uri(_mintedAddress[i]);
        balances[index] = balance;
        index++;
      }
    }
    return (ticketIds, balances, metadata);
  }

  function supportsInterface(bytes4 interfaceId)
    public
    view
    virtual
    override(AccessControl, ERC1155)
    returns (bool)
  {
    return super.supportsInterface(interfaceId);
  }
}
"
    },
    "contracts/common/TransferHelper.sol": {
      "content": "//SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

library TransferHelper {
    function safeApprove(
        address token,
        address to,
        uint256 value
    ) internal {
        // bytes4(keccak256(bytes('approve(address,uint256)')));
        (bool success, bytes memory data) = token.call(abi.encodeWithSelector(0x095ea7b3, to, value));
        require(
            success && (data.length == 0 || abi.decode(data, (bool))),
            'TransferHelper::safeApprove: approve failed'
        );
    }
    function safeTransfer(
        address token,
        address to,
        uint256 value
    ) internal {
        // bytes4(keccak256(bytes('transfer(address,uint256)')));
        (bool success, bytes memory data) = token.call(abi.encodeWithSelector(0xa9059cbb, to, value));
        require(
            success && (data.length == 0 || abi.decode(data, (bool))),
            'TransferHelper::safeTransfer: transfer failed'
        );
    }
    function safeTransferFrom(
        address token,
        address from,
        address to,
        uint256 value
    ) internal {
        // bytes4(keccak256(bytes('transferFrom(address,address,uint256)')));
        (bool success, bytes memory data) = token.call(abi.encodeWithSelector(0x23b872dd, from, to, value));
        require(
            success && (data.length == 0 || abi.decode(data, (bool))),
            'TransferHelper::transferFrom: transferFrom failed'
        );
    }

    function safeEnoughTokenApproved(
        address token,
        address owner,
        address spender,
        uint256 amount
    ) internal {
        // bytes4(keccak256(bytes('allowance(address,address)')));
        (bool success, bytes memory data) = token.call(abi.encodeWithSelector(0xdd62ed3e, owner, spender));
        require( success && (abi.decode(data, (uint256)) >= amount), "Exchange currency allowance of user is too low");
        safeEnoughBalance(token, owner, amount);
    }

    function safeEnoughBalance(
        address token,
        address owner,
        uint256 amount
    ) internal {
        (bool success, bytes memory data) = token.call(abi.encodeWithSelector(0x70a08231, owner));
        require( success && (abi.decode(data, (uint256)) >= amount), "Exchange currency balance of user is too low");
    }
}
"
    },
    "contracts/common/AccessController.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.4;

import "@openzeppelin/contracts/access/AccessControl.sol";

contract AccessController is AccessControl {
  bytes32 public constant OPERATOR = keccak256("OPERATOR");

  modifier onlyAdmin() {
    require(hasRole(DEFAULT_ADMIN_ROLE, _msgSender()), "Caller is not the admin");
    _;
  }

  modifier onlyOperator() {
    require(hasRole(OPERATOR, _msgSender()), "Caller is not the operator");
    _;
  }

  modifier onlyOperatorOrAdmin() {
    require(hasRole(OPERATOR, _msgSender()) || hasRole(DEFAULT_ADMIN_ROLE, _msgSender()), "Caller must be operator or admin");
    _;
  }

  function addOperator(address operator) external onlyAdmin {
    _setupRole(OPERATOR, operator);
  }

  function removeOperator(address operator) external onlyAdmin {
    revokeRole(OPERATOR, operator);
  }
}
"
    },
