/******************************************************************************\
* Implementation of a CryptoChefs Main Proxy(Diamond) contract
* EIP-2535 Diamond Standard: https://eips.ethereum.org/EIPS/eip-2535
* Modified by: CryptoChefs (https://github.com/cryptoChefs)
/******************************************************************************/

pragma solidity ^0.8.1;

import {LibDiamond} from "./libraries/LibDiamond.sol";
import {DiamondCutFacet} from "./facets/DiamondCutFacet.sol";
import {DiamondLoupeFacet} from "./facets/DiamondLoupeFacet.sol";
import {OwnershipFacet} from "./facets/OwnershipFacet.sol";
import {IDiamondCut} from "./interfaces/IDiamondCut.sol";

import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/IERC721Metadata.sol";

contract CryptoChefs {
    constructor(address diamondCutFacetAddress, address _contractOwner) {
        LibDiamond.setContractOwner(_contractOwner);

        IDiamondCut.FacetCut[] memory cut = new IDiamondCut.FacetCut[](1);
        bytes4[] memory functionSelectors = new bytes4[](1);
        functionSelectors[0] = IDiamondCut.diamondCut.selector;
        cut[0] = IDiamondCut.FacetCut({
            facetAddress: diamondCutFacetAddress,
            action: IDiamondCut.FacetCutAction.Add,
            functionSelectors: functionSelectors
        });

        LibDiamond.diamondCut(cut, address(0), "");
    }

    function supportsInterface(bytes4 interfaceId) public view virtual returns (bool) {
        LibDiamond.DiamondStorage storage ds = LibDiamond.diamondStorage();

        // had to add interfaceId == type(IERC721).interfaceId || interfaceId == type(IERC721Metadata).interfaceId here
        // cause OpenSea doesn't detect it as ERC721 when called through the DiamondLoupeFacet
        return ds.supportedInterfaces[interfaceId] || interfaceId == type(IERC721).interfaceId || interfaceId == type(IERC721Metadata).interfaceId;
    }

    function owner() public view virtual returns (address) {
        // similar to supportsInterface. opensea doesn't recognize calling owner() from the OwnershipFacet
        return LibDiamond.contractOwner();
    }

    // fall back to one of the stored facets
    fallback() external payable {
        LibDiamond.DiamondStorage storage ds;
        bytes32 position = LibDiamond.DIAMOND_STORAGE_POSITION;
        assembly {
            ds.slot := position
        }
        address facet = ds.selectorToFacetAndPosition[msg.sig].facetAddress;
        require(facet != address(0), "Diamond: Function does not exist");
        assembly {
            calldatacopy(0, 0, calldatasize())
            let result := delegatecall(gas(), facet, 0, calldatasize(), 0, 0)
            returndatacopy(0, 0, returndatasize())
            switch result
                case 0 {
                    revert(0, returndatasize())
                }
                default {
                    return(0, returndatasize())
                }
        }
    }
}
