      "content": "// SPDX-License-Identifier: AGPL-3.0-only
pragma solidity >=0.8.4 <0.9.0;

import { ERC721L } from "innovation-contracts-solidity/tokens/ERC721L.sol";
import { ERC20 } from "solmate/tokens/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts/utils/cryptography/draft-EIP712.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import { AllowList } from "./AllowList.sol";

error NotAuthorized();
error NotEnoughETH();
error NotEnoughERC20();
error NativeTransferFailed();
error NotEmployee();
error BurnInvalidSignature();
error ECDSAInvalidSignature();
error IncorrectArraySize();

/**
 * @title This ERC721 is a voucher system to give ERC20 tokens and native tokens to users.
 * @notice It holds ERC20 tokens, during each burn the contract transfer 1eth and ERC20 token to the voucher holder
 *         Voucher can be burned using EIP 712 signature to be claimed gasless
 * @author Ledger
 */
contract Voucher is ERC721L, Ownable, EIP712 {
    /// tokenId => value of a voucher in ERC20/
    mapping(uint256 => uint256) public voucherValue;

    /// tokenId to ERC20 contract address
    mapping(uint256 => address) public tokenIdToERC20;

    /// nextTokenId of the NFT tokens
    uint256 public nextTokenId;

    /// counter to know how much ETH is reserved for minted NFT not burnt yet
    uint256 public reservedEth;

    /// native token to distribute when burning NFT
    uint256 public ETHperToken;

    /// counter to know how much ERC20 is reserved for minted NFT not burnt yet
    mapping(address => uint256) public reservedERC20;

    /*///////////////////////////////////////////////////////////////
                              EVENTS
    //////////////////////////////////////////////////////////////*/
    event ContractRefill(address, uint256 value);

    event ETHperTokenUpdated(address, uint256 value);

    // address of the  employee list to prevent transfer outside the organisation
    address public permissions;

    // boolean to bypass permissions in case transfer is possible outside  employees
    bool public checkPermissions;

    bytes32 public constant EMPLOYEE = keccak256("EMPLOYEE");

    string public _baseTokenURI;

    /*///////////////////////////////////////////////////////////////
                              CONSTRUCTOR
    //////////////////////////////////////////////////////////////*/
    constructor(
        string memory _name,
        string memory _symbol,
        uint256 _ETHPerToken,
        address _permissionsAddr
    ) ERC721L(_name, _symbol) Ownable() EIP712("Voucher", "1") {
        ETHperToken = _ETHPerToken;
        nextTokenId = 1;
        permissions = _permissionsAddr;
        checkPermissions = true;
    }

    /**
     * @notice mint requires to pass the value of the voucher, funds are not provided during the minting process
     *         Using safeMint in case addresses provided by users are sm wallet managed by exchange
     * @dev storing the value for each NFT increases by 22K gas per mint
     */
    function mint(
        address to,
        uint256 value,
        address erc20addr
    ) public onlyOwner {
        if (
            erc20addr == address(0) &&
            (address(this).balance < ETHperToken + reservedEth + reservedERC20[address(0)] + value)
        ) revert NotEnoughETH();
        if (erc20addr != address(0)) {
            if (address(this).balance < ETHperToken + reservedEth) revert NotEnoughETH();
            if (ERC20(erc20addr).balanceOf(address(this)) < value + reservedERC20[erc20addr]) revert NotEnoughERC20();
        }

        _safeMint(to, nextTokenId);

        /// can't overflow
        unchecked {
            reservedEth += ETHperToken;
            tokenIdToERC20[nextTokenId] = erc20addr;
            voucherValue[nextTokenId++] = value;
            reservedERC20[erc20addr] += value;
        }
    }

    /// @notice use to mint voucher for only native token
    /// @dev use for default param of address(0) for erc20
    function mint(address to, uint256 value) public onlyOwner {
        mint(to, value, address(0));
    }

    /// @dev used by owner to give many voucher at once.
    /// @param to array of all addresses that will receive a token
    function batchMint(
        address[] calldata to,
        uint256 value,
        address erc20addr
    ) public onlyOwner {
        uint256 nbAddresses = to.length;
        if (
            erc20addr == address(0) &&
            (address(this).balance <
                ETHperToken * nbAddresses + reservedEth + reservedERC20[address(0)] + value * nbAddresses)
        ) revert NotEnoughETH();
        if (erc20addr != address(0)) {
            if (address(this).balance < ETHperToken * nbAddresses + reservedEth) revert NotEnoughETH();
            if (ERC20(erc20addr).balanceOf(address(this)) < value * nbAddresses + reservedERC20[erc20addr])
                revert NotEnoughERC20();
        }
        for (uint256 i; i < nbAddresses; ) {
            _safeMint(to[i], nextTokenId);
            /// can't overflow
            unchecked {
                ++i;
                tokenIdToERC20[nextTokenId] = erc20addr;
                voucherValue[nextTokenId++] = value;
            }
        }
        unchecked {
            reservedEth += nbAddresses * ETHperToken;
            reservedERC20[erc20addr] += nbAddresses * value;
        }
    }

    /// @dev burning token before transferring tokens to prevent reentrancy
    function burn(uint256 tokenId) public {
        if (ownerOf(tokenId) != msg.sender) revert NotAuthorized();
        _burnAndRedeem(msg.sender, tokenId);
    }

    // @dev based on https://soliditydeveloper.com/ecrecover
    function burnFromSignature(
        bytes memory signature,
        address owner,
        uint256 tokenId
    ) external {
        // check if owner can burn token before checking signature
        if (ownerOf(tokenId) != owner) revert NotAuthorized();
        bytes32 digest = _hashTypedDataV4(
            keccak256(abi.encode(keccak256("burn(address owner,uint256 tokenId)"), owner, tokenId))
        );

        address signer = ECDSA.recover(digest, signature);
        if (signer != owner) revert BurnInvalidSignature();
        if (signer == address(0)) revert ECDSAInvalidSignature();

        _burnAndRedeem(owner, tokenId);
    }

    function _burnAndRedeem(address owner, uint256 tokenId) internal {
        address erc20addr = tokenIdToERC20[tokenId];
        uint256 erc20Value = voucherValue[tokenId];
        _burn(tokenId);
        delete voucherValue[tokenId];
        unchecked {
            reservedEth -= ETHperToken;
            reservedERC20[erc20addr] -= erc20Value;
        }
        if (erc20addr == address(0)) {
            (bool success, ) = owner.call{ value: ETHperToken + erc20Value }("");
            /// will never happen if the target is an EOA
            if (!success) revert NativeTransferFailed();
        } else {
            ERC20(erc20addr).transfer(owner, erc20Value);
            (bool success, ) = owner.call{ value: ETHperToken }("");
            /// will never happen if the target is an EOA
            if (!success) revert NativeTransferFailed();
        }
    }

    function updateETHperToken(uint256 _ETHperToken) external onlyOwner {
        uint256 newReserve = (reservedEth * _ETHperToken) / ETHperToken;
        if (newReserve + reservedERC20[address(0)] > address(this).balance) revert NotEnoughETH();
        ETHperToken = _ETHperToken;
        reservedEth = newReserve;
        emit ETHperTokenUpdated(msg.sender, _ETHperToken);
    }

    function withdrawUnreservedETH(address to) external onlyOwner {
        uint256 unreservedEth = address(this).balance - reservedEth;
        (bool success, ) = to.call{ value: unreservedEth }("");
        /// will never happen if the target is an EOA
        if (!success) revert NativeTransferFailed();
        assert(reservedEth == address(this).balance);
    }

    /// to be able to fill the contract
    receive() external payable {
        emit ContractRefill(msg.sender, msg.value);
    }

    function transferFrom(
        address from,
        address to,
        uint256 id
    ) public virtual override {
        if (checkPermissions && !AllowList(permissions).hasRole(EMPLOYEE, to)) revert NotEmployee();
        super.transferFrom(from, to, id);
    }

    function batchTransferFrom(
        address from,
        address[] calldata to,
        uint256[] calldata ids
    ) public virtual {
        uint256 length = ids.length;
        if (length != to.length) revert IncorrectArraySize();
        for (uint256 i; i < length; ) {
            if (checkPermissions && !AllowList(permissions).hasRole(EMPLOYEE, to[i])) revert NotEmployee();
            super.transferFrom(from, to[i], ids[i]);
            unchecked {
                ++i;
            }
        }
    }

    /// @notice only admin can update permission check
    function toggleBypassPermission() public onlyOwner {
        checkPermissions = !checkPermissions;
    }

    // EIP 165
    function changePermissionContract(address _permissionAddr) public onlyOwner {
        require(AllowList(_permissionAddr).supportsInterface(type(IAccessControl).interfaceId));
        permissions = _permissionAddr;
    }

    function changeTokenURI(string calldata newTokenURI) external onlyOwner {
        _baseTokenURI = newTokenURI;
    }

    function baseTokenURI() public view returns (string memory) {
        return _baseTokenURI;
    }

    function tokenURI(uint256 _tokenId) public view override returns (string memory) {
        return baseTokenURI();
    }
}
